'use client';

import { motion } from 'framer-motion';
import Link from 'next/link';
import { ArrowLeft, ChevronRight, Home, Box, MousePointer2, Keyboard, AlertCircle } from 'lucide-react';
import { Tool, slugifyCategory } from '@/lib/tools-config';
import AdUnit from '@/components/ads/AdUnit';
import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';

interface ToolPageClientProps {
    tool: Tool;
    slug: string;
    children?: React.ReactNode;
}

export default function ToolPageClient({ tool, slug, children }: ToolPageClientProps) {
    return (
        <div className="mx-auto max-w-5xl space-y-8 py-8 px-4 sm:px-0">
            {/* Breadcrumbs */}
            <nav className="flex items-center space-x-2 text-sm text-muted-foreground mb-6">
                <Link href="/" className="hover:text-primary transition-colors flex items-center">
                    <Home className="h-4 w-4" />
                </Link>
                <ChevronRight className="h-4 w-4" />
                <Link href={`/category/${slugifyCategory(tool.category)}`} className="hover:text-primary transition-colors">
                    {tool.category}
                </Link>
                <ChevronRight className="h-4 w-4" />
                <span className="text-foreground font-medium">{tool.title}</span>
            </nav>

            <motion.div
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.4 }}
                className="space-y-4"
            >
                <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4">
                    <div className="space-y-2">
                        <h1 className="text-3xl font-extrabold tracking-tight md:text-4xl lg:text-5xl">{tool.title}</h1>
                        <p className="text-lg text-muted-foreground max-w-[700px]">{tool.description}</p>
                    </div>
                    <Link href={`/category/${slugifyCategory(tool.category)}`}>
                        <motion.div whileHover={{ x: -4 }} className="hidden sm:flex items-center text-sm font-medium text-primary cursor-pointer">
                            <ArrowLeft className="mr-2 h-4 w-4" />
                            Back to {tool.category}
                        </motion.div>
                    </Link>
                </div>
            </motion.div>

            {/* Top Ad Unit */}
            <AdUnit
                publisherId="ca-pub-0000000000000000"
                slotId="0000000000"
                className="my-8 min-h-[100px] bg-muted/10 rounded-xl flex items-center justify-center border border-dashed border-muted-foreground/20"
            />

            {/* Compatibility Overlay for Touch Devices */}
            <CompatibilityMessage tool={tool} />

            <motion.div
                initial={{ opacity: 0, scale: 0.98 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.5, delay: 0.2 }}
                className="mt-8 bg-card rounded-2xl border shadow-sm p-1 sm:p-4 overflow-hidden relative"
            >
                {children ? (
                    <div className={tool.requires ? "hidden md:block" : ""}>
                        {children}
                    </div>
                ) : (
                    <div className="rounded-xl border border-dashed p-20 text-center text-muted-foreground">
                        Tool implementation coming soon...
                    </div>
                )}

                {/* Visual placeholder for mobile users when the actual tool is hidden */}
                {tool.requires && (
                    <div className="md:hidden py-20 text-center space-y-4">
                        <div className="h-16 w-16 bg-muted rounded-full flex items-center justify-center mx-auto">
                            {tool.requires.includes('mouse') ? <MousePointer2 className="h-8 w-8 text-muted-foreground" /> : <Keyboard className="h-8 w-8 text-muted-foreground" />}
                        </div>
                        <p className="text-muted-foreground font-medium px-4">This tool is optimized for desktop use.</p>
                    </div>
                )}
            </motion.div>

            {/* SEO Content Section */}
            {tool.longDescription && (
                <article className="mt-16 prose prose-slate dark:prose-invert max-w-none p-8 rounded-[2.5rem] bg-muted/20 border border-primary/5">
                    <h2 className="text-2xl font-black mb-6">About {tool.title}</h2>
                    <div className="text-muted-foreground leading-relaxed whitespace-pre-wrap font-medium">
                        {tool.longDescription}
                    </div>
                </article>
            )}

            {/* Bottom Ad Unit */}
            <AdUnit
                publisherId="ca-pub-0000000000000000"
                slotId="0000000000"
                className="mt-16 min-h-[250px] bg-muted/10 rounded-xl flex items-center justify-center border border-dashed border-muted-foreground/20"
            />
        </div>
    );
}

function CompatibilityMessage({ tool }: { tool: Tool }) {
    const [isTouch, setIsTouch] = useState(false);

    useEffect(() => {
        setIsTouch('ontouchstart' in window || navigator.maxTouchPoints > 0);
    }, []);

    if (!isTouch || !tool.requires) return null;

    return (
        <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            className="p-6 rounded-[2rem] bg-amber-500/5 border border-amber-500/10 flex flex-col sm:flex-row items-center gap-6"
        >
            <div className="h-14 w-14 rounded-2xl bg-amber-500/10 flex items-center justify-center text-amber-600 shrink-0">
                <AlertCircle className="h-7 w-7" />
            </div>
            <div className="space-y-1 text-center sm:text-left">
                <h3 className="text-lg font-bold text-amber-700">Desktop Recommended</h3>
                <p className="text-sm text-amber-600/80 font-medium leading-relaxed">
                    This tool requires a {tool.requires.join(' or ')} for the best experience.
                    Some features may not work correctly on touch devices.
                </p>
            </div>
            <Link href="/" className="sm:ml-auto">
                <Button variant="outline" size="sm" className="rounded-full border-amber-500/20 hover:bg-amber-500/5 text-amber-700">
                    Find Mobile Tools
                </Button>
            </Link>
        </motion.div>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Compass, Info, RefreshCw } from 'lucide-react';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

export default function AngleConverter() {
    const [val, setVal] = useState('0');
    const [unit, setUnit] = useState('deg');

    const convert = (v: number, from: string) => {
        let deg = 0;
        if (from === 'deg') deg = v;
        else if (from === 'rad') deg = v * (180 / Math.PI);
        else if (from === 'grad') deg = v * 0.9;

        return {
            deg: deg,
            rad: deg * (Math.PI / 180),
            grad: deg / 0.9,
            circle: deg / 360
        };
    };

    const results = convert(parseFloat(val) || 0, unit);

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Compass className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Angle Converter</CardTitle>
                <p className="text-muted-foreground mt-2">Geometry utility for Degrees, Radians, and Gradians.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-12">
                <div className="flex flex-col md:flex-row gap-6 items-end">
                    <div className="flex-1 space-y-2 w-full">
                        <label className="text-[10px] font-black uppercase tracking-widest text-primary">Input Rotation</label>
                        <Input
                            type="number"
                            value={val}
                            onChange={(e) => setVal(e.target.value)}
                            className="h-16 rounded-2xl bg-muted/20 border-primary/10 text-2xl font-bold"
                        />
                    </div>
                    <div className="w-full md:w-64 space-y-2">
                        <label className="text-[10px] font-black uppercase tracking-widest text-primary">Unit</label>
                        <Select value={unit} onValueChange={setUnit}>
                            <SelectTrigger className="h-16 rounded-2xl bg-muted/20 border-primary/10 font-bold text-lg">
                                <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                                <SelectItem value="deg">Degrees (┬░)</SelectItem>
                                <SelectItem value="rad">Radians (rad)</SelectItem>
                                <SelectItem value="grad">Gradians (gon)</SelectItem>
                            </SelectContent>
                        </Select>
                    </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-8 items-center text-center">
                    <div className="space-y-4">
                        <AngleResult label="Degrees" value={results.deg.toFixed(2)} unit="┬░" />
                        <AngleResult label="Radians" value={results.rad.toFixed(4)} unit="rad" />
                        <AngleResult label="Gradians" value={results.grad.toFixed(2)} unit="grad" />
                    </div>
                    <div className="relative w-64 h-64 mx-auto bg-muted/10 rounded-full border-4 border-dashed border-primary/10 flex items-center justify-center">
                        <div
                            className="absolute w-1 h-32 bg-primary/40 bottom-1/2 origin-bottom transition-all duration-500"
                            style={{ transform: `rotate(${results.deg}deg)` }}
                        />
                        <div className="w-4 h-4 bg-primary rounded-full shadow-lg shadow-primary/50" />
                        <span className="absolute bottom-4 text-[10px] font-black text-primary uppercase">Visual Preview</span>
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

function AngleResult({ label, value, unit }: any) {
    return (
        <div className="flex justify-between items-center p-6 rounded-2xl bg-muted/5 border border-primary/5">
            <span className="text-[10px] font-black uppercase tracking-widest text-primary">{label}</span>
            <span className="text-xl font-black">{value} <span className="text-xs opacity-50">{unit}</span></span>
        </div>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Maximize, RefreshCw, Map } from 'lucide-react';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

export default function AreaConverter() {
    const [val, setVal] = useState('100');
    const [unit, setUnit] = useState('sqm');

    const rates: Record<string, number> = {
        sqm: 1,
        sqft: 10.7639,
        sqin: 1550,
        acre: 0.000247105,
        hectare: 0.0001,
        sqkm: 0.000001,
        sqmi: 3.861e-7
    };

    const convert = (v: number, from: string) => {
        const base = v / rates[from]; // sqm
        return {
            sqm: base,
            sqft: base * rates.sqft,
            acre: base * rates.acre,
            hectare: base * rates.hectare,
            sqkm: base * rates.sqkm,
            sqmi: base * rates.sqmi
        };
    };

    const results = convert(parseFloat(val) || 0, unit);

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Map className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Area Converter</CardTitle>
                <p className="text-muted-foreground mt-2">Convert land and floor area across international standards.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-12">
                <div className="flex flex-col md:flex-row gap-6 items-end">
                    <div className="flex-1 space-y-2 w-full">
                        <label className="text-[10px] font-black uppercase tracking-widest text-primary">Area Magnitude</label>
                        <Input
                            type="number"
                            value={val}
                            onChange={(e) => setVal(e.target.value)}
                            className="h-16 rounded-2xl bg-muted/20 border-primary/10 text-2xl font-bold"
                        />
                    </div>
                    <div className="w-full md:w-64 space-y-2">
                        <label className="text-[10px] font-black uppercase tracking-widest text-primary">Scale Unit</label>
                        <Select value={unit} onValueChange={setUnit}>
                            <SelectTrigger className="h-16 rounded-2xl bg-muted/20 border-primary/10 font-bold text-lg">
                                <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                                <SelectItem value="sqm">Square Meters (m┬▓)</SelectItem>
                                <SelectItem value="sqft">Square Feet (ft┬▓)</SelectItem>
                                <SelectItem value="acre">Acres</SelectItem>
                                <SelectItem value="hectare">Hectares</SelectItem>
                                <SelectItem value="sqkm">Sq Kilometers (km┬▓)</SelectItem>
                                <SelectItem value="sqmi">Sq Miles (mi┬▓)</SelectItem>
                            </SelectContent>
                        </Select>
                    </div>
                </div>

                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                    <AreaStat label="Sq Meters" value={results.sqm.toLocaleString()} s="m┬▓" />
                    <AreaStat label="Sq Feet" value={results.sqft.toLocaleString()} s="ft┬▓" />
                    <AreaStat label="Acres" value={results.acre.toFixed(4)} s="ac" />
                    <AreaStat label="Hectares" value={results.hectare.toFixed(4)} s="ha" />
                    <AreaStat label="Sq Miles" value={results.sqmi.toFixed(6)} s="mi┬▓" />
                    <AreaStat label="Sq Kilometers" value={results.sqkm.toFixed(6)} s="km┬▓" />
                </div>
            </CardContent>
        </Card>
    );
}

function AreaStat({ label, value, s }: any) {
    return (
        <div className="p-8 rounded-3xl bg-muted/10 border border-primary/5 hover:border-primary/20 transition-all text-center">
            <p className="text-[10px] font-black text-primary uppercase transparency-50 mb-2">{label}</p>
            <div className="flex flex-col">
                <span className="text-2xl font-black">{value}</span>
                <span className="text-[10px] font-bold opacity-30">{s}</span>
            </div>
        </div>
    );
}

