                                <SelectItem value="C">Celsius (┬░C)</SelectItem>
                                <SelectItem value="F">Fahrenheit (┬░F)</SelectItem>
                                <SelectItem value="K">Kelvin (K)</SelectItem>
                            </SelectContent>
                        </Select>
