            <CardContent className="p-8 space-y-12">
                <div className="flex flex-col md:flex-row gap-6 items-end">
                    <div className="flex-1 space-y-2 w-full">
                        <label className="text-[10px] font-black uppercase tracking-widest text-primary">Volume Input</label>
                        <Input
                            type="number"
                            value={val}
                            onChange={(e) => setVal(e.target.value)}
                            className="h-16 rounded-2xl bg-muted/20 border-primary/10 text-2xl font-bold"
                        />
                    </div>
                    <div className="w-full md:w-64 space-y-2">
                        <label className="text-[10px] font-black uppercase tracking-widest text-primary">Source Unit</label>
                        <Select value={unit} onValueChange={setUnit}>
                            <SelectTrigger className="h-16 rounded-2xl bg-muted/20 border-primary/10 font-bold text-lg">
                                <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                                <SelectItem value="liter">Liters (l)</SelectItem>
                                <SelectItem value="ml">Milliliters (ml)</SelectItem>
                                <SelectItem value="gallon">Gallons (US)</SelectItem>
                                <SelectItem value="quart">Quarts</SelectItem>
                                <SelectItem value="floz">Fluid Ounces (oz)</SelectItem>
                                <SelectItem value="cubicmeter">Cubic Meters (m┬│)</SelectItem>
                            </SelectContent>
                        </Select>
                    </div>
                </div>

                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                    <MetricCard label="Liters" value={results.liter.toLocaleString()} s="l" />
                    <MetricCard label="US Gallons" value={results.gallon.toFixed(3)} s="gal" />
                    <MetricCard label="Fluid Ounces" value={results.floz.toFixed(1)} s="fl oz" />
                    <MetricCard label="Cubic Meters" value={results.m3.toFixed(4)} s="m┬│" />
                    <MetricCard label="Milliliters" value={results.ml.toLocaleString()} s="ml" />
                    <MetricCard label="US Quarts" value={results.quart.toFixed(2)} s="qt" />
                </div>
            </CardContent>
        </Card>
    );
}

function MetricCard({ label, value, s }: any) {
    return (
        <div className="p-8 rounded-[2.5rem] bg-background border-2 border-primary/5 hover:border-primary/20 transition-all group flex flex-col justify-center items-center">
            <span className="text-[10px] font-black uppercase text-primary/40 group-hover:text-primary mb-2">{label}</span>
            <div className="flex items-baseline gap-2">
                <h4 className="text-3xl font-black">{value}</h4>
                <span className="text-sm font-bold opacity-30">{s}</span>
            </div>
        </div>
    );
}

'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { AlarmClock, Bell, Volume2, Timer, BellOff } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';

export default function OnlineAlarmClock() {
    const [time, setTime] = useState(new Date());
    const [alarmTime, setAlarmTime] = useState('08:00');
    const [isAlarmSet, setIsAlarmSet] = useState(false);
    const [isRinging, setIsRinging] = useState(false);

    useEffect(() => {
        const timer = setInterval(() => {
            const now = new Date();
            setTime(now);

            if (isAlarmSet) {
                const [h, m] = alarmTime.split(':');
                if (now.getHours() === parseInt(h) && now.getMinutes() === parseInt(m) && now.getSeconds() === 0) {
                    setIsRinging(true);
                }
            }
        }, 1000);
        return () => clearInterval(timer);
    }, [isAlarmSet, alarmTime]);

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <AlarmClock className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Online Alarm Clock</CardTitle>
                <p className="text-muted-foreground mt-2">A reliable, aesthetic alarm for your desk.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="text-center space-y-2">
                    <h2 className="text-8xl font-black tracking-tighter text-primary">
                        {time.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', second: '2-digit' })}
                    </h2>
                    <p className="text-muted-foreground font-mono uppercase tracking-[0.5em] text-sm">
                        {time.toLocaleDateString([], { weekday: 'long', month: 'long', day: 'numeric' })}
                    </p>
                </div>

                <div className="flex flex-col md:flex-row gap-6 justify-center items-center">
                    <div className="relative group">
                        <Input
                            type="time"
                            value={alarmTime}
                            onChange={(e) => setAlarmTime(e.target.value)}
                            className="h-20 w-48 text-4xl font-black rounded-3xl bg-muted/20 border-primary/10 text-center"
                        />
                        <div className="absolute -top-4 left-1/2 -translate-x-1/2 bg-primary text-white text-[10px] font-black px-3 py-1 rounded-full opacity-0 group-hover:opacity-100 transition-opacity">
                            SET TIME
                        </div>
                    </div>

                    <Button
                        size="lg"
                        onClick={() => setIsAlarmSet(!isAlarmSet)}
                        variant={isAlarmSet ? "destructive" : "default"}
                        className="h-20 px-12 rounded-[2.5rem] text-xl font-bold shadow-2xl shadow-primary/20"
                    >
                        {isAlarmSet ? <BellOff className="mr-2 h-6 w-6" /> : <Bell className="mr-2 h-6 w-6" />}
                        {isAlarmSet ? "Cancel Alarm" : "Set Alarm"}
                    </Button>
                </div>

                {isRinging && (
                    <div className="fixed inset-0 bg-primary/95 flex flex-col items-center justify-center z-50 animate-in fade-in zoom-in duration-500">
                        <div className="w-32 h-32 bg-white rounded-full flex items-center justify-center animate-bounce mb-8">
                            <Volume2 className="h-16 w-16 text-primary" />
                        </div>
                        <h2 className="text-6xl font-black text-white mb-8">ALARM RINGING!</h2>
                        <Button
                            size="lg"
                            variant="secondary"
                            className="h-20 px-16 rounded-full text-2xl font-black"
                            onClick={() => {
                                setIsRinging(false);
                                setIsAlarmSet(false);
                            }}
                        >
                            STOP ALARM
                        </Button>
                    </div>
                )}

                <div className="p-8 rounded-[3rem] bg-muted/10 border border-primary/5 flex justify-around">
                    <div className="text-center">
                        <p className="text-[10px] font-black uppercase text-primary mb-1">Status</p>
                        <p className="font-bold">{isAlarmSet ? 'Armed ­ƒöö' : 'Ready'}</p>
                    </div>
                    <div className="text-center">
                        <p className="text-[10px] font-black uppercase text-primary mb-1">Next Alarm</p>
                        <p className="font-bold">{isAlarmSet ? alarmTime : '--:--'}</p>
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Activity, RefreshCw, Info, Scale } from 'lucide-react';
import { Input } from '@/components/ui/input';
import { Slider } from '@/components/ui/slider';

export default function BMICalculator() {
    const [weight, setWeight] = useState(70);
    const [height, setHeight] = useState(175);

    const bmi = weight / Math.pow(height / 100, 2);

    const getCategory = (b: number) => {
        if (b < 18.5) return { label: 'Underweight', color: 'text-blue-500', bg: 'bg-blue-500/10' };
        if (b < 25) return { label: 'Healthy Weight', color: 'text-green-500', bg: 'bg-green-500/10' };
        if (b < 30) return { label: 'Overweight', color: 'text-amber-500', bg: 'bg-amber-500/10' };
        return { label: 'Obese', color: 'text-destructive', bg: 'bg-destructive/10' };
    };

    const category = getCategory(bmi);

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Activity className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">BMI Calculator</CardTitle>
                <p className="text-muted-foreground mt-2">Check your Body Mass Index and understand your weight health.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-12">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                    <div className="space-y-10">
                        <div className="space-y-4">
                            <div className="flex justify-between items-center">
                                <span className="text-[10px] font-black uppercase text-primary tracking-widest">Weight (kg)</span>
                                <span className="text-xl font-black">{weight}</span>
                            </div>
                            <Slider value={[weight]} onValueChange={(v) => setWeight(v[0])} min={30} max={200} step={1} />
                        </div>
                        <div className="space-y-4">
                            <div className="flex justify-between items-center">
                                <span className="text-[10px] font-black uppercase text-primary tracking-widest">Height (cm)</span>
                                <span className="text-xl font-black">{height}</span>
                            </div>
                            <Slider value={[height]} onValueChange={(v) => setHeight(v[0])} min={100} max={250} step={1} />
                        </div>
                    </div>

                    <div className="flex flex-col justify-center gap-6">
                        <div className="p-12 rounded-[3.5rem] bg-primary text-white shadow-2xl shadow-primary/30 text-center relative overflow-hidden">
                            <div className="absolute top-0 right-0 p-6 opacity-10">
                                <Scale className="h-24 w-24" />
                            </div>
                            <p className="text-[10px] font-black uppercase tracking-widest opacity-60 mb-2">Your BMI Score</p>
                            <h3 className="text-7xl font-black">{bmi.toFixed(1)}</h3>
                            <div className={`mt-6 inline-flex items-center px-6 py-2 rounded-full bg-white/20 font-black text-sm`}>
                                {category.label}
                            </div>
                        </div>

                        <div className={`p-6 rounded-2xl border ${category.color} ${category.bg} font-bold text-center`}>
                            Ideal range: 18.5 - 24.9
                        </div>
                    </div>
                </div>

                <div className="grid grid-cols-4 gap-2">
                    {['Under', 'Normal', 'Over', 'Obese'].map((l, i) => (
                        <div key={l} className={`h-2 rounded-full ${i === 0 ? 'bg-blue-500' : i === 1 ? 'bg-green-500' : i === 2 ? 'bg-amber-500' : 'bg-destructive'
                            } opacity-30`} />
                    ))}
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Circle, Play, RefreshCw, RotateCw } from 'lucide-react';
import { Button } from '@/components/ui/button';

export default function CoinFlipper() {
    const [result, setResult] = useState<'HEADS' | 'TAILS' | null>(null);
    const [isFlipping, setIsFlipping] = useState(false);
    const [history, setHistory] = useState<string[]>([]);

    const flip = () => {
        setIsFlipping(true);
        setResult(null);
        setTimeout(() => {
            const side = Math.random() > 0.5 ? 'HEADS' : 'TAILS';
            setResult(side);
            setHistory([side, ...history].slice(0, 20));
            setIsFlipping(false);
        }, 800);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden text-center">
            <CardHeader className="border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Circle className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">3D Coin Flipper</CardTitle>
                <p className="text-muted-foreground mt-2">Heads or Tails? Let fate decide with a click.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="flex justify-center perspective-[1000px]">
                    <div
                        className={`w-48 h-48 bg-primary text-white rounded-full border-8 border-primary/20 flex flex-col items-center justify-center shadow-3xl transition-all duration-700 ${isFlipping ? 'animate-flip' : ''
                            } ${result === 'TAILS' ? 'bg-amber-500 border-amber-600/20' : ''}`}
                    >
                        <h3 className="text-4xl font-black">
                            {result ? result[0] : '?'}
                        </h3>
                        <span className="text-[10px] font-black uppercase opacity-50 tracking-widest mt-2">
                            {result || 'Flipping...'}
                        </span>
                    </div>
                </div>

                <div className="flex flex-col items-center gap-8">
                    <Button
                        size="lg"
                        onClick={flip}
                        className="h-20 px-16 rounded-[2.5rem] text-2xl font-black shadow-2xl shadow-primary/20"
                        disabled={isFlipping}
                    >
                        <RotateCw className={`mr-4 h-8 w-8 ${isFlipping ? 'animate-spin' : ''}`} />
                        FLIP COIN
                    </Button>

                    <div className="flex flex-wrap justify-center gap-2">
                        {history.map((r, i) => (
                            <div key={i} className={`h-8 w-8 rounded-full border border-primary/10 flex items-center justify-center text-[10px] font-black ${r === 'HEADS' ? 'bg-primary/20 text-primary' : 'bg-amber-500/20 text-amber-600'
                                }`}>
                                {r[0]}
                            </div>
                        ))}
                    </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                    <div className="p-6 rounded-3xl bg-muted/10 border border-primary/5">
                        <p className="text-[10px] font-black uppercase text-primary opacity-50 mb-1">Total Heads</p>
                        <p className="text-3xl font-black">{history.filter(h => h === 'HEADS').length}</p>
                    </div>
                    <div className="p-6 rounded-3xl bg-muted/10 border border-primary/5">
                        <p className="text-[10px] font-black uppercase text-amber-600 opacity-50 mb-1">Total Tails</p>
                        <p className="text-3xl font-black">{history.filter(h => h === 'TAILS').length}</p>
                    </div>
                </div>
            </CardContent>
            <style jsx>{`
                @keyframes flip {
                    0% { transform: rotateY(0) translateY(0); }
                    50% { transform: rotateY(1800deg) translateY(-150px); }
                    100% { transform: rotateY(3600deg) translateY(0); }
                }
                .animate-flip {
                    animation: flip 0.8s ease-in-out;
                }
            `}</style>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { ListChecks, Plus, Trash2, Calendar, CheckCircle2, Circle } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';

interface Task {
    id: string;
    text: string;
    completed: boolean;
}

export default function DailyPlanner() {
    const [tasks, setTasks] = useState<Task[]>([
        { id: '1', text: 'Drink 2 liters of water', completed: false },
        { id: '2', text: 'Meditate for 10 minutes', completed: true },
        { id: '3', text: 'Review Batch 5 expansion', completed: false }
    ]);
    const [input, setInput] = useState('');

    const addTask = () => {
        if (!input.trim()) return;
        setTasks([...tasks, { id: Date.now().toString(), text: input, completed: false }]);
        setInput('');
    };

    const toggleTask = (id: string) => {
        setTasks(tasks.map(t => t.id === id ? { ...t, completed: !t.completed } : t));
    };

    const removeTask = (id: string) => {
        setTasks(tasks.filter(t => t.id !== id));
    };

    const progress = Math.round((tasks.filter(t => t.completed).length / (tasks.length || 1)) * 100);

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <ListChecks className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Visual Daily Planner</CardTitle>
                <p className="text-muted-foreground mt-2">Organize your thoughts and crush your daily goals.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-12">
                <div className="flex flex-col md:flex-row gap-8">
                    <div className="flex-1 space-y-6">
                        <div className="flex gap-4">
                            <Input
                                value={input}
                                onChange={(e) => setInput(e.target.value)}
                                onKeyPress={(e) => e.key === 'Enter' && addTask()}
                                placeholder="What's on the agenda today?"
                                className="h-16 rounded-2xl bg-muted/20 border-primary/10 text-lg px-6"
                            />
                            <Button onClick={addTask} className="h-16 w-16 rounded-2xl shadow-xl shadow-primary/20">
                                <Plus className="h-8 w-8" />
                            </Button>
                        </div>

                        <div className="space-y-3">
                            {tasks.map((task) => (
                                <div key={task.id} className="group flex items-center gap-4 p-5 rounded-2xl bg-background border border-primary/5 hover:border-primary/20 transition-all shadow-sm">
                                    <button onClick={() => toggleTask(task.id)}>
                                        {task.completed ?
                                            <CheckCircle2 className="h-6 w-6 text-green-500" /> :
                                            <Circle className="h-6 w-6 text-muted-foreground opacity-30 group-hover:opacity-100" />
                                        }
                                    </button>
                                    <span className={`flex-1 font-bold text-lg ${task.completed ? 'line-through opacity-30' : ''}`}>
                                        {task.text}
                                    </span>
                                    <button onClick={() => removeTask(task.id)} className="opacity-0 group-hover:opacity-100 p-2 text-destructive hover:bg-destructive/10 rounded-xl transition-all">
                                        <Trash2 className="h-4 w-4" />
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>

                    <div className="w-full md:w-80 space-y-6">
                        <div className="p-10 rounded-[3rem] bg-primary text-white shadow-2xl shadow-primary/30 text-center flex flex-col items-center">
                            <p className="text-[10px] font-black uppercase tracking-widest opacity-70 mb-4">Today's Progress</p>
                            <div className="relative w-32 h-32 flex items-center justify-center">
                                <svg className="w-full h-full transform -rotate-90">
                                    <circle cx="64" cy="64" r="58" stroke="currentColor" strokeWidth="8" fill="transparent" className="text-white/10" />
                                    <circle cx="64" cy="64" r="58" stroke="currentColor" strokeWidth="8" fill="transparent" strokeDasharray={364} strokeDashoffset={364 - (364 * progress / 100)} className="text-white transition-all duration-1000" />
                                </svg>
                                <span className="absolute text-3xl font-black">{progress}%</span>
                            </div>
                            <p className="mt-6 text-xs font-bold opacity-50">{tasks.filter(t => t.completed).length} of {tasks.length} Completed</p>
                        </div>
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Dice5, Play, History, RotateCcw } from 'lucide-react';
import { Button } from '@/components/ui/button';

export default function DiceRoller() {
    const [dice, setDice] = useState<number[]>([1]);
    const [history, setHistory] = useState<number[][]>([]);
    const [isRolling, setIsRolling] = useState(false);
    const [diceCount, setDiceCount] = useState(1);

    const rollDice = () => {
        setIsRolling(true);
        setTimeout(() => {
            const results = Array.from({ length: diceCount }).map(() => Math.floor(Math.random() * 6) + 1);
            setDice(results);
            setHistory([results, ...history].slice(0, 10));
            setIsRolling(false);
        }, 600);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden text-center">
            <CardHeader className="border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Dice5 className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">3D Dice Roller</CardTitle>
                <p className="text-muted-foreground mt-2">Roll for initiative or settle a board game dispute.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="flex justify-center gap-6 mb-8">
                    {[1, 2, 3, 4, 5].map(num => (
                        <Button
                            key={num}
                            variant={diceCount === num ? "default" : "outline"}
                            className="h-12 w-12 rounded-xl font-bold"
                            onClick={() => setDiceCount(num)}
                        >
                            {num}
                        </Button>
                    ))}
                </div>

                <div className="flex flex-wrap justify-center gap-8 min-h-[160px]">
                    {dice.map((value, idx) => (
                        <div
                            key={idx}
                            className={`w-32 h-32 bg-background border-4 border-primary/20 rounded-[2rem] flex items-center justify-center text-6xl font-black shadow-2xl transition-all duration-500 scale-100 ${isRolling ? 'animate-bounce blur-[2px] rotate-[360deg] scale-90 opacity-50' : ''
                                }`}
                        >
                            {value}
                            {/* Simple Dot Patterns could be added for realism */}
                        </div>
                    ))}
                </div>

                <div className="flex flex-col items-center gap-6">
                    <Button
                        size="lg"
                        onClick={rollDice}
                        className="h-20 px-16 rounded-[2.5rem] text-2xl font-black shadow-2xl shadow-primary/20"
                        disabled={isRolling}
                    >
                        <Play className="mr-4 h-8 w-8 fill-current" />
                        ROLL DICE
                    </Button>

                    <div className="w-full max-w-md space-y-4">
                        <div className="flex items-center justify-between text-[10px] font-black uppercase tracking-widest text-primary opacity-50">
                            <span>Last 10 Rolls</span>
                            <History className="h-4 w-4" />
                        </div>
                        <div className="flex flex-wrap gap-2 justify-center">
                            {history.map((roll, i) => (
                                <div key={i} className="px-3 py-1 bg-muted/20 border border-primary/5 rounded-full text-xs font-bold">
                                    {roll.join(' + ')} = {roll.reduce((a, b) => a + b, 0)}
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

