'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Disc, Play, Plus, Trash2, Trophy } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';

export default function WheelOfFortune() {
    const [options, setOptions] = useState(['Lunch', 'Coffee', 'Gym', 'Code', 'Nap']);
    const [inputValue, setInputValue] = useState('');
    const [isSpinning, setIsSpinning] = useState(false);
    const [winner, setWinner] = useState<string | null>(null);
    const [rotation, setRotation] = useState(0);

    const spin = () => {
        if (isSpinning) return;
        setIsSpinning(true);
        setWinner(null);

        const extraSpins = 5 + Math.random() * 5;
        const newRotation = rotation + (extraSpins * 360);
        setRotation(newRotation);

        setTimeout(() => {
            setIsSpinning(false);
            const index = Math.floor(Math.random() * options.length);
            setWinner(options[index]);
        }, 3000);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden text-center">
            <CardHeader className="border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Disc className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Decision Wheel</CardTitle>
                <p className="text-muted-foreground mt-2">Can't decide? Let the modern wheel of fortune choose for you.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="flex flex-col md:flex-row gap-12 items-center">
                    <div className="relative w-80 h-80">
                        {/* Pointer */}
                        <div className="absolute top-0 left-1/2 -translate-x-1/2 -translate-y-4 z-20">
                            <div className="w-8 h-8 bg-destructive border-4 border-white rounded-full shadow-lg" />
                            <div className="w-0 h-0 border-l-[12px] border-l-transparent border-r-[12px] border-r-transparent border-t-[20px] border-t-destructive mx-auto -mt-1" />
                        </div>

                        {/* Wheel */}
                        <div
                            className="w-full h-full rounded-full border-8 border-primary/20 shadow-3xl overflow-hidden relative transition-transform duration-[3000ms] cubic-bezier(0.1, 0, 0, 1)"
                            style={{ transform: `rotate(${rotation}deg)` }}
                        >
                            {options.map((opt, i) => (
                                <div
                                    key={i}
                                    className="absolute top-0 left-1/2 w-1/2 h-1/2 origin-bottom-left flex items-center justify-center px-4"
                                    style={{
                                        transform: `rotate(${i * (360 / options.length)}deg)`,
                                        backgroundColor: `hsl(${i * (360 / options.length)}, 70%, 50%)`,
                                    }}
                                >
                                    <span className="text-white font-black text-xs -rotate-45 whitespace-nowrap overflow-hidden text-ellipsis max-w-full">
                                        {opt}
                                    </span>
                                </div>
                            ))}
                        </div>

                        {/* Center Hub */}
                        <div className="absolute inset-0 m-auto w-12 h-12 bg-white rounded-full shadow-xl border-4 border-primary/20 z-10 flex items-center justify-center">
                            <div className="w-4 h-4 bg-primary rounded-full animate-pulse" />
                        </div>
                    </div>

                    <div className="flex-1 space-y-6 text-left">
                        <div className="flex gap-2">
                            <Input
                                value={inputValue}
                                onChange={(e) => setInputValue(e.target.value)}
                                onKeyPress={(e) => e.key === 'Enter' && setOptions([...options, inputValue])}
                                placeholder="Add choice..."
                                className="h-12 rounded-xl"
                            />
                            <Button onClick={() => {
                                if (inputValue) {
                                    setOptions([...options, inputValue]);
                                    setInputValue('');
                                }
                            }}><Plus /></Button>
                        </div>

                        <div className="flex flex-wrap gap-2 max-h-48 overflow-y-auto p-4 rounded-2xl bg-muted/10 border border-primary/5">
                            {options.map((opt, i) => (
                                <div key={i} className="px-3 py-1 bg-background border border-primary/10 rounded-full flex items-center gap-2 text-xs font-bold">
                                    {opt}
                                    <button onClick={() => setOptions(options.filter((_, idx) => idx !== i))} className="text-destructive hover:scale-125 transition-transform"><Trash2 className="h-3 w-3" /></button>
                                </div>
                            ))}
                        </div>

                        <Button
                            className="w-full h-20 rounded-[2.5rem] text-2xl font-black shadow-2xl shadow-primary/20"
                            onClick={spin}
                            disabled={isSpinning || options.length < 2}
                        >
                            <Play className="mr-4 h-8 w-8 fill-current" />
                            SPIN NOW
                        </Button>
                    </div>
                </div>

                {winner && (
                    <div className="p-12 rounded-[4rem] bg-primary text-white animate-in zoom-in duration-500 shadow-3xl shadow-primary/40">
                        <Trophy className="h-12 w-12 mx-auto mb-4" />
                        <p className="text-[10px] font-black uppercase tracking-[0.5em] opacity-60 mb-2">The Winner Is</p>
                        <h2 className="text-6xl font-black uppercase">{winner}</h2>
                    </div>
                )}
            </CardContent>
        </Card>
    );
}

'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Globe, Plus, Trash2, Clock, Moon, Sun } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';

interface CityTime {
    id: string;
    city: string;
    tz: string;
}

export default function WorldClock() {
    const [cities, setCities] = useState<CityTime[]>([
        { id: '1', city: 'London', tz: 'Europe/London' },
        { id: '2', city: 'New York', tz: 'America/New_York' },
        { id: '3', city: 'Tokyo', tz: 'Asia/Tokyo' },
        { id: '4', city: 'Dubai', tz: 'Asia/Dubai' }
    ]);
    const [now, setNow] = useState(new Date());

    useEffect(() => {
        const timer = setInterval(() => setNow(new Date()), 1000);
        return () => clearInterval(timer);
    }, []);

    const getTimeInZone = (tz: string) => {
        return new Intl.DateTimeFormat('en-US', {
            timeZone: tz,
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit',
            hour12: false
        }).format(now);
    };

    const isDay = (tz: string) => {
        const hour = parseInt(new Intl.DateTimeFormat('en-US', {
            timeZone: tz,
            hour: '2-digit',
            hour12: false
        }).format(now));
        return hour >= 6 && hour < 18;
    };

    return (
        <Card className="max-w-5xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Globe className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">World Clock Dashboard</CardTitle>
                <p className="text-muted-foreground mt-2">Global time tracking for a connected world.</p>
            </CardHeader>
            <CardContent className="p-8">
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                    {cities.map((city) => (
                        <div key={city.id} className="p-8 rounded-[2.5rem] bg-background border-2 border-primary/5 hover:border-primary/20 transition-all group relative">
                            <div className="flex justify-between items-start mb-4">
                                {isDay(city.tz) ? <Sun className="h-5 w-5 text-amber-500" /> : <Moon className="h-5 w-5 text-blue-500" />}
                                <span className="text-[10px] font-black uppercase tracking-widest text-primary opacity-50">{city.city}</span>
                            </div>
                            <h4 className="text-4xl font-black mb-1">{getTimeInZone(city.tz)}</h4>
                            <p className="text-[10px] font-bold text-muted-foreground">{city.tz.split('/')[1].replace('_', ' ')}</p>

                            <button className="absolute top-4 right-4 opacity-0 group-hover:opacity-100 transition-opacity p-2 text-destructive hover:bg-destructive/10 rounded-xl">
                                <Trash2 className="h-4 w-4" />
                            </button>
                        </div>
                    ))}
                </div>

                <div className="mt-12 p-8 rounded-[3rem] bg-primary text-white flex flex-col md:flex-row gap-6 items-center">
                    <div className="space-y-1 flex-1">
                        <h5 className="text-xl font-bold">Add New City</h5>
                        <p className="text-white/60 text-xs">Stay synchronized with your global team.</p>
                    </div>
                    <div className="flex gap-4 w-full md:w-auto">
                        <Input placeholder="City Name" className="bg-white/10 border-white/20 text-white placeholder:text-white/40 h-12 w-full md:w-64 rounded-xl" />
                        <Button variant="secondary" className="h-12 px-8 rounded-xl font-bold">
                            <Plus className="mr-2 h-4 w-4" /> Add
                        </Button>
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { MousePointer2, Copy, Check, RefreshCw } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Slider } from '@/components/ui/slider';

export default function BorderRadiusGen() {
    const [tl, setTl] = useState(30);
    const [tr, setTr] = useState(70);
    const [br, setBr] = useState(40);
    const [bl, setBl] = useState(60);

    // Fancy mode (8 values)
    const [tl2, setTl2] = useState(70);
    const [tr2, setTr2] = useState(30);
    const [br2, setBr2] = useState(60);
    const [bl2, setBl2] = useState(40);

    const [copied, setCopied] = useState(false);

    const radius = `${tl}% ${tr}% ${br}% ${bl}% / ${tl2}% ${tr2}% ${br2}% ${bl2}%`;
    const cssCode = `border-radius: ${radius};`;

    const copyCode = () => {
        navigator.clipboard.writeText(cssCode);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden text-center">
            <CardHeader className="border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <MousePointer2 className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Fancy Border Radius</CardTitle>
                <p className="text-muted-foreground mt-2">Generate organic, blob-like CSS shapes for modern web designs.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="flex flex-col md:flex-row gap-16 items-center justify-center">
                    <div className="grid grid-cols-2 gap-8 w-full md:w-auto">
                        <CornerControl label="Top Left" v1={tl} v2={tl2} onChange1={setTl} onChange2={setTl2} />
                        <CornerControl label="Top Right" v1={tr} v2={tr2} onChange1={setTr} onChange2={setTr2} />
                        <CornerControl label="Bottom Right" v1={br} v2={br2} onChange1={setBr} onChange2={setBr2} />
                        <CornerControl label="Bottom Left" v1={bl} v2={bl2} onChange1={setBl} onChange2={setBl2} />
                    </div>

                    <div
                        className="w-64 h-64 bg-primary text-white shadow-3xl shadow-primary/30 transition-all duration-500 ease-in-out flex items-center justify-center p-8"
                        style={{ borderRadius: radius }}
                    >
                        <span className="text-xs font-black uppercase tracking-[0.3em] opacity-40">Organic Shape</span>
                    </div>
                </div>

                <div className="w-full max-w-xl mx-auto space-y-4">
                    <div className="flex items-center justify-between text-[10px] font-black uppercase tracking-widest text-primary">
                        <span>Generated CSS Code</span>
                        <button onClick={copyCode} className="flex items-center gap-2 text-primary hover:opacity-100 transition-opacity">
                            {copied ? <Check className="h-4 w-4" /> : <Copy className="h-4 w-4" />}
                            {copied ? 'COPIED!' : 'COPY CSS'}
                        </button>
                    </div>
                    <div className="p-6 rounded-2xl bg-primary/5 border border-primary/10 font-mono text-sm break-all leading-relaxed">
                        {cssCode}
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

function CornerControl({ label, v1, v2, onChange1, onChange2 }: any) {
    return (
        <div className="space-y-4 text-left">
            <p className="text-[10px] font-black uppercase text-primary mb-2">{label}</p>
            <Slider value={[v1]} onValueChange={(v) => onChange1(v[0])} min={0} max={100} className="w-32" />
            <Slider value={[v2]} onValueChange={(v) => onChange2(v[0])} min={0} max={100} className="w-32 opacity-50" />
        </div>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Layers, Copy, Check, RefreshCw, Sliders } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Slider } from '@/components/ui/slider';

export default function BoxShadowGen() {
    const [hOffset, setHOffset] = useState(0);
    const [vOffset, setVOffset] = useState(15);
    const [blur, setBlur] = useState(30);
    const [spread, setSpread] = useState(0);
    const [opacity, setOpacity] = useState(0.1);
    const [color, setColor] = useState('#000000');
    const [inset, setInset] = useState(false);

    const [copied, setCopied] = useState(false);

    const shadowStr = `${inset ? 'inset ' : ''}${hOffset}px ${vOffset}px ${blur}px ${spread}px ${color}${Math.round(opacity * 255).toString(16).padStart(2, '0')}`;
    const cssCode = `box-shadow: ${shadowStr};`;

    const copyCode = () => {
        navigator.clipboard.writeText(cssCode);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
    };

    return (
        <Card className="max-w-6xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Layers className="h-8 w-8 text-primary" />
