'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Type, RefreshCw, Copy, Check, Scale } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from '@/components/ui/input';

export default function TypographicScaleGen() {
    const [baseSize, setBaseSize] = useState(16);
    const [ratio, setRatio] = useState(1.25); // Major Third
    const [copied, setCopied] = useState<number | null>(null);

    const ratios = [
        { name: 'Minor Second', value: 1.067 },
        { name: 'Major Second', value: 1.125 },
        { name: 'Minor Third', value: 1.200 },
        { name: 'Major Third', value: 1.250 },
        { name: 'Perfect Fourth', value: 1.333 },
        { name: 'Augmented Fourth', value: 1.414 },
        { name: 'Perfect Fifth', value: 1.500 },
        { name: 'Golden Ratio', value: 1.618 },
    ];

    const generateScale = () => {
        const scale = [];
        for (let i = 5; i >= -2; i--) {
            scale.push({
                level: i > 0 ? `H${6 - i}` : i === 0 ? 'Body' : `Small ${Math.abs(i)}`,
                size: baseSize * Math.pow(ratio, i),
                key: i
            });
        }
        return scale.reverse();
    };

    const scale = generateScale();

    return (
        <Card className="max-w-5xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Type className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Typographic Scale Generator</CardTitle>
                <p className="text-muted-foreground mt-2">Create harmonious font hierarchies using mathematical ratios.</p>
            </CardHeader>
            <CardContent className="p-0 flex flex-col md:flex-row min-h-[600px]">
                <div className="w-full md:w-80 bg-muted/5 p-8 border-b md:border-b-0 md:border-r border-primary/5 space-y-8">
                    <div className="space-y-4">
                        <label className="text-[10px] font-black uppercase text-primary">Base Size (px)</label>
                        <Input type="number" value={baseSize} onChange={(e) => setBaseSize(parseInt(e.target.value) || 16)} className="h-12 rounded-xl" />
                    </div>
                    <div className="space-y-4">
                        <label className="text-[10px] font-black uppercase text-primary">Scale Ratio</label>
                        <Select value={ratio.toString()} onValueChange={(v) => setRatio(parseFloat(v))}>
                            <SelectTrigger className="h-12 rounded-xl"><SelectValue /></SelectTrigger>
                            <SelectContent>
                                {ratios.map(r => <SelectItem key={r.value} value={r.value.toString()}>{r.name} ({r.value})</SelectItem>)}
                            </SelectContent>
                        </Select>
                    </div>
                </div>

                <div className="flex-1 p-12 space-y-8 overflow-y-auto">
                    {scale.map((item, idx) => (
                        <div key={item.key} className="group relative flex items-baseline gap-8 border-b border-primary/5 pb-8 hover:bg-primary/5 p-4 rounded-2xl transition-all">
                            <div className="w-24 shrink-0">
                                <p className="text-[10px] font-black uppercase text-primary opacity-40">{item.level}</p>
                                <p className="text-xs font-bold">{item.size.toFixed(2)}px</p>
                            </div>
                            <h2
                                className="flex-1 font-black text-primary overflow-hidden text-ellipsis whitespace-nowrap"
                                style={{ fontSize: `${item.size}px` }}
                            >
                                Harmony in Design
                            </h2>
                            <Button
                                variant="ghost"
                                size="sm"
                                className="opacity-0 group-hover:opacity-100 transition-opacity"
                                onClick={() => {
                                    navigator.clipboard.writeText(`${item.size.toFixed(2)}px`);
                                    setCopied(idx);
                                    setTimeout(() => setCopied(null), 2000);
                                }}
                            >
                                {copied === idx ? <Check className="h-4 w-4 text-green-500" /> : <Copy className="h-4 w-4" />}
                            </Button>
                        </div>
                    ))}
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Terminal, AlertCircle, CheckCircle2, History, ChevronRight } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';

export default function BashScriptValidator() {
    const [input, setInput] = useState('');
    const [issues, setIssues] = useState<any[]>([]);

    const validate = () => {
        const found = [];
        if (!input.includes('#!/bin/bash') && !input.includes('#!/bin/sh')) {
            found.push({ type: 'warn', msg: 'Shebang line (#!/bin/bash) is missing.' });
        }
        if (input.includes('rm -rf /')) {
            found.push({ type: 'error', msg: 'Dangerous command detected (rm -rf /)!' });
        }
        if (input.match(/ \$[a-zA-Z0-9_]+/)) {
            found.push({ type: 'warn', msg: 'Unquoted variable usage detected. Use "$var" instead of $var.' });
        }

        setIssues(found);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Terminal className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Bash Script Validator</CardTitle>
                <p className="text-muted-foreground mt-2">Check your shell scripts for syntax errors and dangerous commands.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-8">
                <div className="space-y-4">
                    <label className="text-xs font-black uppercase tracking-widest text-primary">Script Input</label>
                    <Textarea
                        placeholder="#!/bin/bash\necho 'hello'..."
                        className="h-64 rounded-3xl border-primary/10 bg-muted/20 font-mono text-xs leading-relaxed"
                        value={input}
                        onChange={(e) => { setInput(e.target.value); validate(); }}
                    />
                </div>

                <div className="space-y-4">
                    <h4 className="font-bold flex items-center gap-2">
                        <History className="h-4 w-4 text-primary" /> Validation Results
                    </h4>
                    <div className="space-y-2">
                        {issues.map((issue, i) => (
                            <div key={i} className={`p-4 rounded-xl border flex items-center gap-4 animate-in slide-in-from-left-2 ${issue.type === 'error' ? 'bg-destructive/10 border-destructive/20 text-destructive' : 'bg-amber-500/10 border-amber-500/20 text-amber-500'
                                }`}>
                                <AlertCircle className="h-5 w-5 shrink-0" />
                                <span className="text-xs font-bold">{issue.msg}</span>
                            </div>
                        ))}
                        {issues.length === 0 && input.length > 0 && (
                            <div className="p-8 rounded-2xl bg-green-500/5 border border-green-500/10 text-center">
                                <CheckCircle2 className="h-8 w-8 text-green-500 mx-auto mb-2" />
                                <p className="text-sm font-bold text-green-600">No syntax issues detected</p>
                            </div>
                        )}
                        {input.length === 0 && (
                            <p className="text-center text-muted-foreground italic text-xs py-10 border-2 border-dashed border-primary/5 rounded-3xl">Enter code to start real-time validation...</p>
                        )}
                    </div>
                </div>

                <div className="p-6 rounded-2xl bg-primary/5 border border-primary/10 flex gap-4">
                    <ChevronRight className="h-6 w-6 text-primary shrink-0" />
                    <p className="text-xs text-muted-foreground italic leading-relaxed">
                        <b>Pro Tip:</b> Always use `shellcheck` in production environments for comprehensive static analysis of heavy infrastructure scripts.
                    </p>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import {
    FileJson,
    FileSpreadsheet,
    Copy,
    Trash2,
    Check,
    AlertCircle,
    ArrowRightLeft,
    Download,
    RefreshCw
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Textarea } from '@/components/ui/textarea';
import { cn } from '@/lib/utils';
import { toast } from 'sonner';

export default function CSVJSONConverter({ mode }: { mode: 'csv-to-json' | 'json-to-csv' }) {
    const [input, setInput] = useState('');
    const [output, setOutput] = useState('');
    const [error, setError] = useState<string | null>(null);
    const [copied, setCopied] = useState(false);

    const convertAction = () => {
        try {
            setError(null);
            if (!input.trim()) {
                setOutput('');
                return;
            }

            if (mode === 'csv-to-json') {
                const lines = input.trim().split('\n');
                if (lines.length < 1) throw new Error('Input is empty');

                const headers = lines[0].split(',').map(h => h.trim().replace(/^"|"$/g, ''));
                const result = lines.slice(1).map(line => {
                    const values = line.split(',').map(v => v.trim().replace(/^"|"$/g, ''));
                    const obj: any = {};
                    headers.forEach((header, i) => {
                        obj[header] = values[i] || '';
                    });
                    return obj;
                });
                setOutput(JSON.stringify(result, null, 2));
            } else {
                try {
                    const json = JSON.parse(input);
                    const array = Array.isArray(json) ? json : [json];
                    if (array.length === 0) throw new Error('Empty array');

                    const headers = Object.keys(array[0]);
                    const csvLines = [
                        headers.join(','),
                        ...array.map(obj => headers.map(header => {
                            const val = String(obj[header] || '');
                            return val.includes(',') ? `"${val}"` : val;
                        }).join(','))
                    ];
                    setOutput(csvLines.join('\n'));
                } catch (e: any) {
                    throw new Error('Invalid JSON format. Please provide an array of objects.');
                }
            }
            toast.success('Converted successfully!');
        } catch (err: any) {
            setError(err.message);
            toast.error(err.message);
        }
    };

    const copyToClipboard = () => {
        navigator.clipboard.writeText(output);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
        toast.info('Copied to clipboard');
    };

    const downloadOutput = () => {
        const blob = new Blob([output], { type: mode === 'csv-to-json' ? 'application/json' : 'text/csv' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = mode === 'csv-to-json' ? 'converted.json' : 'converted.csv';
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
    };

    return (
        <div className="max-w-5xl mx-auto space-y-8 p-4">
            <div className="grid lg:grid-cols-2 gap-8">
                {/* Input Area */}
                <div className="space-y-4">
                    <div className="flex items-center justify-between">
                        <h3 className="text-sm font-bold uppercase tracking-widest text-muted-foreground flex items-center gap-2">
                            {mode === 'csv-to-json' ? <FileSpreadsheet className="h-4 w-4" /> : <FileJson className="h-4 w-4" />}
                            Input {mode === 'csv-to-json' ? 'CSV' : 'JSON'}
                        </h3>
                        <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => { setInput(''); setOutput(''); setError(null); }}
                            className="text-xs font-bold text-destructive hover:bg-destructive/10 rounded-xl"
                        >
                            <Trash2 className="h-3 w-3 mr-1" /> Clear
                        </Button>
                    </div>
                    <Card className="rounded-[2rem] border-primary/10 bg-muted/30 overflow-hidden">
                        <Textarea
                            value={input}
                            onChange={(e) => setInput(e.target.value)}
                            placeholder={mode === 'csv-to-json' ? "name,age,city\nJohn,30,New York\nJane,25,London" : '[{"name":"John","age":30,"city":"New York"}]'}
                            className="min-h-[400px] border-none focus-visible:ring-0 bg-transparent p-6 font-mono text-sm resize-none"
                        />
                    </Card>
                </div>

                {/* Output Area */}
                <div className="space-y-4">
                    <div className="flex items-center justify-between">
                        <h3 className="text-sm font-bold uppercase tracking-widest text-primary flex items-center gap-2">
                            {mode === 'csv-to-json' ? <FileJson className="h-4 w-4" /> : <FileSpreadsheet className="h-4 w-4" />}
                            Output {mode === 'csv-to-json' ? 'JSON' : 'CSV'}
                        </h3>
                        {output && (
                            <div className="flex gap-2">
                                <Button
                                    variant="outline"
                                    size="sm"
                                    onClick={copyToClipboard}
                                    className="text-xs font-bold rounded-xl h-8"
                                >
                                    {copied ? <Check className="h-3 w-3 mr-1" /> : <Copy className="h-3 w-3 mr-1" />}
                                    {copied ? 'Copied' : 'Copy'}
                                </Button>
                                <Button
                                    variant="outline"
                                    size="sm"
                                    onClick={downloadOutput}
                                    className="text-xs font-bold rounded-xl h-8"
                                >
                                    <Download className="h-3 w-3 mr-1" /> Download
                                </Button>
                            </div>
                        )}
                    </div>
                    <Card className="rounded-[2rem] border-primary/20 bg-background shadow-inner overflow-hidden flex flex-col min-h-[400px]">
                        {error ? (
                            <div className="flex-1 flex flex-col items-center justify-center p-8 text-center space-y-4">
                                <div className="h-12 w-12 rounded-full bg-destructive/10 flex items-center justify-center">
                                    <AlertCircle className="h-6 w-6 text-destructive" />
                                </div>
                                <p className="text-sm font-medium text-destructive">{error}</p>
                            </div>
                        ) : output ? (
                            <pre className="flex-1 p-6 font-mono text-sm overflow-auto text-primary">
                                {output}
                            </pre>
                        ) : (
                            <div className="flex-1 flex flex-col items-center justify-center p-8 text-center text-muted-foreground opacity-40">
                                <RefreshCw className="h-12 w-12 mb-4 animate-pulse" />
                                <p className="text-sm">Click the button to convert your data</p>
                            </div>
                        )}
                    </Card>
                </div>
            </div>

            <div className="flex justify-center">
                <Button
                    size="lg"
                    onClick={convertAction}
                    className="h-16 rounded-[1.5rem] px-12 text-xl font-bold shadow-2xl shadow-primary/20 group overflow-hidden relative"
                >
                    <div className="absolute inset-0 bg-gradient-to-r from-primary via-blue-500 to-primary bg-[length:200%_100%] animate-shimmer opacity-0 group-hover:opacity-100 transition-opacity" />
                    <span className="relative flex items-center gap-3">
                        Convert Now <ArrowRightLeft className="h-6 w-6 group-hover:rotate-180 transition-transform duration-500" />
                    </span>
                </Button>
            </div>
        </div>
    );
}

'use client';

