'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Hash, Plus, Trash2, Info, RefreshCw, Calculator, ArrowRightLeft } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';

export default function ComplexNumberPro() {
    const [real1, setReal1] = useState('3');
    const [imag1, setImag1] = useState('4');
    const [real2, setReal2] = useState('1');
    const [imag2, setImag2] = useState('-2');
    const [op, setOp] = useState<'add' | 'sub' | 'mult' | 'div'>('add');

    const calculate = () => {
        const r1 = parseFloat(real1) || 0;
        const i1 = parseFloat(imag1) || 0;
        const r2 = parseFloat(real2) || 0;
        const i2 = parseFloat(imag2) || 0;

        let resR = 0;
        let resI = 0;

        switch (op) {
            case 'add':
                resR = r1 + r2;
                resI = i1 + i2;
                break;
            case 'sub':
                resR = r1 - r2;
                resI = i1 - i2;
                break;
            case 'mult':
                // (a+bi)(c+di) = (ac - bd) + (ad + bc)i
                resR = r1 * r2 - i1 * i2;
                resI = r1 * i2 + i1 * r2;
                break;
            case 'div':
                // (a+bi)/(c+di) = [(ac + bd)/(c2+d2)] + [(bc - ad)/(c2+d2)]i
                const den = r2 * r2 + i2 * i2;
                if (den === 0) return { resR: 0, resI: 0, error: 'Division by Zero' };
                resR = (r1 * r2 + i1 * i2) / den;
                resI = (i1 * r2 - r1 * i2) / den;
                break;
        }

        const mod = Math.sqrt(resR * resR + resI * resI);
        const arg = Math.atan2(resI, resR) * (180 / Math.PI);

        return { resR, resI: resI.toFixed(2), resR_fixed: resR.toFixed(2), mod: mod.toFixed(2), arg: arg.toFixed(2) };
    };

    const result = calculate() as any;

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden font-scholar">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Hash className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Complex Number Pro</CardTitle>
                <p className="text-muted-foreground mt-2">Advanced arithmetic and polar conversions for imaginary number systems.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-12 items-center">
                    {/* Complex Input 1 */}
                    <div className="space-y-6">
                        <p className="text-[10px] font-black uppercase text-primary/40 tracking-[0.4em] text-center">Number 1 (a + bi)</p>
                        <div className="flex items-center gap-4 bg-background p-8 rounded-[3rem] border border-primary/5 shadow-inner">
                            <Input value={real1} onChange={(e) => setReal1(e.target.value)} className="h-16 text-3xl font-black text-center border-none bg-transparent" />
                            <span className="text-4xl font-black text-primary opacity-20">+</span>
                            <div className="relative">
                                <Input value={imag1} onChange={(e) => setImag1(e.target.value)} className="h-16 text-3xl font-black text-center border-none bg-transparent" />
                                <span className="absolute -right-4 top-1/2 -translate-y-1/2 text-2xl font-black text-primary italic">i</span>
                            </div>
                        </div>
                    </div>

                    {/* Complex Input 2 */}
                    <div className="space-y-6">
                        <p className="text-[10px] font-black uppercase text-primary/40 tracking-[0.4em] text-center">Number 2 (c + di)</p>
                        <div className="flex items-center gap-4 bg-background p-8 rounded-[3rem] border border-primary/5 shadow-inner">
                            <Input value={real2} onChange={(e) => setReal2(e.target.value)} className="h-16 text-3xl font-black text-center border-none bg-transparent" />
                            <span className="text-4xl font-black text-primary opacity-20">+</span>
                            <div className="relative">
                                <Input value={imag2} onChange={(e) => setImag2(e.target.value)} className="h-16 text-3xl font-black text-center border-none bg-transparent" />
                                <span className="absolute -right-4 top-1/2 -translate-y-1/2 text-2xl font-black text-primary italic">i</span>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="flex justify-center gap-4">
                    {['add', 'sub', 'mult', 'div'].map(m => (
                        <Button
                            key={m}
                            variant={op === m ? 'default' : 'outline'}
                            onClick={() => setOp(m as any)}
                            className="h-14 rounded-2xl px-8 font-black uppercase text-xs tracking-widest"
                        >
                            {m === 'add' ? 'Add' : m === 'sub' ? 'Sub' : m === 'mult' ? 'Multiply' : 'Divide'}
                        </Button>
                    ))}
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                    <div className="p-16 rounded-[4rem] bg-primary text-white border-4 border-primary shadow-3xl shadow-primary/30 flex flex-col items-center justify-center gap-4">
                        <p className="text-[10px] font-black uppercase text-white/50 tracking-[0.5em]">RECTANGULAR RESULT</p>
                        <h2 className="text-6xl font-black">
                            {result.resR_fixed} <span className="opacity-40">{parseFloat(result.resI) >= 0 ? '+' : ''}</span> {result.resI}<span className="italic ml-1">i</span>
                        </h2>
                    </div>

                    <div className="space-y-4">
                        <PolarStat label="Modulus |z|" value={result.mod} icon={<Calculator className="h-4 w-4" />} />
                        <PolarStat label="Argument (Arg z)" value={`${result.arg}├é┬░`} icon={<RefreshCw className="h-4 w-4" />} />
                        <div className="p-8 rounded-[2.5rem] bg-muted/10 border border-primary/5 flex items-center justify-between">
                            <span className="text-[10px] font-black uppercase text-primary/40">Euler Form</span>
                            <span className="text-xl font-black text-primary font-mono">{result.mod}e^{result.arg}├é┬░i</span>
                        </div>
                    </div>
                </div>

                <div className="p-8 rounded-[2.5rem] bg-amber-500/5 border border-amber-500/10 flex items-start gap-4">
                    <Info className="h-6 w-6 text-amber-500 shrink-0 mt-1" />
                    <div className="text-xs text-muted-foreground leading-relaxed italic">
                        The complex plane uses the real part on the x-axis and the imaginary part on the y-axis. Multiplication in this plane corresponds to rotating and scaling the vectors.
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

function PolarStat({ label, value, icon }: any) {
    return (
        <div className="flex justify-between items-center p-6 rounded-3xl bg-background border border-primary/5 shadow-lg group hover:border-primary/20 transition-all">
            <div className="flex items-center gap-3">
                <div className="text-primary/20 group-hover:text-primary transition-colors">{icon}</div>
                <span className="text-[10px] font-black uppercase text-primary/40 tracking-widest">{label}</span>
            </div>
            <span className="text-2xl font-black text-primary">{value}</span>
        </div>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Braces, Calculator, BookOpen, ChevronRight, Info, Zap } from 'lucide-react';
import { Button } from '@/components/ui/button';

const rules = {
    derivatives: [
        { name: 'Power Rule', rule: 'd/dx [x├ó ┬┐] = nx├ó ┬┐├ó ┬╗├é┬╣', ex: 'd/dx [x├é┬│] = 3x├é┬▓' },
        { name: 'Product Rule', rule: 'd/dx [f(x)g(x)] = f\'g + fg\'', ex: 'd/dx [x sin(x)] = sin(x) + x cos(x)' },
        { name: 'Chain Rule', rule: 'd/dx [f(g(x))] = f\'(g(x))g\'(x)', ex: 'd/dx [sin(x├é┬▓)] = 2x cos(x├é┬▓)' }
    ],
    integrals: [
        { name: 'Power Rule', rule: '├ó╦å┬½ x├ó ┬┐ dx = (x├ó ┬┐├ó ┬║├é┬╣)/(n+1) + C', ex: '├ó╦å┬½ x├é┬▓ dx = x├é┬│/3 + C' },
        { name: 'Exponential', rule: '├ó╦å┬½ e├ï┬ú dx = e├ï┬ú + C', ex: '├ó╦å┬½ e├ï┬ú dx = e├ï┬ú + C' },
        { name: 'Sine', rule: '├ó╦å┬½ sin(x) dx = -cos(x) + C', ex: '├ó╦å┬½ sin(x) dx = -cos(x) + C' }
    ]
};

export default function DerivativeIntegralRules() {
    const [mode, setMode] = useState<'derivatives' | 'integrals'>('derivatives');

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden font-scholar">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Calculator className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Differentiator & Integrator</CardTitle>
                <p className="text-muted-foreground mt-2">A comprehensive reference and rule solver for fundamental calculus operations.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="flex justify-center">
                    <div className="bg-muted/10 p-2 rounded-2xl border border-primary/5 flex gap-2">
                        <Button
                            variant={mode === 'derivatives' ? 'default' : 'ghost'}
                            onClick={() => setMode('derivatives')}
                            className="rounded-xl px-10 h-12 font-black"
                        >
                            Derivatives
                        </Button>
                        <Button
                            variant={mode === 'integrals' ? 'default' : 'ghost'}
                            onClick={() => setMode('integrals')}
                            className="rounded-xl px-10 h-12 font-black"
                        >
                            Integrals
                        </Button>
                    </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {rules[mode].map((r, i) => (
                        <div key={i} className="group p-8 rounded-[3rem] bg-background border border-primary/5 hover:border-primary/20 hover:scale-[1.05] transition-all shadow-xl flex flex-col items-center text-center gap-4">
                            <div className="w-12 h-12 rounded-2xl bg-primary/10 flex items-center justify-center group-hover:bg-primary group-hover:text-primary-foreground transition-colors">
                                <Zap className="h-6 w-6" />
                            </div>
                            <h4 className="font-black text-primary uppercase text-[10px] tracking-widest">{r.name}</h4>
                            <p className="text-2xl font-black font-mono leading-tight">{r.rule}</p>
                            <div className="mt-4 pt-4 border-t border-primary/5 w-full">
                                <p className="text-[10px] font-bold text-muted-foreground mb-1 uppercase">Instant Example</p>
                                <p className="text-sm font-black italic text-primary/60">{r.ex}</p>
                            </div>
                        </div>
                    ))}
                </div>

                <div className="p-10 rounded-[3.5rem] bg-primary/5 border border-primary/10 space-y-4">
                    <div className="flex items-center gap-3">
                        <div className="p-2 rounded-lg bg-primary text-white">
                            <BookOpen className="h-5 w-5" />
                        </div>
                        <h4 className="text-lg font-black text-primary">Fundamental Theorem of Calculus</h4>
                    </div>
                    <p className="text-sm text-muted-foreground leading-relaxed italic">
                        The theorem states that differentiation and integration are inverse operations. If you integrate a function and then differentiate it, you return to the original function.
                    </p>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Braces, Play, ArrowRight, Info, Zap, RefreshCw, Calculator } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';

export default function DifferentialEqPro() {
    const [equation, setEquation] = useState('dy/dx = 2x');
    const [result, setResult] = useState<any>(null);
    const [isSolving, setIsSolving] = useState(false);

    const solve = () => {
        setIsSolving(true);
        // Heuristic solver for simple first order
        setTimeout(() => {
            let sol = '';
            let step1 = '';
            let step2 = '';

            if (equation.replace(/\s/g, '') === 'dy/dx=2x') {
                sol = 'y = x├é┬▓ + C';
                step1 = 'Integrate both sides with respect to x';
                step2 = '├ó╦å┬½ dy = ├ó╦å┬½ 2x dx';
            } else if (equation.replace(/\s/g, '') === 'dy/dx=y') {
                sol = 'y = Ce├ï┬│';
                step1 = 'Separate variables: (1/y) dy = dx';
                step2 = '├ó╦å┬½ (1/y) dy = ├ó╦å┬½ dx => ln|y| = x + C';
            } else {
                sol = 'Resolution pending (Standard form required)';
                step1 = 'Identifying equation type...';
                step2 = 'Applying separation of variables...';
            }

            setResult({ sol, steps: [step1, step2] });
            setIsSolving(false);
        }, 800);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden font-scholar">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Braces className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Differential Eq. Pro</CardTitle>
                <p className="text-muted-foreground mt-2">Advanced calculus engine for first-order linear and separable differential equations.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="flex flex-col md:flex-row gap-6">
                    <div className="relative flex-1">
                        <Input
                            value={equation}
                            onChange={(e) => setEquation(e.target.value)}
                            placeholder="e.g. dy/dx = 2x"
                            className="h-20 rounded-[2.5rem] text-4xl font-black px-10 bg-background border-primary/20 focus:border-primary transition-all font-mono"
                        />
                        <p className="absolute -bottom-6 left-6 text-[10px] font-bold text-primary opacity-40">Format: dy/dx = f(x, y)</p>
                    </div>
                    <Button onClick={solve} disabled={isSolving} className="h-20 px-12 rounded-[2.5rem] text-lg font-black shadow-xl shadow-primary/20">
                        {isSolving ? <RefreshCw className="animate-spin" /> : 'DERIVE'}
                    </Button>
                </div>

                {result && (
                    <div className="space-y-8 animate-in slide-in-from-bottom-6 duration-700">
                        <div className="p-16 rounded-[4rem] bg-primary text-white border-4 border-primary shadow-3xl shadow-primary/30 flex flex-col items-center justify-center gap-4">
                            <p className="text-[10px] font-black uppercase text-white/50 tracking-[0.5em]">GENERAL SOLUTION</p>
                            <h2 className="text-6xl font-black font-mono tracking-tighter">{result.sol}</h2>
                        </div>

                        <div className="space-y-4">
                            {result.steps.map((s: string, i: number) => (
                                <div key={i} className="flex items-center gap-6 group">
                                    <div className="w-10 h-10 rounded-full bg-primary/10 text-primary flex items-center justify-center font-black text-xs">
                                        {i + 1}
                                    </div>
                                    <div className="flex-1 p-6 rounded-[2rem] bg-muted/20 border border-primary/5 font-bold italic text-muted-foreground flex justify-between items-center pr-10">
                                        {s}
                                        <ArrowRight className="h-5 w-5 opacity-20" />
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                <div className="p-10 rounded-[4rem] bg-amber-500/5 border border-amber-500/10 flex items-start gap-4">
                    <Info className="h-6 w-6 text-amber-500 shrink-0 mt-1" />
                    <div className="text-xs text-muted-foreground leading-relaxed">
                        <p className="font-black text-amber-600 uppercase mb-2 tracking-widest">Calculus Insight:</p>
                        A differential equation relates a function with its derivatives. Solving it means finding the original function $y(x)$ that satisfies the relationship. The constant $C$ represents an infinite family of possible solutions.
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
