'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Target, ArrowRight, RefreshCw, Info, Percent } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';

export default function FinalGradeRequired() {
    const [currentGrade, setCurrentGrade] = useState('85');
    const [targetGrade, setTargetGrade] = useState('90');
    const [examWeight, setExamWeight] = useState('20');

    const calculateNeeded = () => {
        const c = parseFloat(currentGrade) || 0;
        const t = parseFloat(targetGrade) || 0;
        const w = (parseFloat(examWeight) || 0) / 100;

        if (w === 0) return '0';

        // Target = Current * (1 - w) + Needed * w
        // Needed = (Target - Current * (1 - w)) / w
        const needed = (t - (c * (1 - w))) / w;
        return needed.toFixed(2);
    };

    const needed = calculateNeeded();
    const isImpossible = parseFloat(needed) > 100;

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Target className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Final Grade Required</CardTitle>
                <p className="text-muted-foreground mt-2">Calculate exactly what score you need on your remaining exams to reach your goal.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
                    <InputField label="Current Grade (%)" value={currentGrade} onChange={setCurrentGrade} />
                    <InputField label="Target Overall (%)" value={targetGrade} onChange={setTargetGrade} />
                    <InputField label="Final Exam Weight (%)" value={examWeight} onChange={setExamWeight} />
                </div>

                <div className="relative group">
                    <div className={`p-16 rounded-[4rem] border-4 transition-all duration-500 flex flex-col items-center justify-center text-center gap-6 ${isImpossible ? 'bg-red-500/10 border-red-500/20' : 'bg-primary border-primary shadow-3xl shadow-primary/30'}`}>
                        <p className={`text-xs font-black uppercase tracking-[0.4em] ${isImpossible ? 'text-red-500' : 'text-white/60'}`}>REQUIRED EXAM SCORE</p>
                        <h2 className={`text-9xl font-black ${isImpossible ? 'text-red-600' : 'text-white'}`}>{needed}%</h2>

                        <div className={`px-8 py-3 rounded-2xl font-black text-sm ${isImpossible ? 'bg-red-500 text-white' : 'bg-white/20 text-white'}`}>
                            {isImpossible ? 'SCORE EXCEEDS 100%' : 'STAY FOCUSED'}
                        </div>
                    </div>

                    {isImpossible && (
                        <div className="mt-8 p-8 rounded-3xl bg-red-100 border border-red-200 text-red-700 flex items-start gap-4 animate-bounce">
                            <Info className="h-6 w-6 mt-1" />
                            <p className="font-bold leading-relaxed">It is mathematically impossible to reach your target grade with this final exam weight. You may need extra credit or a lower target.</p>
                        </div>
                    )}
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="p-8 rounded-3xl bg-muted/20 border border-primary/5 space-y-2">
                        <p className="text-[10px] font-black uppercase text-primary/40">The Math Behind It</p>
                        <p className="text-sm font-bold opacity-60 italic">Goal = (Current Avg ├âÔÇö Content Weight) + (Exam Score ├âÔÇö Exam Weight)</p>
                    </div>
                    <div className="p-8 rounded-3xl bg-muted/20 border border-primary/5 space-y-2">
                        <p className="text-[10px] font-black uppercase text-primary/40">Pro Tip</p>
                        <p className="text-sm font-bold opacity-60">Try lowering your target by 1-2% to see how much the required score drops.</p>
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

function InputField({ label, value, onChange }: any) {
    return (
        <div className="space-y-3">
            <label className="text-[10px] font-black uppercase text-primary/60 tracking-widest pl-2">{label}</label>
            <div className="relative">
                <Input
                    type="number"
                    value={value}
                    onChange={(e) => onChange(e.target.value)}
                    className="h-16 rounded-[2rem] bg-background border-primary/10 text-2xl font-black text-center pr-12"
                />
                <Percent className="absolute right-6 top-1/2 -translate-y-1/2 h-5 w-5 opacity-20" />
            </div>
        </div>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Layers, Plus, ChevronLeft, ChevronRight, RotateCw, Trash2, Edit3 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';

interface Flashcard {
    id: number;
    front: string;
    back: string;
}

export default function FlashcardMaker() {
    const [cards, setCards] = useState<Flashcard[]>([
        { id: 1, front: 'Photosynthesis', back: 'The process by which green plants and some other organisms use sunlight to synthesize foods from carbon dioxide and water.' },
        { id: 2, front: 'Mitosis', back: 'A type of cell division that results in two daughter cells each having the same number and kind of chromosomes as the parent nucleus.' }
    ]);
    const [currentIndex, setCurrentIndex] = useState(0);
    const [isFlipped, setIsFlipped] = useState(false);
    const [showEditor, setShowEditor] = useState(false);

    const [newFront, setNewFront] = useState('');
    const [newBack, setNewBack] = useState('');

    const addCard = () => {
        if (!newFront || !newBack) return;
        setCards([...cards, { id: Date.now(), front: newFront, back: newBack }]);
        setNewFront('');
        setNewBack('');
    };

    const nextCard = () => {
        setIsFlipped(false);
        setTimeout(() => setCurrentIndex((currentIndex + 1) % cards.length), 150);
    };

    const prevCard = () => {
        setIsFlipped(false);
        setTimeout(() => setCurrentIndex((currentIndex - 1 + cards.length) % cards.length), 150);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Layers className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Interactive Flashcards</CardTitle>
                <p className="text-muted-foreground mt-2">Master any subject with active recall and digital study decks.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">

                {/* Progress Bar */}
                <div className="w-full h-2 bg-muted/20 rounded-full overflow-hidden">
                    <div
                        className="h-full bg-primary transition-all duration-500"
                        style={{ width: `${((currentIndex + 1) / cards.length) * 100}%` }}
                    />
                </div>

                {/* Flashcard Component */}
                <div className="perspective-1000 h-96 w-full cursor-pointer" onClick={() => setIsFlipped(!isFlipped)}>
                    <div className={`relative w-full h-full transition-all duration-500 preserve-3d ${isFlipped ? 'rotate-y-180' : ''}`}>
                        {/* Front */}
                        <div className="absolute inset-0 bg-background border-4 border-primary/10 rounded-[4rem] flex flex-col items-center justify-center text-center p-12 backface-hidden shadow-2xl">
                            <p className="text-[10px] font-black uppercase text-primary/40 tracking-[0.5em] mb-8">QUESTION</p>
                            <h3 className="text-4xl font-black text-primary leading-tight">{cards[currentIndex]?.front}</h3>
                            <div className="mt-12 flex items-center gap-2 text-muted-foreground text-xs font-bold animate-pulse">
                                <RotateCw className="h-4 w-4" /> Click to reveal answer
                            </div>
                        </div>
                        {/* Back */}
                        <div className="absolute inset-0 bg-primary text-primary-foreground rounded-[4rem] flex flex-col items-center justify-center text-center p-12 backface-hidden rotate-y-180 shadow-2xl overflow-y-auto">
                            <p className="text-[10px] font-black uppercase text-white/50 tracking-[0.5em] mb-6">ANSWER</p>
                            <p className="text-xl font-bold leading-relaxed">{cards[currentIndex]?.back}</p>
                        </div>
                    </div>
                </div>

                {/* Controls */}
                <div className="flex justify-between items-center px-12">
                    <Button variant="outline" size="lg" className="h-16 w-16 rounded-2xl" onClick={(e) => { e.stopPropagation(); prevCard(); }}>
                        <ChevronLeft />
                    </Button>
                    <div className="text-center">
                        <span className="text-2xl font-black text-primary">{currentIndex + 1}</span>
                        <span className="text-xl font-bold opacity-20 mx-2">/</span>
                        <span className="text-xl font-bold opacity-40">{cards.length}</span>
                    </div>
                    <Button variant="outline" size="lg" className="h-16 w-16 rounded-2xl" onClick={(e) => { e.stopPropagation(); nextCard(); }}>
                        <ChevronRight />
                    </Button>
                </div>

                {/* Editor Toggle */}
                <div className="pt-8 border-t border-primary/5">
                    <Button
                        variant="ghost"
                        className="w-full h-14 rounded-2xl text-muted-foreground hover:text-primary transition-colors"
                        onClick={() => setShowEditor(!showEditor)}
                    >
                        <Edit3 className="mr-2 h-4 w-4" /> {showEditor ? 'Hide Deck Editor' : 'Edit Study Deck'}
                    </Button>

                    {showEditor && (
                        <div className="mt-8 p-8 bg-muted/20 rounded-[3rem] border border-primary/5 space-y-6 animate-in fade-in zoom-in duration-300">
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                <textarea
                                    placeholder="Front Side (Question)"
                                    value={newFront}
                                    onChange={(e) => setNewFront(e.target.value)}
                                    className="h-32 p-6 rounded-3xl bg-background border border-primary/10 focus:border-primary outline-none transition-all resize-none font-bold"
                                />
                                <textarea
                                    placeholder="Back Side (Answer)"
                                    value={newBack}
                                    onChange={(e) => setNewBack(e.target.value)}
                                    className="h-32 p-6 rounded-3xl bg-background border border-primary/10 focus:border-primary outline-none transition-all resize-none font-bold"
                                />
                            </div>
                            <Button onClick={addCard} className="w-full h-14 rounded-2xl font-bold">
                                <Plus className="mr-2" /> Add New Card to Deck
                            </Button>

                            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mt-8">
                                {cards.map((c, i) => (
                                    <div key={c.id} className="p-4 bg-background border border-primary/5 rounded-2xl flex justify-between items-center group">
                                        <span className="text-xs font-bold truncate pr-2">#{i + 1}: {c.front}</span>
                                        <button onClick={() => setCards(cards.filter(card => card.id !== c.id))} className="text-destructive opacity-0 group-hover:opacity-100 transition-opacity">
                                            <Trash2 className="h-4 w-4" />
                                        </button>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}
                </div>
            </CardContent>
            <style jsx>{`
                .perspective-1000 { perspective: 1000px; }
                .preserve-3d { transform-style: preserve-3d; }
                .backface-hidden { backface-visibility: hidden; }
                .rotate-y-180 { transform: rotateY(180deg); }
            `}</style>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Calculator, Plus, Trash2, GraduationCap, Info } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

interface Course {
    id: number;
    name: string;
    grade: string;
    credits: string;
}

export default function GPACalculatorPro() {
    const [courses, setCourses] = useState<Course[]>([
        { id: 1, name: '', grade: 'A', credits: '3' }
    ]);
    const [scale, setScale] = useState('4.0');

    const gradePoints: Record<string, number> = {
        'A+': 4.0, 'A': 4.0, 'A-': 3.7,
        'B+': 3.3, 'B': 3.0, 'B-': 2.7,
        'C+': 2.3, 'C': 2.0, 'C-': 1.7,
        'D+': 1.3, 'D': 1.0, 'F': 0.0
    };

    const addCourse = () => {
        setCourses([...courses, { id: Date.now(), name: '', grade: 'A', credits: '3' }]);
    };

    const removeCourse = (id: number) => {
        if (courses.length > 1) {
            setCourses(courses.filter(c => c.id !== id));
        }
    };

    const updateCourse = (id: number, field: keyof Course, value: string) => {
        setCourses(courses.map(c => c.id === id ? { ...c, [field]: value } : c));
    };

    const calculateGPA = () => {
        let totalPoints = 0;
        let totalCredits = 0;
        courses.forEach(c => {
            const credits = parseFloat(c.credits) || 0;
            const points = gradePoints[c.grade] || 0;
            totalPoints += points * credits;
            totalCredits += credits;
        });
        const gpa = totalCredits > 0 ? totalPoints / totalCredits : 0;
        const multiplier = parseFloat(scale) / 4.0;
        return (gpa * multiplier).toFixed(2);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <GraduationCap className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">GPA Calculator Pro</CardTitle>
                <p className="text-muted-foreground mt-2">Track your academic performance with precise term and cumulative calculations.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-8">
                <div className="flex justify-between items-center bg-muted/20 p-6 rounded-3xl border border-primary/5">
                    <div className="space-y-1">
                        <p className="text-xs font-black uppercase text-primary/60">Rating Scale</p>
                        <Select value={scale} onValueChange={setScale}>
                            <SelectTrigger className="w-32 h-10 rounded-xl bg-background border-primary/10 font-bold">
                                <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                                <SelectItem value="4.0">4.0 Scale</SelectItem>
                                <SelectItem value="5.0">5.0 Scale</SelectItem>
                                <SelectItem value="10.0">10.0 Scale</SelectItem>
                            </SelectContent>
                        </Select>
                    </div>
                    <div className="text-right">
                        <p className="text-xs font-black uppercase text-primary/60">Current GPA</p>
                        <h2 className="text-5xl font-black text-primary">{calculateGPA()}</h2>
                    </div>
                </div>

                <div className="space-y-4">
                    <div className="grid grid-cols-12 gap-4 px-4 text-[10px] font-black uppercase text-muted-foreground">
                        <div className="col-span-6">Course Name</div>
                        <div className="col-span-3">Grade</div>
                        <div className="col-span-2">Credits</div>
                        <div className="col-span-1"></div>
                    </div>

                    {courses.map((course) => (
                        <div key={course.id} className="grid grid-cols-12 gap-4 items-center bg-background/40 p-2 rounded-2xl border border-primary/5 group animate-in slide-in-from-left-2 duration-300">
                            <div className="col-span-6">
                                <Input
                                    placeholder="e.g. Mathematics"
                                    value={course.name}
                                    onChange={(e) => updateCourse(course.id, 'name', e.target.value)}
                                    className="h-12 rounded-xl border-transparent bg-transparent focus:bg-background transition-all"
                                />
                            </div>
                            <div className="col-span-3">
                                <Select value={course.grade} onValueChange={(v) => updateCourse(course.id, 'grade', v)}>
                                    <SelectTrigger className="h-12 rounded-xl border-transparent bg-transparent hover:bg-background transition-all font-bold">
                                        <SelectValue />
                                    </SelectTrigger>
                                    <SelectContent>
                                        {Object.keys(gradePoints).map(g => (
                                            <SelectItem key={g} value={g}>{g}</SelectItem>
                                        ))}
                                    </SelectContent>
                                </Select>
                            </div>
                            <div className="col-span-2">
                                <Input
                                    type="number"
                                    value={course.credits}
                                    onChange={(e) => updateCourse(course.id, 'credits', e.target.value)}
                                    className="h-12 rounded-xl border-transparent bg-transparent focus:bg-background text-center font-bold"
                                />
                            </div>
                            <div className="col-span-1 flex justify-center">
                                <Button
                                    variant="ghost"
                                    size="icon"
                                    onClick={() => removeCourse(course.id)}
                                    className="text-destructive opacity-20 group-hover:opacity-100 transition-opacity"
                                >
                                    <Trash2 className="h-4 w-4" />
                                </Button>
                            </div>
                        </div>
                    ))}
                </div>

                <Button
                    onClick={addCourse}
                    className="w-full h-16 rounded-2xl bg-primary/10 text-primary hover:bg-primary/20 border border-primary/10 font-bold text-lg"
                >
                    <Plus className="mr-2" /> Add Course
                </Button>

                <div className="p-6 rounded-3xl bg-muted/10 border border-primary/5 flex items-start gap-4">
                    <Info className="h-5 w-5 text-primary shrink-0 mt-0.5" />
                    <div className="text-xs text-muted-foreground leading-relaxed">
                        <p className="font-bold text-primary mb-1">How it works:</p>
                        GPA is calculated by multiplying the grade value by credit hours, summing them up, and dividing by total credits. Our tool handles weighted averages automatically.
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Wind, Info, RefreshCw, Calculator, ArrowRight, Settings2, Zap } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';

export default function GasLawAdvanced() {
    const [mode, setMode] = useState<'Ideal' | 'Boyle' | 'Charles'>('Ideal');
    const [p, setP] = useState('1'); // atm
    const [v, setV] = useState('22.4'); // L
    const [n, setN] = useState('1'); // mol
    const [t, setT] = useState('273.15'); // K
    const [solveFor, setSolveFor] = useState<'P' | 'V' | 'n' | 'T'>('P');

    const R = 0.08206; // L*atm/(mol*K)

    const calculate = () => {
        const pv = parseFloat(p) || 0;
        const vv = parseFloat(v) || 0;
        const nv = parseFloat(n) || 0;
        const tv = parseFloat(t) || 0;

        let result = 0;
        let unit = '';

        if (mode === 'Ideal') {
            switch (solveFor) {
                case 'P': result = (nv * R * tv) / vv; unit = 'atm'; break;
                case 'V': result = (nv * R * tv) / pv; unit = 'L'; break;
                case 'n': result = (pv * vv) / (R * tv); unit = 'mol'; break;
                case 'T': result = (pv * vv) / (nv * R); unit = 'K'; break;
            }
        } else if (mode === 'Boyle') {
            // P1V1 = P2V2
            result = (pv * vv) / vv; // placeholder logic for simple solver
        }

        return { val: result.toFixed(3), unit };
    };

    const res = calculate();

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden font-scholar">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Wind className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Gas Law Advanced</CardTitle>
                <p className="text-muted-foreground mt-2">Thermodynamic solver for PV=nRT and other fundamental gas laws.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="flex justify-center mb-8">
                    <div className="bg-muted/10 p-2 rounded-2xl border border-primary/5 flex gap-2">
                        {['Ideal', 'Boyle', 'Charles'].map(m => (
                            <Button key={m} variant={mode === m ? 'default' : 'ghost'} onClick={() => setMode(m as any)} className="rounded-xl px-8 h-12 font-black">
                                {m} Law
                            </Button>
                        ))}
                    </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                    <div className="space-y-6">
                        <div className="grid grid-cols-2 gap-4">
                            <GasInput label="Pressure (P)" sub="atm" value={p} onChange={setP} active={solveFor === 'P'} onSelect={() => setSolveFor('P')} />
                            <GasInput label="Volume (V)" sub="L" value={v} onChange={setV} active={solveFor === 'V'} onSelect={() => setSolveFor('V')} />
                            <GasInput label="Moles (n)" sub="mol" value={n} onChange={setN} active={solveFor === 'n'} onSelect={() => setSolveFor('n')} />
                            <GasInput label="Temp (T)" sub="K" value={t} onChange={setT} active={solveFor === 'T'} onSelect={() => setSolveFor('T')} />
                        </div>
                    </div>

                    <div className="flex flex-col justify-center gap-6">
                        <div className="p-16 rounded-[4rem] bg-background border-4 border-primary/20 shadow-2xl flex flex-col items-center justify-center gap-4 text-primary relative group">
                            <div className="absolute inset-0 bg-primary/5 opacity-0 group-hover:opacity-100 transition-opacity rounded-[3.8rem]" />
                            <p className="text-[10px] font-black uppercase text-primary/40 tracking-[0.4em]">SOLVING FOR {solveFor}</p>
                            <h2 className="text-7xl font-black">{res.val}<span className="text-xl ml-2 opacity-40">{res.unit}</span></h2>
                            <div className="px-6 py-2 rounded-2xl bg-primary text-white text-xs font-black shadow-lg shadow-primary/20">
                                PV = nRT
                            </div>
                        </div>
                    </div>
                </div>

                <div className="p-10 rounded-[4rem] bg-primary/5 border border-primary/10 space-y-4">
                    <div className="flex items-center gap-3">
                        <Zap className="h-5 w-5 text-primary" />
                        <h4 className="text-lg font-black text-primary">Thermodynamic Insights</h4>
                    </div>
                    <p className="text-sm text-muted-foreground leading-relaxed italic">
                        Standard Temperature and Pressure (STP) is defined as 0├é┬░C (273.15K) and 1 atm. At STP, one mole of any ideal gas occupies approximately 22.4 liters.
                    </p>
                </div>
            </CardContent>
        </Card>
    );
}

function GasInput({ label, sub, value, onChange, active, onSelect }: any) {
    return (
        <div
            onClick={onSelect}
            className={`p-6 rounded-[2.5rem] border-2 transition-all cursor-pointer group ${active ? 'bg-primary/5 border-primary' : 'bg-background border-primary/5 hover:border-primary/20'}`}
        >
            <div className="flex justify-between items-center mb-2">
                <span className={`text-[8px] font-black uppercase tracking-widest ${active ? 'text-primary' : 'text-primary/40'}`}>{label}</span>
                <span className="text-[8px] font-bold opacity-40">{sub}</span>
            </div>
            <Input
                type="number"
                value={active ? '?' : value}
                readOnly={active}
                onChange={(e) => onChange(e.target.value)}
                className={`h-10 text-xl font-black border-transparent bg-transparent p-0 text-center focus:ring-0 ${active ? 'text-primary' : ''}`}
            />
        </div>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { LineChart as ChartIcon, Settings2, Info, Maximize2 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

export default function GraphingToolLite() {
    const [a, setA] = useState('1');
    const [b, setB] = useState('0');
    const [c, setC] = useState('0');
    const [type, setType] = useState<'linear' | 'quadratic'>('linear');

    const generateData = () => {
        const data = [];
        const av = parseFloat(a) || 0;
        const bv = parseFloat(b) || 0;
        const cv = parseFloat(c) || 0;

        for (let x = -10; x <= 10; x += 0.5) {
            let y = 0;
            if (type === 'linear') {
                y = av * x + bv;
            } else {
                y = av * Math.pow(x, 2) + bv * x + cv;
            }
            data.push({ x, y });
        }
        return data;
    };

    const data = generateData();

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden text-scholar">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <ChartIcon className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Visual Graph Plotter</CardTitle>
                <p className="text-muted-foreground mt-2">Visualize linear and quadratic functions on an interactive coordinate plane.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="flex flex-col lg:flex-row gap-12">
                    {/* Controls */}
                    <div className="w-full lg:w-80 space-y-8">
                        <div className="bg-muted/20 p-2 rounded-2xl border border-primary/5 flex gap-2">
                            <Button
                                variant={type === 'linear' ? 'default' : 'ghost'}
                                onClick={() => setType('linear')}
                                className="flex-1 rounded-xl font-bold"
                            >
                                Linear
                            </Button>
                            <Button
                                variant={type === 'quadratic' ? 'default' : 'ghost'}
                                onClick={() => setType('quadratic')}
                                className="flex-1 rounded-xl font-bold"
                            >
                                Quadratic
                            </Button>
                        </div>

                        <div className="space-y-6">
                            <div className="p-8 rounded-[3rem] bg-background border border-primary/5 shadow-xl space-y-4">
                                <p className="text-[10px] font-black uppercase text-primary tracking-widest text-center mb-6">FUNCTION PARAMETERS</p>
                                {type === 'linear' ? (
                                    <>
                                        <ParamInput label="Slope (m)" value={a} onChange={setA} />
                                        <ParamInput label="Y-Intercept (b)" value={b} onChange={setB} />
                                    </>
                                ) : (
                                    <>
                                        <ParamInput label="a (x├é┬▓)" value={a} onChange={setA} />
                                        <ParamInput label="b (x)" value={b} onChange={setB} />
                                        <ParamInput label="c (constant)" value={c} onChange={setC} />
                                    </>
                                )}
                            </div>

                            <div className="flex items-center gap-2 p-4 bg-primary/5 rounded-2xl border border-primary/10">
                                <Settings2 className="h-4 w-4 text-primary" />
                                <span className="text-xs font-bold text-primary italic">
                                    {type === 'linear' ? `y = ${a}x + ${b}` : `y = ${a}x├é┬▓ + ${b}x + ${c}`}
                                </span>
                            </div>
                        </div>
                    </div>

                    {/* Plot */}
                    <div className="flex-1 h-[500px] bg-muted/10 rounded-[3rem] border border-primary/10 p-8 shadow-inner relative">
                        <div className="absolute top-6 left-1/2 -translate-x-1/2 px-4 py-1 rounded-full bg-background/50 backdrop-blur-sm border border-primary/10 text-[10px] font-black uppercase tracking-widest flex items-center gap-2">
                            <Maximize2 className="h-3 w-3" /> Coordinate Plane (-10 to 10)
                        </div>
                        <ResponsiveContainer width="100%" height="100%">
                            <LineChart data={data}>
                                <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--primary)/0.05)" />
                                <XAxis dataKey="x" stroke="hsl(var(--primary)/0.2)" fontSize={10} fontStyle="bold" />
                                <YAxis stroke="hsl(var(--primary)/0.2)" fontSize={10} fontStyle="bold" />
                                <Tooltip
                                    contentStyle={{ backgroundColor: 'hsl(var(--card))', borderRadius: '16px', border: '1px solid hsl(var(--primary)/0.1)', fontWeight: 'bold' }}
                                    itemStyle={{ color: 'hsl(var(--primary))' }}
                                />
                                <Line
                                    type="monotone"
                                    dataKey="y"
                                    stroke="hsl(var(--primary))"
                                    strokeWidth={4}
                                    dot={false}
                                    animationDuration={500}
                                />
                            </LineChart>
                        </ResponsiveContainer>
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

function ParamInput({ label, value, onChange }: any) {
    return (
        <div className="space-y-1">
            <label className="text-[10px] font-black uppercase text-primary/40 pl-2">{label}</label>
            <Input
                type="number"
                value={value}
                onChange={(e) => onChange(e.target.value)}
                className="h-12 rounded-xl bg-muted/10 border-transparent focus:bg-background transition-colors text-center font-bold"
            />
        </div>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Braces, Play, ArrowRight, CheckCircle2, Info } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';

export default function MathStepSolver() {
    const [equation, setEquation] = useState('2x + 5 = 15');
    const [steps, setSteps] = useState<{ step: string, desc: string }[]>([]);
    const [isSolving, setIsSolving] = useState(false);

    const solve = () => {
        setIsSolving(true);
        // Simulated step-by-step logic for 2x + 5 = 15
        const mockSteps = [
            { step: '2x + 5 = 15', desc: 'Original Equation' },
            { step: '2x = 15 - 5', desc: 'Subtract 5 from both sides' },
            { step: '2x = 10', desc: 'Simplify the right side' },
            { step: 'x = 10 / 2', desc: 'Divide both sides by 2' },
            { step: 'x = 5', desc: 'Final Answer' }
        ];

        setTimeout(() => {
            setSteps(mockSteps);
            setIsSolving(false);
        }, 800);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Braces className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Visual Math Step-Solver</CardTitle>
                <p className="text-muted-foreground mt-2">Break down complex algebra into logical, easy-to-follow steps.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-12">
                <div className="flex flex-col md:flex-row gap-6">
                    <Input
                        value={equation}
                        onChange={(e) => setEquation(e.target.value)}
                        placeholder="Enter equation (e.g. 2x + 5 = 15)"
                        className="h-16 rounded-2xl text-2xl font-bold px-8 bg-background border-primary/20 focus:border-primary transition-all flex-1"
                    />
                    <Button onClick={solve} disabled={isSolving} className="h-16 px-12 rounded-2xl text-lg font-black shadow-xl shadow-primary/20">
                        {isSolving ? 'SOLVING...' : 'SOLVE STEPS'}
                    </Button>
                </div>

                <div className="space-y-6">
                    {steps.map((s, i) => (
                        <div key={i} className="flex items-start gap-6 group animate-in slide-in-from-bottom-4 duration-500" style={{ animationDelay: `${i * 100}ms` }}>
                            <div className="flex flex-col items-center">
                                <div className={`w-10 h-10 rounded-full flex items-center justify-center font-black ${i === steps.length - 1 ? 'bg-green-500 text-white' : 'bg-primary/10 text-primary'}`}>
                                    {i === steps.length - 1 ? <CheckCircle2 className="h-6 w-6" /> : i + 1}
                                </div>
                                {i < steps.length - 1 && <div className="w-1 h-12 bg-primary/5 my-2" />}
                            </div>

                            <div className="flex-1 bg-background/60 p-6 rounded-[2rem] border border-primary/5 hover:border-primary/20 transition-all flex justify-between items-center pr-10">
                                <div>
                                    <p className="text-[10px] font-black uppercase text-primary/40 tracking-widest">{s.desc}</p>
                                    <h4 className="text-3xl font-black font-mono mt-1">{s.step}</h4>
                                </div>
                                <ArrowRight className="h-8 w-8 text-primary/10 group-hover:text-primary/40 transition-colors" />
                            </div>
                        </div>
                    ))}
                </div>

                {steps.length === 0 && !isSolving && (
                    <div className="text-center py-20 opacity-20">
                        <Braces className="h-24 w-24 mx-auto mb-4" />
                        <p className="font-bold italic">Enter an equation to see the logical breakdown</p>
                    </div>
                )}

                <div className="p-8 rounded-[2.5rem] bg-amber-500/5 border border-amber-500/10 flex items-start gap-4">
                    <Info className="h-6 w-6 text-amber-500 shrink-0 mt-1" />
                    <div>
                        <p className="text-xs font-black uppercase text-amber-500 mb-2">Math Tutor AI Notes</p>
                        <p className="text-sm text-muted-foreground leading-relaxed">
                            Currently supporting linear equations. Always remember to perform the same operation on <b>both sides</b> of the equals sign to maintain balance.
                        </p>
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Grid, Plus, Minus, X, RotateCcw, Info } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';

export default function MatrixCalculatorPro() {
    const [matrixA, setMatrixA] = useState([[1, 2], [3, 4]]);
    const [matrixB, setMatrixB] = useState([[5, 6], [7, 8]]);
    const [op, setOp] = useState<'add' | 'sub' | 'mult'>('add');

    const updateMatrix = (m: 'A' | 'B', r: number, c: number, val: string) => {
        const v = parseInt(val) || 0;
        if (m === 'A') {
            const next = [...matrixA];
            next[r][c] = v;
            setMatrixA(next);
        } else {
            const next = [...matrixB];
            next[r][c] = v;
            setMatrixB(next);
        }
    };

    const calculate = () => {
        const res = [[0, 0], [0, 0]];
        if (op === 'add') {
            for (let i = 0; i < 2; i++) {
                for (let j = 0; j < 2; j++) {
                    res[i][j] = matrixA[i][j] + matrixB[i][j];
                }
            }
        } else if (op === 'sub') {
            for (let i = 0; i < 2; i++) {
                for (let j = 0; j < 2; j++) {
                    res[i][j] = matrixA[i][j] - matrixB[i][j];
                }
            }
        } else {
            // Multiplication
            res[0][0] = matrixA[0][0] * matrixB[0][0] + matrixA[0][1] * matrixB[1][0];
            res[0][1] = matrixA[0][0] * matrixB[0][1] + matrixA[0][1] * matrixB[1][1];
            res[1][0] = matrixA[1][0] * matrixB[0][0] + matrixA[1][1] * matrixB[1][0];
            res[1][1] = matrixA[1][0] * matrixB[0][1] + matrixA[1][1] * matrixB[1][1];
        }
        return res;
    };

    const result = calculate();

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden font-scholar">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
