export default function ProbabilityPro() {
    const [mode, setMode] = useState<'Normal' | 'Binomial'>('Normal');
    const [mean, setMean] = useState('0');
    const [stdDev, setStdDev] = useState('1');
    const [n, setN] = useState('10');
    const [p, setP] = useState('0.5');

    const generateNormalData = () => {
        const data = [];
        const m = parseFloat(mean) || 0;
        const s = parseFloat(stdDev) || 1;

        for (let x = m - 4 * s; x <= m + 4 * s; x += 0.1 * s) {
            const y = (1 / (s * Math.sqrt(2 * Math.PI))) * Math.exp(-0.5 * Math.pow((x - m) / s, 2));
            data.push({ x: x.toFixed(2), y });
        }
        return data;
    };

    const generateBinomialData = () => {
        const data = [];
        const nv = parseInt(n) || 10;
        const pv = parseFloat(p) || 0.5;

        const factorial = (num: number): number => (num <= 1 ? 1 : num * factorial(num - 1));
        const nCr = (n: number, r: number) => factorial(n) / (factorial(r) * factorial(n - r));

        for (let k = 0; k <= nv; k++) {
            const prob = nCr(nv, k) * Math.pow(pv, k) * Math.pow(1 - pv, nv - k);
            data.push({ k, p: prob });
        }
        return data;
    };

    const normalData = generateNormalData();
    const binomialData = generateBinomialData();

    return (
        <Card className="max-w-6xl mx-auto border-primary/10 shadow-2xl bg-card/50 backdrop-blur-sm overflow-hidden font-scholar">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <BarChart3 className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Probability Pro</CardTitle>
                <p className="text-muted-foreground mt-2">Professional statistical engine for Normal and Binomial distribution analysis.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="flex flex-col lg:flex-row gap-12">
                    {/* Controls */}
                    <div className="w-full lg:w-80 space-y-8 animate-in slide-in-from-left-4">
                        <div className="bg-muted/20 p-2 rounded-2xl border border-primary/5 flex gap-2">
                            <Button
                                variant={mode === 'Normal' ? 'default' : 'ghost'}
                                onClick={() => setMode('Normal')}
                                className="flex-1 rounded-xl font-bold"
                            >
                                Normal
                            </Button>
                            <Button
                                variant={mode === 'Binomial' ? 'default' : 'ghost'}
                                onClick={() => setMode('Binomial')}
                                className="flex-1 rounded-xl font-bold"
                            >
                                Binomial
                            </Button>
                        </div>

                        <div className="p-8 rounded-[3rem] bg-background border border-primary/5 shadow-xl space-y-6">
                            {mode === 'Normal' ? (
                                <>
                                    <ParamInput label="Mean (├Ä┬╝)" value={mean} onChange={setMean} />
                                    <ParamInput label="Std Dev (├ÅãÆ)" value={stdDev} onChange={setStdDev} />
                                </>
                            ) : (
                                <>
                                    <ParamInput label="Trials (n)" value={n} onChange={setN} />
                                    <ParamInput label="Prob (p)" value={p} onChange={setP} />
                                </>
                            )}
                        </div>

                        <div className="p-6 rounded-2xl bg-primary/5 border border-primary/10">
                            <h5 className="text-[10px] font-black uppercase text-primary mb-2 flex items-center gap-2">
                                <TrendingUp className="h-3 w-3" /> STAT NOTE
                            </h5>
                            <p className="text-[10px] text-muted-foreground leading-relaxed">
                                {mode === 'Normal'
                                    ? '68% of data falls within 1├ÅãÆ, 95% within 2├ÅãÆ, and 99.7% within 3├ÅãÆ.'
                                    : 'Binomial distribution models the number of successes in n independent Bernoulli trials.'}
                            </p>
                        </div>
                    </div>

                    {/* Chart Area */}
                    <div className="flex-1 h-[500px] bg-muted/10 rounded-[3rem] border border-primary/10 p-8 relative">
                        <div className="absolute top-6 right-8 px-4 py-1 rounded-full bg-background/50 backdrop-blur-sm border border-primary/10 text-[10px] font-black uppercase tracking-widest flex items-center gap-2 z-10">
                            <Maximize2 className="h-3 w-3" /> Interactive PDF
                        </div>

                        <ResponsiveContainer width="100%" height="100%">
                            {mode === 'Normal' ? (
                                <AreaChart data={normalData}>
                                    <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--primary)/0.05)" />
                                    <XAxis dataKey="x" stroke="hsl(var(--primary)/0.2)" fontSize={10} fontStyle="bold" />
                                    <YAxis stroke="hsl(var(--primary)/0.2)" fontSize={10} fontStyle="bold" />
                                    <Tooltip
                                        contentStyle={{ backgroundColor: 'hsl(var(--card))', borderRadius: '16px', border: '1px solid hsl(var(--primary)/0.1)', fontWeight: 'bold' }}
                                        itemStyle={{ color: 'hsl(var(--primary))' }}
                                    />
                                    <Area
                                        type="monotone"
                                        dataKey="y"
                                        stroke="hsl(var(--primary))"
                                        fill="hsl(var(--primary))"
                                        fillOpacity={0.1}
                                        strokeWidth={4}
                                        animationDuration={800}
                                    />
                                </AreaChart>
                            ) : (
                                <BarChart data={binomialData}>
                                    <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--primary)/0.05)" />
                                    <XAxis dataKey="k" stroke="hsl(var(--primary)/0.2)" fontSize={10} fontStyle="bold" />
                                    <YAxis stroke="hsl(var(--primary)/0.2)" fontSize={10} fontStyle="bold" />
                                    <Tooltip
                                        contentStyle={{ backgroundColor: 'hsl(var(--card))', borderRadius: '16px', border: '1px solid hsl(var(--primary)/0.1)', fontWeight: 'bold' }}
                                        itemStyle={{ color: 'hsl(var(--primary))' }}
                                    />
                                    <Bar
                                        dataKey="p"
                                        fill="hsl(var(--primary))"
                                        radius={[10, 10, 0, 0]}
                                        animationDuration={800}
                                    />
                                </BarChart>
                            )}
                        </ResponsiveContainer>
                    </div>
                </div>

                <div className="p-10 rounded-[4rem] bg-indigo-500/5 border border-indigo-500/10 flex items-start gap-4">
                    <Info className="h-6 w-6 text-indigo-500 shrink-0 mt-1" />
                    <div className="text-xs text-muted-foreground leading-relaxed italic">
                        The Central Limit Theorem states that as the sample size increases, the distribution of the sample mean approaches a normal distribution, regardless of the population distribution shape.
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

function ParamInput({ label, value, onChange }: any) {
    return (
        <div className="space-y-2">
            <label className="text-[10px] font-black uppercase text-primary/40 pl-2">{label}</label>
            <Input
                type="number"
                value={value}
                onChange={(e) => onChange(e.target.value)}
                className="h-14 rounded-2xl bg-muted/10 border-transparent focus:bg-background transition-colors text-center font-black text-xl"
            />
        </div>
    );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Zap, Play, RotateCcw, Info, Settings2, Target, MoveUpRight } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Slider } from '@/components/ui/slider';

export default function ProjectileSimulator() {
    const [velocity, setVelocity] = useState([50]); // m/s
    const [angle, setAngle] = useState([45]); // degrees
    const [gravity, setGravity] = useState([9.8]); // m/s^2
    const [isSimulating, setIsSimulating] = useState(false);
    const [path, setPath] = useState<{ x: number, y: number }[]>([]);
    const [currentPos, setCurrentPos] = useState({ x: 0, y: 0 });
    const timerRef = useRef<any>(null);

    const reset = () => {
        setIsSimulating(false);
        setPath([]);
        setCurrentPos({ x: 0, y: 0 });
        if (timerRef.current) clearInterval(timerRef.current);
    };

    const runSimulation = () => {
        reset();
        setIsSimulating(true);
        let t = 0;
        const dt = 0.05;
        const v0 = velocity[0];
        const a = (angle[0] * Math.PI) / 180;
        const g = gravity[0];

        const vx = v0 * Math.cos(a);
        const vy0 = v0 * Math.sin(a);

        const newPath: { x: number, y: number }[] = [];

        timerRef.current = setInterval(() => {
            t += dt;
            const x = vx * t;
            const y = vy0 * t - 0.5 * g * t * t;

            if (y < 0) {
                clearInterval(timerRef.current);
                setIsSimulating(false);
                return;
            }

            setCurrentPos({ x, y });
            newPath.push({ x, y });
            setPath([...newPath]);
        }, 20);
    };

    const stats = {
        maxHeight: (Math.pow(velocity[0] * Math.sin((angle[0] * Math.PI) / 180), 2) / (2 * gravity[0])).toFixed(2),
        range: ((Math.pow(velocity[0], 2) * Math.sin((2 * angle[0] * Math.PI) / 180)) / gravity[0]).toFixed(2),
        timeOfFlight: ((2 * velocity[0] * Math.sin((angle[0] * Math.PI) / 180)) / gravity[0]).toFixed(2),
    };

    return (
        <Card className="max-w-6xl mx-auto border-primary/10 shadow-2xl bg-card/50 backdrop-blur-sm overflow-hidden font-scholar">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Target className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Projectile Simulator</CardTitle>
                <p className="text-muted-foreground mt-2">Visually analyze 2D motion with real-time kinematic derivations and trajectory mapping.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="flex flex-col lg:flex-row gap-12">
                    {/* Controls Panel */}
                    <div className="w-full lg:w-80 space-y-8 animate-in slide-in-from-left-4">
                        <div className="space-y-6 bg-muted/20 p-8 rounded-[3rem] border border-primary/5 shadow-inner">
                            <SimControl label="Initial Velocity" value={velocity[0]} unit="m/s" min={1} max={100} onValueChange={setVelocity} />
                            <SimControl label="Launch Angle" value={angle[0]} unit="├é┬░" min={0} max={90} onValueChange={setAngle} />
                            <SimControl label="Gravity" value={gravity[0]} unit="m/s├é┬▓" min={1} max={25} step={0.1} onValueChange={setGravity} />
                        </div>

                        <div className="grid grid-cols-1 gap-4">
                            <Button onClick={runSimulation} disabled={isSimulating} className="h-16 rounded-2xl bg-primary shadow-xl shadow-primary/20 font-black text-lg">
                                <Play className="mr-2" /> LAUNCH PROJECTILE
                            </Button>
                            <Button variant="outline" onClick={reset} className="h-16 rounded-2xl border-primary/10 font-black">
                                <RotateCcw className="mr-2" /> RESET STAGE
                            </Button>
                        </div>
                    </div>

                    {/* Simulation Stage */}
                    <div className="flex-1 space-y-8">
                        <div className="relative h-[500px] w-full bg-slate-950 rounded-[4rem] border-4 border-primary/20 shadow-2xl overflow-hidden group">
                            <div className="absolute inset-0 bg-[url('https://www.transparenttextures.com/patterns/carbon-fibre.png')] opacity-20 pointer-events-none" />

                            {/* Static Grid */}
                            <svg className="absolute inset-0 w-full h-full opacity-10">
                                <defs>
                                    <pattern id="grid" width="40" height="40" patternUnits="userSpaceOnUse">
                                        <path d="M 40 0 L 0 0 0 40" fill="none" stroke="white" strokeWidth="0.5" />
                                    </pattern>
                                </defs>
                                <rect width="100%" height="100%" fill="url(#grid)" />
                            </svg>

                            {/* Trajectory */}
                            <svg className="absolute inset-0 w-full h-full" viewBox="0 0 1000 500" preserveAspectRatio="none">
                                <path
                                    d={`M 50,450 ${path.map(p => `L ${50 + p.x * 2},${450 - p.y * 2}`).join(' ')}`}
                                    fill="none"
                                    stroke="hsl(var(--primary))"
                                    strokeWidth="4"
                                    strokeLinecap="round"
                                    className="drop-shadow-[0_0_10px_rgba(var(--primary-rgb),0.5)]"
                                />

                                {/* Current Projectile */}
                                {isSimulating && (
                                    <circle
                                        cx={50 + currentPos.x * 2}
                                        cy={450 - currentPos.y * 2}
                                        r="8"
                                        fill="white"
                                        className="shadow-xl"
                                    >
                                        <animate attributeName="r" values="8;10;8" dur="0.5s" repeatCount="indefinite" />
                                    </circle>
                                )}

                                <line x1="0" y1="450" x2="1000" y2="450" stroke="white" strokeWidth="2" strokeOpacity="0.2" />
                            </svg>

                            <div className="absolute bottom-8 left-12 flex items-center gap-4">
                                <div className="px-4 py-2 rounded-xl bg-primary/20 backdrop-blur-md border border-primary/20 text-white text-[10px] font-black uppercase tracking-widest">
                                    X: {currentPos.x.toFixed(1)}m | Y: {currentPos.y.toFixed(1)}m
                                </div>
                            </div>
                        </div>

                        {/* Stats Readout */}
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                            <StatBox label="Max Altitude" value={stats.maxHeight} unit="m" />
                            <StatBox label="Total Range" value={stats.range} unit="m" />
                            <StatBox label="Time of Flight" value={stats.timeOfFlight} unit="s" />
                        </div>
                    </div>
                </div>

                <div className="p-10 rounded-[4rem] bg-muted/10 border border-primary/5 flex items-start gap-6">
                    <div className="p-4 bg-primary/10 rounded-2xl text-primary">
                        <MoveUpRight className="h-6 w-6" />
                    </div>
                    <div>
                        <h4 className="font-black text-primary uppercase text-[10px] tracking-widest mb-2">Kinematic Context</h4>
                        <p className="text-sm text-muted-foreground leading-relaxed italic">
                            Vertical motion is independent of horizontal motion. In this simulator, horizontal velocity ($v_x$) remains constant while vertical velocity ($v_y$) is governed by the acceleration of gravity ($g$).
                        </p>
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

function SimControl({ label, value, unit, min, max, step = 1, onValueChange }: any) {
    return (
        <div className="space-y-4">
            <div className="flex justify-between items-center px-1">
                <span className="text-[10px] font-black uppercase text-primary/40 tracking-widest">{label}</span>
                <span className="text-lg font-black text-primary">{value}{unit}</span>
            </div>
            <Slider value={[value]} onValueChange={onValueChange} min={min} max={max} step={step} className="py-2" />
        </div>
    );
}

function StatBox({ label, value, unit }: any) {
    return (
        <div className="p-8 rounded-[2.5rem] bg-background border border-primary/5 shadow-xl flex flex-col items-center gap-1 hover:border-primary/20 transition-all group">
            <span className="text-[8px] font-black uppercase text-primary/40 tracking-widest">{label}</span>
            <h4 className="text-4xl font-black text-primary group-hover:scale-110 transition-transform">{value}<span className="text-sm ml-1 opacity-40 font-bold">{unit}</span></h4>
        </div>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Activity, Beaker, Info, RefreshCw, Calculator, TrendingDown, Clock } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

export default function ReactionKineticsPro() {
    const [order, setOrder] = useState<'0' | '1' | '2'>('1');
    const [initialConc, setInitialConc] = useState('1.0');
    const [rateConstant, setRateConstant] = useState('0.05');

    const generateDecayData = () => {
        const data = [];
        const A0 = parseFloat(initialConc) || 1;
        const k = parseFloat(rateConstant) || 0.05;
        const ord = parseInt(order);

        for (let t = 0; t <= 100; t += 5) {
            let A = 0;
            if (ord === 0) {
                A = Math.max(0, A0 - k * t);
            } else if (ord === 1) {
                // A = A0 * e^(-kt)
                A = A0 * Math.exp(-k * t);
            } else if (ord === 2) {
                // 1/A = 1/A0 + kt => A = 1 / (1/A0 + kt)
                A = 1 / (1 / A0 + k * t);
            }
            data.push({ t, conc: A.toFixed(4) });
        }
        return data;
    };

    const calculateHalfLife = () => {
        const A0 = parseFloat(initialConc) || 1;
        const k = parseFloat(rateConstant) || 0.05;
        if (k === 0) return '├ó╦å┼¥';

        if (order === '0') return (A0 / (2 * k)).toFixed(2);
        if (order === '1') return (Math.log(2) / k).toFixed(2);
        if (order === '2') return (1 / (k * A0)).toFixed(2);
        return '0';
    };

    const data = generateDecayData();
    const halfLife = calculateHalfLife();

    return (
        <Card className="max-w-6xl mx-auto border-primary/10 shadow-2xl bg-card/50 backdrop-blur-sm overflow-hidden font-scholar">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Activity className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Reaction Kinetics Pro</CardTitle>
                <p className="text-muted-foreground mt-2">Analyze chemical decay patterns and calculate reaction rates with integrated order modeling.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="flex flex-col lg:flex-row gap-12">
                    {/* Controls */}
                    <div className="w-full lg:w-80 space-y-8 animate-in slide-in-from-left-4">
                        <div className="space-y-6 bg-muted/20 p-8 rounded-[3rem] border border-primary/5">
                            <div className="space-y-2">
                                <label className="text-[10px] font-black uppercase text-primary/40 tracking-widest pl-2">Reaction Order</label>
                                <Select value={order} onValueChange={(v: any) => setOrder(v)}>
                                    <SelectTrigger className="h-12 rounded-xl bg-background border-primary/10 font-bold">
                                        <SelectValue />
                                    </SelectTrigger>
                                    <SelectContent>
                                        <SelectItem value="0">Zero Order</SelectItem>
                                        <SelectItem value="1">First Order</SelectItem>
                                        <SelectItem value="2">Second Order</SelectItem>
                                    </SelectContent>
                                </Select>
                            </div>

                            <KinInput label="Init. Concentration [M]" value={initialConc} onChange={setInitialConc} />
                            <KinInput label="Rate Constant (k)" value={rateConstant} onChange={setRateConstant} />
                        </div>

                        <div className="p-10 rounded-[3rem] bg-primary border-4 border-primary shadow-3xl shadow-primary/30 text-white flex flex-col items-center justify-center gap-2">
                            <p className="text-[10px] font-black uppercase text-white/50 tracking-[0.4em]">CALCULATED T├óÔÇÜ/├óÔÇÜÔÇÜ</p>
                            <h3 className="text-5xl font-black">{halfLife}<span className="text-sm ml-1 opacity-50">Units</span></h3>
                            <Clock className="h-5 w-5 opacity-40 mt-2" />
                        </div>
                    </div>

                    {/* Plot Area */}
                    <div className="flex-1 h-[500px] bg-muted/10 rounded-[4rem] border border-primary/10 p-10 relative">
                        <div className="absolute top-8 left-12 flex items-center gap-3">
                            <div className="w-4 h-4 rounded-full bg-primary animate-pulse" />
                            <span className="text-[10px] font-black uppercase tracking-widest text-primary/60">Concentration vs Time Decay</span>
                        </div>
                        <ResponsiveContainer width="100%" height="100%">
                            <AreaChart data={data}>
                                <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--primary)/0.05)" />
                                <XAxis dataKey="t" stroke="hsl(var(--primary)/20%)" fontSize={10} fontStyle="bold" label={{ value: 'Time (s)', position: 'insideBottom', offset: -10, fontSize: 10, fill: 'currentColor', opacity: 0.5 }} />
                                <YAxis stroke="hsl(var(--primary)/20%)" fontSize={10} fontStyle="bold" label={{ value: 'Conc [M]', angle: -90, position: 'insideLeft', fontSize: 10, fill: 'currentColor', opacity: 0.5 }} />
                                <Tooltip
                                    contentStyle={{ backgroundColor: 'hsl(var(--card))', borderRadius: '16px', border: '1px solid hsl(var(--primary)/0.1)', fontWeight: 'bold' }}
                                    itemStyle={{ color: 'hsl(var(--primary))' }}
                                />
                                <Area
                                    type="monotone"
                                    dataKey="conc"
                                    stroke="hsl(var(--primary))"
                                    fill="hsl(var(--primary))"
                                    fillOpacity={0.1}
                                    strokeWidth={4}
                                    animationDuration={1000}
                                />
                            </AreaChart>
                        </ResponsiveContainer>
                    </div>
                </div>

                <div className="p-10 rounded-[3.5rem] bg-background border border-primary/5 grid grid-cols-1 md:grid-cols-2 gap-8">
                    <div className="flex items-start gap-4">
                        <div className="p-3 bg-primary/10 rounded-2xl text-primary"><Beaker className="h-5 w-5" /></div>
                        <div>
                            <p className="text-xs font-black uppercase text-primary/40 mb-1">Integrated Rate Law</p>
                            <p className="text-sm font-bold opacity-70 italic text-primary">
                                {order === '0' ? '[A] = -kt + [A]├óÔÇÜÔé¼' : order === '1' ? 'ln[A] = -kt + ln[A]├óÔÇÜÔé¼' : '1/[A] = kt + 1/[A]├óÔÇÜÔé¼'}
                            </p>
                        </div>
                    </div>
                    <div className="flex items-start gap-4 border-l border-primary/5 pl-8">
                        <div className="p-3 bg-primary/10 rounded-2xl text-primary"><Info className="h-5 w-5" /></div>
                        <p className="text-xs text-muted-foreground leading-relaxed italic">
                            Half-life depends on initial concentration for zero and second-order reactions, but is constant for first-order reactions.
                        </p>
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

function KinInput({ label, value, onChange }: any) {
    return (
        <div className="space-y-2">
            <label className="text-[10px] font-black uppercase text-primary/40 pl-2">{label}</label>
            <Input
                type="number"
                value={value}
                onChange={(e) => onChange(e.target.value)}
                className="h-12 rounded-xl bg-background border-primary/10 font-bold"
            />
        </div>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { RefreshCw, GraduationCap, ArrowLeftRight, CheckCircle2, Info } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';

export default function SATACTConverter() {
    const [score, setScore] = useState('1400');
    const [mode, setMode] = useState<'SATtoACT' | 'ACTtoSAT'>('SATtoACT');

    const convert = () => {
        const s = parseInt(score) || 0;
        if (mode === 'SATtoACT') {
            // Very simplified concordance logic
            if (s >= 1570) return '36';
            if (s >= 1530) return '35';
            if (s >= 1490) return '34';
            if (s >= 1450) return '33';
            if (s >= 1420) return '32';
            if (s >= 1390) return '31';
            return Math.round((s / 1600) * 36).toString();
        } else {
            if (s >= 36) return '1570-1600';
            if (s >= 35) return '1530-1560';
            if (s >= 34) return '1490-1520';
