        </div>
    );
}

'use client';

import { useState, useMemo } from 'react';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Landmark, Receipt, Percent, CircleDollarSign } from 'lucide-react';

export default function SalaryTaxCalc() {
    const [salary, setSalary] = useState('60000');
    const [state, setState] = useState('TX'); // US-based simplified example

    const taxData = useMemo(() => {
        const income = parseFloat(salary) || 0;

        // Very simplified marginal tax brackets (US-like for demo)
        let federalTax = 0;
        if (income > 100000) {
            federalTax = 16000 + (income - 100000) * 0.24;
        } else if (income > 50000) {
            federalTax = 6000 + (income - 50000) * 0.22;
        } else {
            federalTax = income * 0.12;
        }

        const stateTax = state === 'NY' || state === 'CA' ? income * 0.05 : 0;
        const takeHome = income - federalTax - stateTax;

        return {
            federal: federalTax,
            state: stateTax,
            takeHome: takeHome,
            monthly: takeHome / 12
        };
    }, [salary, state]);

    const formatter = new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: 'USD',
    });

    return (
        <div className="space-y-12">
            <div className="grid gap-12 lg:grid-cols-5">
                <div className="lg:col-span-2 space-y-8">
                    <div className="space-y-4">
                        <Label className="text-lg font-bold flex items-center gap-2">
                            <CircleDollarSign className="h-5 w-5 text-primary" />
                            Annual Gross Salary
                        </Label>
                        <div className="relative">
                            <span className="absolute left-4 top-1/2 -translate-y-1/2 text-muted-foreground font-bold">$</span>
                            <Input
                                type="number"
                                value={salary}
                                onChange={(e) => setSalary(e.target.value)}
                                className="pl-8 h-14 rounded-2xl text-xl font-bold"
                            />
                        </div>
                    </div>

                    <div className="space-y-4">
                        <Label className="text-lg font-bold flex items-center gap-2">
                            <Landmark className="h-5 w-5 text-primary" />
                            Location (Simplified)
                        </Label>
                        <div className="grid grid-cols-3 gap-2">
                            {['TX (No Tax)', 'NY (5%)', 'CA (5%)'].map((loc) => (
                                <Button
                                    key={loc}
                                    variant={state === loc.split(' ')[0] ? 'default' : 'outline'}
                                    className="rounded-xl h-12 text-xs"
                                    onClick={() => setState(loc.split(' ')[0])}
                                >
                                    {loc}
                                </Button>
                            ))}
                        </div>
                    </div>
                </div>

                <div className="lg:col-span-3 space-y-6">
                    <div className="p-8 rounded-[40px] bg-muted/20 border shadow-inner space-y-8">
                        <div className="text-center space-y-1">
                            <p className="text-sm font-bold text-muted-foreground uppercase tracking-[0.2em]">Monthly Take-Home</p>
                            <p className="text-6xl font-black text-primary tracking-tighter tabular-nums">
                                {formatter.format(taxData.monthly)}
                            </p>
                        </div>

                        <div className="grid gap-4 pt-8 border-t border-primary/10">
                            <TaxRow label="Annual Gross" value={formatter.format(parseFloat(salary))} />
                            <TaxRow label="Federal Tax Est." value={`- ${formatter.format(taxData.federal)}`} color="text-destructive" />
                            <TaxRow label="State Tax Est." value={`- ${formatter.format(taxData.state)}`} color="text-destructive" />
                            <TaxRow label="Total Annual Net" value={formatter.format(taxData.takeHome)} bold />
                        </div>
                    </div>

                    <div className="p-6 rounded-2xl bg-primary/5 border border-primary/10 flex items-start gap-4">
                        <div className="h-10 w-10 bg-primary/10 rounded-full flex items-center justify-center shrink-0">
                            <Percent className="h-5 w-5 text-primary" />
                        </div>
                        <p className="text-xs text-muted-foreground leading-relaxed">
                            <strong>Note:</strong> This is a simplified educational tool. Actual taxes depend on filing status, deductions, and local laws. Always consult a tax professional.
                        </p>
                    </div>
                </div>
            </div>
        </div>
    );
}

function TaxRow({ label, value, color, bold }: { label: string; value: string; color?: string; bold?: boolean }) {
    return (
        <div className={`flex items-center justify-between py-2 ${bold ? 'text-lg font-black border-t-2 pt-4 mt-2' : 'text-sm'}`}>
            <span className="text-muted-foreground">{label}</span>
            <span className={`${color || 'text-foreground'} tabular-nums`}>{value}</span>
        </div>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Scale, RefreshCw, ShoppingCart, Info } from 'lucide-react';
import { Input } from '@/components/ui/input';

export default function UnitPriceCalc() {
    const [p1, setP1] = useState('10');
    const [q1, setQ1] = useState('500');
    const [p2, setP2] = useState('18');
    const [q2, setQ2] = useState('1000');

    const unit1 = (parseFloat(p1) || 0) / (parseFloat(q1) || 1);
    const unit2 = (parseFloat(p2) || 0) / (parseFloat(q2) || 1);

    const is1Better = unit1 < unit2;
    const diff = Math.abs(unit1 - unit2) / Math.max(unit1, unit2) * 100;

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Scale className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Unit Price Comparison</CardTitle>
                <p className="text-muted-foreground mt-2">Find the true "best deal" by comparing cost per gram, liter, or unit.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-12">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                    <ItemInput label="Item A" price={p1} setPrice={setP1} qty={q1} setQty={setQ1} active={is1Better} unitPrice={unit1} />
                    <ItemInput label="Item B" price={p2} setPrice={setP2} qty={q2} setQty={setQ2} active={!is1Better} unitPrice={unit2} />
                </div>

                <div className={`p-10 rounded-[3rem] text-center transition-all ${is1Better ? 'bg-green-500/10 border-2 border-green-500/20' : 'bg-blue-500/10 border-2 border-blue-500/20'
                    }`}>
                    <p className="text-sm font-black uppercase tracking-widest text-muted-foreground mb-2">The Verdict</p>
                    <h3 className="text-3xl font-black">
                        {is1Better ? 'Item A' : 'Item B'} is {diff.toFixed(1)}% cheaper!
                    </h3>
                </div>
            </CardContent>
        </Card>
    );
}

function ItemInput({ label, price, setPrice, qty, setQty, active, unitPrice }: any) {
    return (
        <div className={`p-8 rounded-[2.5rem] border-2 transition-all space-y-6 ${active ? 'border-primary bg-primary/5 shadow-lg' : 'border-primary/5 bg-background'
            }`}>
            <div className="flex justify-between items-center">
                <h4 className="text-lg font-black uppercase tracking-tighter">{label}</h4>
                {active && <span className="text-[10px] font-black bg-primary text-white px-3 py-1 rounded-full">BEST VALUE</span>}
            </div>
            <div className="grid grid-cols-2 gap-4">
                <div className="space-y-1">
                    <label className="text-[10px] font-bold opacity-50">Price ($)</label>
                    <Input value={price} onChange={(e) => setPrice(e.target.value)} className="h-12 rounded-xl font-bold" />
                </div>
                <div className="space-y-1">
                    <label className="text-[10px] font-bold opacity-50">Qty (g/ml/pcs)</label>
                    <Input value={qty} onChange={(e) => setQty(e.target.value)} className="h-12 rounded-xl font-bold" />
                </div>
            </div>
            <div className="pt-4 border-t border-primary/5 text-center">
                <p className="text-[10px] font-black text-primary opacity-50">Cost per Unit</p>
                <p className="text-2xl font-black">${unitPrice.toFixed(4)}</p>
            </div>
        </div>
    );
}

'use client';

import React, { useState, useEffect, useCallback } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Target, Trophy, XCircle } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

export default function AccuracyTrainer() {
    const [score, setScore] = useState(0);
    const [misses, setMisses] = useState(0);
    const [isActive, setIsActive] = useState(false);
    const [target, setTarget] = useState<{ x: number, y: number, id: number } | null>(null);
    const [difficulty, setDifficulty] = useState(1);
    const [timeLeft, setTimeLeft] = useState(30);

    const spawnTarget = useCallback(() => {
        setTarget({
            x: Math.random() * 80 + 10,
            y: Math.random() * 80 + 10,
            id: Date.now()
        });
    }, []);

    useEffect(() => {
        let interval: NodeJS.Timeout;
        if (isActive && timeLeft > 0) {
            interval = setInterval(() => {
                setTimeLeft(prev => {
                    if (prev <= 1) {
                        setIsActive(false);
                        return 0;
                    }
                    return prev - 1;
                });
            }, 1000);
        }
        return () => clearInterval(interval);
    }, [isActive, timeLeft]);

    const startGame = () => {
        setScore(0);
        setMisses(0);
        setTimeLeft(30);
        setIsActive(true);
        spawnTarget();
    };

    const handleTargetClick = (e: React.MouseEvent) => {
        e.stopPropagation();
        if (!isActive) return;
        setScore(prev => prev + 1);
        spawnTarget();
    };

    const handleArenaClick = () => {
        if (!isActive) return;
        setMisses(prev => prev + 1);
    };

    const accuracy = score + misses === 0 ? 100 : (score / (score + misses)) * 100;

    return (
        <div className="space-y-6">
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                <Card><CardContent className="pt-6 px-4">
                    <div className="text-xs text-muted-foreground uppercase">Score</div>
                    <div className="text-2xl font-bold">{score}</div>
                </CardContent></Card>
                <Card><CardContent className="pt-6 px-4">
                    <div className="text-xs text-muted-foreground uppercase">Accuracy</div>
                    <div className="text-2xl font-bold">{accuracy.toFixed(1)}%</div>
                </CardContent></Card>
                <Card><CardContent className="pt-6 px-4">
                    <div className="text-xs text-muted-foreground uppercase">Time</div>
                    <div className="text-2xl font-bold">{timeLeft}s</div>
                </CardContent></Card>
                <Card><CardContent className="pt-6 px-4">
                    <div className="text-xs text-muted-foreground uppercase">High Score</div>
                    <div className="text-2xl font-bold opacity-50">0</div>
                </CardContent></Card>
            </div>

            <div
                className="relative h-[500px] bg-zinc-950 rounded-3xl overflow-hidden border-4 border-muted cursor-crosshair"
                onClick={handleArenaClick}
            >
                {!isActive ? (
                    <div className="absolute inset-0 flex items-center justify-center bg-black/60 backdrop-blur-sm z-20">
                        <div className="text-center space-y-4">
                            <Target className="h-16 w-16 text-primary mx-auto opacity-50" />
                            <h2 className="text-3xl font-black text-white">READY TO TRAIN?</h2>
                            <p className="text-zinc-400">30 seconds to hit as many targets as possible.</p>
                            <Button size="lg" onClick={startGame} className="rounded-full px-12">START TRAINING</Button>
                        </div>
                    </div>
                ) : (
                    <AnimatePresence>
                        {target && (
                            <motion.div
                                key={target.id}
                                initial={{ scale: 0, opacity: 0 }}
                                animate={{ scale: 1, opacity: 1 }}
                                exit={{ scale: 1.5, opacity: 0 }}
                                transition={{ type: 'spring', stiffness: 300, damping: 20 }}
                                className="absolute h-10 w-10 -ml-5 -mt-5 rounded-full bg-primary shadow-[0_0_20px_rgba(var(--primary),0.5)] border-4 border-white flex items-center justify-center"
                                style={{ left: `${target.x}%`, top: `${target.y}%` }}
                                onMouseDown={handleTargetClick}
                            >
                                <div className="h-2 w-2 rounded-full bg-white" />
                            </motion.div>
                        )}
                    </AnimatePresence>
                )}

                {isActive && (
                    <div className="absolute bottom-4 left-4 flex gap-2">
                        {[1, 2, 3].map(i => (
                            <div key={i} className={`h-2 w-8 rounded-full ${difficulty >= i ? 'bg-primary' : 'bg-muted'}`} />
                        ))}
                    </div>
                )}
            </div>

            <div className="bg-muted/30 p-4 rounded-xl text-sm flex items-center gap-3">
                <Trophy className="h-5 w-5 text-yellow-500" />
                <span>Tip: Focus on small, controlled mouse movements rather than speed initially.</span>
            </div>
        </div>
    );
}

