'use client';

import { useState, useEffect, useCallback, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { LineChart, RotateCcw, Target, Trophy, TrendingUp, History, Sparkles } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

interface Session {
    id: number;
    date: string;
    accuracy: number;
    targets: number;
}

export default function AimConsistency() {
    const [sessions, setSessions] = useState<Session[]>([]);
    const [isActive, setIsActive] = useState(false);
    const [isFinished, setIsFinished] = useState(false);
    const [targetsHit, setTargetsHit] = useState(0);
    const [totalClicks, setTotalClicks] = useState(0);
    const [timeLeft, setTimeLeft] = useState(15);
    const [target, setTarget] = useState<{ x: number; y: number; id: number } | null>(null);

    const containerRef = useRef<HTMLDivElement>(null);

    // Load history from localStorage
    useEffect(() => {
        const saved = localStorage.getItem('aim_history');
        if (saved) setSessions(JSON.parse(saved));
    }, []);

    const spawnTarget = useCallback(() => {
        if (!containerRef.current) return;
        const { width, height } = containerRef.current.getBoundingClientRect();
        const x = Math.random() * (width - 40);
        const y = Math.random() * (height - 40);
        setTarget({ x, y, id: Date.now() });
    }, []);

    const startSession = () => {
        setIsActive(true);
        setIsFinished(false);
        setTargetsHit(0);
        setTotalClicks(0);
        setTimeLeft(15);
        spawnTarget();
    };

    const endSession = useCallback(() => {
        setIsActive(false);
        setIsFinished(true);
        setTarget(null);

        const accuracy = totalClicks > 0 ? (targetsHit / totalClicks) * 100 : 0;
        const newSession: Session = {
            id: Date.now(),
            date: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
            accuracy: Math.round(accuracy),
            targets: targetsHit
        };

        setSessions(prev => {
            const next = [newSession, ...prev.slice(0, 9)];
            localStorage.setItem('aim_history', JSON.stringify(next));
            return next;
        });
    }, [targetsHit, totalClicks]);

    useEffect(() => {
        if (isActive && timeLeft > 0) {
            const timer = setInterval(() => {
                setTimeLeft(prev => {
                    if (prev <= 1) {
                        endSession();
                        return 0;
                    }
                    return prev - 1;
                });
            }, 1000);
            return () => clearInterval(timer);
        }
    }, [isActive, timeLeft, endSession]);

    const handleTargetClick = (e: React.MouseEvent) => {
        e.stopPropagation();
        if (!isActive) return;
        setTargetsHit(prev => prev + 1);
        setTotalClicks(prev => prev + 1);
        spawnTarget();
    };

    const clearHistory = () => {
        setSessions([]);
        localStorage.removeItem('aim_history');
    };

    const averageAccuracy = sessions.length > 0
        ? sessions.reduce((a, b) => a + b.accuracy, 0) / sessions.length
        : 0;

    return (
        <div className="mx-auto max-w-5xl space-y-8">
            <div className="grid gap-6 md:grid-cols-4">
                {/* Stats Column */}
                <div className="space-y-4">
                    <Card className="border-primary/10 bg-primary/5">
                        <CardContent className="pt-6 text-center">
                            <TrendingUp className="h-6 w-6 text-primary mx-auto mb-1" />
                            <p className="text-[10px] font-bold uppercase tracking-widest text-muted-foreground">Avg accuracy</p>
                            <p className="text-3xl font-black text-primary">{Math.round(averageAccuracy)}%</p>
                        </CardContent>
                    </Card>

                    <Card className="border-primary/10 h-80 overflow-hidden">
                        <CardHeader className="py-3 bg-muted/30 flex-row items-center justify-between">
                            <CardTitle className="text-xs font-bold uppercase">History</CardTitle>
                            <button onClick={clearHistory} className="text-[10px] font-bold text-destructive hover:underline">Clear</button>
                        </CardHeader>
                        <CardContent className="p-0 h-full overflow-y-auto">
                            {sessions.map((s) => (
                                <div key={s.id} className="flex justify-between items-center px-4 py-3 border-b text-xs">
                                    <div className="space-y-0.5">
                                        <p className="text-[10px] text-muted-foreground">{s.date}</p>
                                        <p className="font-bold">{s.targets} Targets</p>
                                    </div>
                                    <div className={`px-2 py-1 rounded font-black ${s.accuracy > 80 ? 'bg-green-500/10 text-green-600' : 'bg-primary/10 text-primary'}`}>
                                        {s.accuracy}%
                                    </div>
                                </div>
                            ))}
                            {sessions.length === 0 && (
                                <div className="h-full flex items-center justify-center text-muted-foreground italic text-xs">No sessions yet</div>
                            )}
                        </CardContent>
                    </Card>

                    <Button size="lg" className="w-full h-14 rounded-2xl shadow-lg shadow-primary/20" onClick={startSession} disabled={isActive}>
                        {isActive ? 'Tracking...' : 'Start Tracker'}
                    </Button>
                </div>

                {/* Game Area */}
                <Card className="md:col-span-3 border-primary/20 bg-muted/20 relative overflow-hidden h-[500px] cursor-crosshair">
                    <div
                        ref={containerRef}
                        className="w-full h-full p-4"
                        onClick={() => isActive && setTotalClicks(prev => prev + 1)}
                    >
                        <AnimatePresence>
                            {isActive && target && (
                                <motion.div
                                    key={target.id}
                                    initial={{ scale: 0, opacity: 0 }}
                                    animate={{ scale: 1, opacity: 1 }}
                                    exit={{ scale: 1.2, opacity: 0 }}
                                    style={{ position: 'absolute', left: target.x, top: target.y }}
                                    className="bg-primary h-10 w-10 rounded-full flex items-center justify-center shadow-lg shadow-primary/40 border-4 border-white/20"
                                    onClick={handleTargetClick}
                                >
                                    <div className="w-2 h-2 bg-white rounded-full" />
                                </motion.div>
                            )}
                        </AnimatePresence>

                        {!isActive && !isFinished && (
                            <div className="flex flex-col items-center justify-center h-full space-y-4">
                                <LineChart className="h-16 w-16 text-primary opacity-20" />
                                <h3 className="text-2xl font-bold">Aim Consistency</h3>
                                <p className="text-muted-foreground text-center max-w-xs">Track how your accuracy changes across sessions to measure improvement.</p>
                            </div>
                        )}

                        {isFinished && (
                            <motion.div
                                initial={{ opacity: 0 }}
                                animate={{ opacity: 1 }}
                                className="flex flex-col items-center justify-center h-full text-center p-8 bg-background/90 backdrop-blur-sm absolute inset-0 z-10"
                            >
                                <div className="h-20 w-20 bg-primary rounded-3xl flex items-center justify-center mb-6 shadow-xl shadow-primary/30">
                                    <Target className="h-10 w-10 text-white" />
                                </div>
                                <h2 className="text-4xl font-black mb-2">Session Ended</h2>
                                <div className="flex gap-12 my-8">
                                    <div className="text-center">
                                        <p className="text-xs font-bold text-muted-foreground uppercase">Accuracy</p>
                                        <p className="text-4xl font-black text-primary">{sessions[0]?.accuracy}%</p>
                                    </div>
                                    <div className="text-center">
                                        <p className="text-xs font-bold text-muted-foreground uppercase">Targets</p>
                                        <p className="text-4xl font-black text-primary">{sessions[0]?.targets}</p>
                                    </div>
                                </div>
                                <Button size="lg" onClick={startSession} className="rounded-full px-12">New Session</Button>
                            </motion.div>
                        )}
                    </div>

                    {/* In-game timer overlay */}
                    {isActive && (
                        <div className="absolute top-4 left-1/2 -translate-x-1/2 flex items-center gap-4 bg-background/50 backdrop-blur-md px-6 py-2 rounded-full border border-white/20 font-black text-2xl">
                            <span className="text-primary">{timeLeft}s</span>
                            <div className="w-px h-6 bg-white/20" />
                            <span>{targetsHit}</span>
                        </div>
                    )}
                </Card>
            </div>

            <div className="rounded-2xl border bg-muted/30 p-8 space-y-4">
                <h3 className="text-xl font-bold flex items-center gap-2">
                    <Sparkles className="h-5 w-5 text-primary" />
                    Consistency is King
                </h3>
                <p className="text-sm text-muted-foreground leading-relaxed">
                    Many players have "good days" and "bad days". The goal of tracking aim consistency is to
                    narrow the gap between your ceiling and your floor. Pro players are defined not by
                    their best shots, but by their high minimum performance.
                </p>
            </div>
        </div>
    );
}

'use client';

import { useState, useEffect, useCallback, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Target, Trophy, Timer, Crosshair, Zap, Activity, Flame, MousePointer2 } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

interface TargetPosition {
    x: number;
    y: number;
    id: number;
    size: number;
}

const DIFFICULTIES = [
    { name: 'Easy', size: 60, time: 30 },
    { name: 'Standard', size: 45, time: 30 },
    { name: 'Hard', size: 30, time: 30 },
    { name: 'Elite', size: 20, time: 30 },
];

export default function AimTrainer() {
    const [isActive, setIsActive] = useState(false);
    const [isFinished, setIsFinished] = useState(false);
    const [targetsHit, setTargetsHit] = useState(0);
    const [totalClicks, setTotalClicks] = useState(0);
    const [timeLeft, setTimeLeft] = useState(30);
    const [target, setTarget] = useState<TargetPosition | null>(null);
    const [score, setScore] = useState(0);
    const [combo, setCombo] = useState(0);
    const [maxCombo, setMaxCombo] = useState(0);
    const [difficulty, setDifficulty] = useState(DIFFICULTIES[1]);

    const containerRef = useRef<HTMLDivElement>(null);
    const timerRef = useRef<NodeJS.Timeout | null>(null);

    const spawnTarget = useCallback(() => {
        if (!containerRef.current) return;
        const { width, height } = containerRef.current.getBoundingClientRect();
        const size = difficulty.size;
        const x = Math.random() * (width - size - 40) + 20;
        const y = Math.random() * (height - size - 40) + 20;
        setTarget({ x, y, id: Date.now(), size });
    }, [difficulty]);

    const startTraining = () => {
        setIsActive(true);
        setIsFinished(false);
        setTargetsHit(0);
        setTotalClicks(0);
        setTimeLeft(difficulty.time);
        setScore(0);
        setCombo(0);
        setMaxCombo(0);
        spawnTarget();
    };

    const endTraining = useCallback(() => {
        setIsActive(false);
        setIsFinished(true);
        setTarget(null);
        if (timerRef.current) clearInterval(timerRef.current);
    }, []);

    useEffect(() => {
        if (isActive && timeLeft > 0) {
            timerRef.current = setInterval(() => {
                setTimeLeft((prev) => {
                    if (prev <= 1) {
                        endTraining();
                        return 0;
                    }
                    return prev - 1;
                });
            }, 1000);
        }
        return () => {
            if (timerRef.current) clearInterval(timerRef.current);
        };
    }, [isActive, timeLeft, endTraining]);

    const handleContainerClick = (e: React.MouseEvent) => {
        if (!isActive) return;
        setTotalClicks(prev => prev + 1);
        setCombo(0); // Break combo on miss (if it's not on the target)
    };

    const handleTargetClick = (e: React.MouseEvent) => {
        e.stopPropagation();
        if (!isActive) return;

        const newHits = targetsHit + 1;
        const newCombo = combo + 1;

        setTargetsHit(newHits);
        setTotalClicks(prev => prev + 1);
        setCombo(newCombo);
        if (newCombo > maxCombo) setMaxCombo(newCombo);

        // Score based on combo and difficulty
        const points = 100 * (1 + (newCombo * 0.1)) * (60 / difficulty.size);
        setScore(prev => Math.floor(prev + points));

        spawnTarget();
    };

    const accuracy = totalClicks > 0 ? (targetsHit / totalClicks) * 100 : 0;

    return (
        <div className="mx-auto max-w-6xl space-y-8">
            <div className="grid gap-6 lg:grid-cols-4">
                {/* Side Stats */}
                <div className="space-y-4">
                    <Card className="border-primary/20 bg-primary/5 shadow-inner">
                        <CardContent className="pt-6 text-center">
                            <Timer className="h-5 w-5 text-primary mx-auto mb-1 opacity-60" />
                            <p className="text-[10px] font-black uppercase tracking-widest text-muted-foreground">Focus Time</p>
                            <p className="text-4xl font-black tabular-nums">{timeLeft}s</p>
                            <div className="w-full h-1 bg-primary/10 mt-3 rounded-full overflow-hidden">
                                <motion.div
                                    className="h-full bg-primary"
                                    initial={{ width: '100%' }}
                                    animate={{ width: `${(timeLeft / difficulty.time) * 100}%` }}
                                />
                            </div>
                        </CardContent>
                    </Card>

                    <div className="grid grid-cols-2 gap-3">
                        <Card className="border-primary/10 group">
                            <CardContent className="pt-6 text-center">
                                <Crosshair className="h-4 w-4 text-primary mx-auto mb-1 group-hover:scale-125 transition-transform" />
                                <p className="text-[9px] font-black uppercase text-muted-foreground">Accuracy</p>
                                <p className="text-2xl font-black">{accuracy.toFixed(0)}%</p>
                            </CardContent>
                        </Card>
                        <Card className="border-primary/10 group">
                            <CardContent className="pt-6 text-center">
                                <Flame className={`h-4 w-4 mx-auto mb-1 transition-transform ${combo > 5 ? 'text-orange-500 scale-125 animate-bounce' : 'text-primary'}`} />
                                <p className="text-[9px] font-black uppercase text-muted-foreground">Combo</p>
                                <p className="text-2xl font-black">{combo}x</p>
                            </CardContent>
                        </Card>
                    </div>

                    <Card className="border-primary/10 bg-muted/20">
                        <CardHeader className="py-3 px-4 flex flex-row items-center justify-between border-b">
                            <CardTitle className="text-[10px] font-black uppercase tracking-widest opacity-60">Session Info</CardTitle>
                            <Activity className="h-3 w-3 opacity-40" />
                        </CardHeader>
                        <CardContent className="p-4 space-y-3">
                            <div className="flex justify-between items-center text-xs">
                                <span className="text-muted-foreground font-medium">Points</span>
                                <span className="font-black text-primary">{score.toLocaleString()}</span>
                            </div>
                            <div className="flex justify-between items-center text-xs">
                                <span className="text-muted-foreground font-medium">Hits</span>
                                <span className="font-black">{targetsHit}</span>
                            </div>
                            <div className="flex justify-between items-center text-xs">
                                <span className="text-muted-foreground font-medium">Max Combo</span>
                                <span className="font-black text-orange-500">{maxCombo}</span>
                            </div>
                        </CardContent>
                    </Card>

                    <div className="space-y-2">
                        <p className="text-[10px] font-black uppercase tracking-widest text-muted-foreground px-1">Difficulty</p>
                        <div className="grid grid-cols-2 gap-2">
                            {DIFFICULTIES.map((d) => (
                                <Button
                                    key={d.name}
                                    variant={difficulty.name === d.name ? 'default' : 'outline'}
                                    size="sm"
                                    className="h-9 rounded-xl text-[10px] font-black"
                                    onClick={() => setDifficulty(d)}
                                    disabled={isActive}
                                >
                                    {d.name}
                                </Button>
                            ))}
                        </div>
                    </div>
                </div>

                {/* Training Area */}
                <Card className="lg:col-span-3 border-2 border-primary/20 bg-zinc-950 shadow-2xl relative overflow-hidden h-[600px] cursor-crosshair rounded-[2.5rem]">
                    <div className="absolute inset-0 opacity-10 pointer-events-none"
                        style={{ backgroundImage: 'radial-gradient(circle at 2px 2px, gray 1px, transparent 0)', backgroundSize: '40px 40px' }} />

                    <div
                        ref={containerRef}
                        className="w-full h-full relative"
                        onClick={handleContainerClick}
                    >
                        <AnimatePresence>
                            {isActive && target && (
                                <motion.div
                                    key={target.id}
                                    initial={{ scale: 0, opacity: 0 }}
                                    animate={{ scale: 1, opacity: 1 }}
                                    exit={{ scale: 1.5, opacity: 0 }}
                                    style={{
                                        position: 'absolute',
                                        left: target.x,
                                        top: target.y,
                                        width: target.size,
                                        height: target.size
                                    }}
                                    className="group"
                                    onClick={handleTargetClick}
                                >
                                    <div className="w-full h-full rounded-full bg-primary flex items-center justify-center shadow-[0_0_30px_rgba(var(--primary),0.5)] border-4 border-white/30 relative">
                                        <div className="w-[60%] h-[60%] rounded-full bg-white/40 border-2 border-white/60" />
                                        <div className="absolute -inset-1 rounded-full border border-primary animate-ping opacity-30" />
                                    </div>
                                    <motion.div
                                        initial={{ width: '100%' }}
                                        animate={{ width: '0%' }}
                                        transition={{ duration: 1.5, ease: 'linear' }}
                                        className="h-1 bg-primary absolute -bottom-3 left-0 rounded-full"
                                        onAnimationComplete={() => isActive && target && spawnTarget()}
                                    />
                                </motion.div>
                            )}
                        </AnimatePresence>

                        {!isActive && !isFinished && (
                            <div className="flex flex-col items-center justify-center h-full space-y-6">
                                <div className="relative">
                                    <div className="absolute inset-0 bg-primary/20 blur-[60px] rounded-full animate-pulse" />
                                    <div className="h-24 w-24 bg-primary rounded-[2rem] flex items-center justify-center relative shadow-2xl">
                                        <Target className="h-12 w-12 text-white" />
                                    </div>
                                </div>
                                <div className="text-center space-y-2">
                                    <h3 className="text-3xl font-black text-white tracking-tight">Arena is Ready</h3>
                                    <p className="text-white/40 max-w-xs mx-auto">Targets will vanish in 1.5s. Accuracy and combos multiply your score.</p>
                                </div>
                                <Button size="lg" onClick={startTraining} className="rounded-2xl h-14 px-12 font-black text-lg shadow-xl shadow-primary/40">
                                    Enter Training
                                </Button>
                            </div>
                        )}

                        {isFinished && (
                            <motion.div
                                initial={{ opacity: 0 }}
                                animate={{ opacity: 1 }}
                                className="absolute inset-0 z-50 flex items-center justify-center bg-zinc-950/90 backdrop-blur-xl p-8"
                            >
                                <motion.div
                                    initial={{ scale: 0.9, y: 20 }}
                                    animate={{ scale: 1, y: 0 }}
                                    className="max-w-md w-full text-center space-y-8"
                                >
                                    <div className="inline-flex h-20 w-20 bg-primary rounded-3xl items-center justify-center shadow-2xl shadow-primary/30 rotate-3">
                                        <Trophy className="h-10 w-10 text-white" />
                                    </div>
                                    <div className="space-y-1">
                                        <p className="text-xs font-black text-primary uppercase tracking-[0.3em]">Evaluation Complete</p>
                                        <h2 className="text-6xl font-black text-white tracking-tighter">GOOD RANGE!</h2>
                                    </div>

                                    <div className="grid grid-cols-3 gap-4 border-y border-white/10 py-8">
                                        <div>
                                            <p className="text-[10px] font-black text-white/40 uppercase mb-1">Score</p>
                                            <p className="text-2xl font-black text-white">{score.toLocaleString()}</p>
                                        </div>
                                        <div>
                                            <p className="text-[10px] font-black text-white/40 uppercase mb-1">Accuracy</p>
                                            <p className="text-2xl font-black text-white">{accuracy.toFixed(1)}%</p>
                                        </div>
                                        <div>
                                            <p className="text-[10px] font-black text-white/40 uppercase mb-1">Max Combo</p>
                                            <p className="text-2xl font-black text-orange-500">{maxCombo}x</p>
                                        </div>
                                    </div>

                                    <div className="flex gap-4">
                                        <Button variant="outline" size="lg" className="flex-1 rounded-2xl h-14 font-black border-2 border-white/20 text-white" onClick={() => setIsFinished(false)}>
                                            Review Area
                                        </Button>
                                        <Button size="lg" className="flex-1 rounded-2xl h-14 font-black shadow-xl" onClick={startTraining}>
                                            Restart
                                        </Button>
                                    </div>
                                </motion.div>
                            </motion.div>
                        )}
                    </div>
                </Card>
            </div>

            {/* Pro Benchmarks */}
            <div className="grid gap-6 md:grid-cols-3">
                <Card className="rounded-3xl border-primary/10 bg-muted/20 p-6 flex flex-col items-center text-center gap-3">
                    <Zap className="h-6 w-6 text-yellow-500" />
                    <h4 className="font-black text-xs uppercase tracking-widest">Low Latency</h4>
                    <p className="text-xs text-muted-foreground">Sub-millisecond input registration for the most competitive practice environment.</p>
                </Card>
                <Card className="rounded-3xl border-primary/10 bg-muted/20 p-6 flex flex-col items-center text-center gap-3">
                    <MousePointer2 className="h-6 w-6 text-primary" />
                    <h4 className="font-black text-xs uppercase tracking-widest">Muscle Memory</h4>
                    <p className="text-xs text-muted-foreground">Consistent target scaling helps you translate mouse movements to real-game reflexes.</p>
                </Card>
                <Card className="rounded-3xl border-primary/10 bg-muted/20 p-6 flex flex-col items-center text-center gap-3">
                    <Trophy className="h-6 w-6 text-orange-500" />
                    <h4 className="font-black text-xs uppercase tracking-widest">Performance Tracking</h4>
                    <p className="text-xs text-muted-foreground">Session-based analysis tools to help you identify and eliminate aiming plateau.</p>
                </Card>
            </div>
        </div>
    );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { EyeOff, Play, History, Trophy, AlertCircle, Activity } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';

const TEXTS = [
    "The quick brown fox jumps over the lazy dog.",
    "Pack my box with five dozen liquor jugs.",
    "Programming is the art of telling another human what one wants the computer to do.",
    "Touch typing is a style of typing where you do not look at the keyboard.",
    "Reliability is the most important feature of any system, technical or otherwise."
];

export default function BlindTypingChallenge() {
    const [state, setState] = useState<'idle' | 'playing' | 'result'>('idle');
    const [target, setTarget] = useState("");
    const [input, setInput] = useState("");
    const [startTime, setStartTime] = useState(0);
    const [wpm, setWpm] = useState(0);
    const [accuracy, setAccuracy] = useState(0);

    const startGame = () => {
        const t = TEXTS[Math.floor(Math.random() * TEXTS.length)];
        setTarget(t);
        setInput("");
        setWpm(0);
        setAccuracy(0);
        setState('playing');
        setStartTime(Date.now());
    };

    const handleInput = (val: string) => {
        setInput(val);
        if (val === target) {
            endGame(val);
        }
    };

    const endGame = (finalInput: string) => {
        const endTime = Date.now();
        const durationMin = (endTime - startTime) / 60000;
        const words = target.length / 5;
        const finalWpm = Math.round(words / durationMin);

        let correct = 0;
        for (let i = 0; i < target.length; i++) {
            if (finalInput[i] === target[i]) correct++;
        }
        const finalAcc = Math.round((correct / target.length) * 100);

        setWpm(finalWpm);
        setAccuracy(finalAcc);
        setState('result');
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <EyeOff className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Blind Typing Challenge</CardTitle>
                <p className="text-muted-foreground mt-2">Test your muscle memory. Can you type without seeing the letters?</p>
            </CardHeader>
            <CardContent className="p-8 space-y-8">
                {state === 'idle' && (
                    <div className="text-center py-12 space-y-6">
                        <p className="text-muted-foreground max-w-sm mx-auto">We will show you a sentence. Memorize it, then type it out as fast as possible. Your input will be hidden!</p>
                        <Button size="lg" className="rounded-2xl h-16 px-12 text-xl font-bold" onClick={startGame}>
                            Start Challenge
                        </Button>
                    </div>
                )}

                {state === 'playing' && (
                    <div className="space-y-8 animate-in fade-in duration-500">
                        <div className="p-8 rounded-3xl bg-primary/5 border border-primary/10 text-center">
                            <p className="text-xs font-black uppercase tracking-tighter text-primary mb-4">Memorize This:</p>
                            <p className="text-2xl font-bold italic">"{target}"</p>
                        </div>

                        <div className="relative">
                            <Input
                                autoFocus
                                value={input}
                                onChange={(e) => handleInput(e.target.value)}
                                className="h-24 text-center rounded-[2rem] bg-muted/20 border-2 border-primary/20 text-transparent caret-primary selection:bg-primary/20"
                                autoComplete="off"
                                autoCorrect="off"
                                spellCheck="false"
                            />
                            <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
                                {input.split('').map((_, i) => (
                                    <div key={i} className="w-3 h-3 bg-primary/40 rounded-full mx-1" />
                                ))}
                                {input.length === 0 && <span className="text-muted-foreground opacity-50 uppercase tracking-[0.3em] font-black">Type blindly now...</span>}
                            </div>
                        </div>

                        <div className="flex justify-center">
                            <Button variant="ghost" onClick={() => setState('idle')}>Reset</Button>
                        </div>
                    </div>
                )}

                {state === 'result' && (
                    <div className="space-y-8 animate-in zoom-in-95">
                        <div className="grid grid-cols-2 gap-4">
                            <div className="p-8 rounded-[2rem] bg-primary/10 border border-primary/10 text-center">
                                <Trophy className="h-8 w-8 text-primary mx-auto mb-2" />
                                <p className="text-4xl font-black">{wpm} WPM</p>
                                <p className="text-[10px] uppercase font-bold text-primary">Typing Speed</p>
                            </div>
                            <div className="p-8 rounded-[2rem] bg-muted/30 border border-primary/5 text-center">
                                <Activity className="h-8 w-8 text-muted-foreground mx-auto mb-2" />
                                <p className="text-4xl font-black">{accuracy}%</p>
                                <p className="text-[10px] uppercase font-bold text-muted-foreground">Accuracy</p>
                            </div>
                        </div>
                        <Button size="lg" className="w-full rounded-2xl h-14" onClick={startGame}>Try New Sentence</Button>
                    </div>
                )}

                <div className="p-4 rounded-xl bg-amber-500/5 border border-amber-500/10 flex gap-3">
                    <AlertCircle className="h-4 w-4 text-amber-500 shrink-0 mt-0.5" />
                    <p className="text-[10px] text-muted-foreground italic">Blind typing builds extreme neural pathways between your fingers and brain, drastically reducing error rates in high-speed environments.</p>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Target, Play, Activity, Music } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { toast } from 'sonner';

export default function BurstTimingTrainer() {
    const [state, setState] = useState<'idle' | 'playing' | 'result'>('idle');
    const [isBeating, setIsBeating] = useState(false);
    const [score, setScore] = useState(0);
    const [total, setTotal] = useState(0);
    const bpm = 120; // Default rhythm for burst resets
    const interval = 60000 / bpm;
    const timerRef = useRef<any>(null);
    const lastClickRef = useRef(0);

    const startGame = () => {
        setState('playing');
        setScore(0);
        setTotal(0);

        timerRef.current = setInterval(() => {
            setIsBeating(true);
            setTimeout(() => setIsBeating(false), 100);
        }, interval);
    };

    const endGame = () => {
        clearInterval(timerRef.current);
        setState('result');
    };

    const handleClick = () => {
        if (state !== 'playing') return;
        const now = Date.now();
        const beatTime = Math.round(now / interval) * interval;
        const diff = Math.abs(now - beatTime);

        setTotal(prev => prev + 1);
        if (diff < 100) { // 100ms window
            setScore(prev => prev + 1);
        }

        if (total >= 29) endGame();
    };

    const accuracy = total > 0 ? Math.round((score / total) * 100) : 0;

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
