'use client';

import React, { useState, useEffect } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { MousePointer2, Target, RotateCcw } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

export default function CircleClick() {
    const [score, setScore] = useState(0);
    const [timeLeft, setTimeLeft] = useState(20);
    const [isActive, setIsActive] = useState(false);
    const [targets, setTargets] = useState<{ id: number, angle: number }[]>([]);
    const radius = 150;

    const spawnTargets = () => {
        const newTargets = [];
        for (let i = 0; i < 8; i++) {
            newTargets.push({ id: Math.random(), angle: (i * 45) * (Math.PI / 180) });
        }
        setTargets(newTargets);
    };

    useEffect(() => {
        let interval: NodeJS.Timeout;
        if (isActive && timeLeft > 0) {
            interval = setInterval(() => {
                setTimeLeft(prev => prev - 1);
            }, 1000);
        } else if (timeLeft === 0) {
            setIsActive(false);
        }
        return () => clearInterval(interval);
    }, [isActive, timeLeft]);

    const start = () => {
        setScore(0);
        setTimeLeft(20);
        setIsActive(true);
        spawnTargets();
    };

    const handleHit = (id: number) => {
        setScore(s => s + 1);
        setTargets(prev => prev.filter(t => t.id !== id));
        if (targets.length === 1) {
            spawnTargets();
        }
    };

    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center bg-muted/30 p-4 rounded-2xl border border-muted">
                <div>
                    <h2 className="text-xl font-black">Circle Flow</h2>
                    <p className="text-xs text-muted-foreground">Pattern-based clicking accuracy</p>
                </div>
                <div className="flex gap-6">
                    <div className="text-center">
                        <div className="text-[10px] font-bold text-muted-foreground uppercase">Hits</div>
                        <div className="text-2xl font-black text-primary">{score}</div>
                    </div>
                    <div className="text-center">
                        <div className="text-[10px] font-bold text-muted-foreground uppercase">Remaining</div>
                        <div className="text-2xl font-black">{timeLeft}s</div>
                    </div>
                </div>
            </div>

            <div className="relative h-[500px] bg-background rounded-3xl border-2 overflow-hidden flex items-center justify-center">
                <div className="absolute w-[300px] h-[300px] border-2 border-dashed border-muted rounded-full opacity-20" />

                {!isActive ? (
                    <div className="z-10 text-center space-y-4">
                        <div className="h-20 w-20 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-4">
                            <MousePointer2 className="h-10 w-10 text-primary" />
                        </div>
                        <Button size="lg" onClick={start} className="rounded-full px-12">BEGIN CHALLENGE</Button>
                    </div>
                ) : (
                    <div className="relative w-full h-full">
                        <AnimatePresence>
                            {targets.map(t => (
                                <motion.button
                                    key={t.id}
                                    initial={{ scale: 0 }}
                                    animate={{ scale: 1 }}
                                    exit={{ scale: 0 }}
                                    className="absolute h-12 w-12 -ml-6 -mt-6 rounded-full bg-card border-4 border-primary shadow-lg flex items-center justify-center group"
                                    style={{
                                        left: `calc(50% + ${Math.cos(t.angle) * radius}px)`,
                                        top: `calc(50% + ${Math.sin(t.angle) * radius}px)`
                                    }}
                                    onClick={() => handleHit(t.id)}
                                >
                                    <div className="h-4 w-4 rounded-full bg-primary group-hover:scale-150 transition-transform" />
                                </motion.button>
                            ))}
                        </AnimatePresence>
                        <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 flex flex-col items-center">
                            <Target className="h-8 w-8 text-muted opacity-20" />
                        </div>
                    </div>
                )}
            </div>

            <Button variant="ghost" className="w-full gap-2" onClick={start} disabled={isActive}>
                <RotateCcw className="h-4 w-4" /> Restart
            </Button>
        </div>
    );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Crosshair, Play, Target, Clock } from 'lucide-react';
import { Button } from '@/components/ui/button';

export default function ClickTimingTrainer() {
    const [state, setState] = useState<'idle' | 'playing' | 'result'>('idle');
    const [score, setScore] = useState(0);
    const [time, setTime] = useState(30);
    const [pos, setPos] = useState(-10);
    const containerRef = useRef<HTMLDivElement>(null);
    const timerRef = useRef<any>(null);
    const speed = 0.5;

    useEffect(() => {
        let req: any;
        if (state === 'playing') {
            const animate = () => {
                setPos(prev => {
                    let next = prev + speed;
                    if (next > 110) return -10;
                    return next;
                });
                req = requestAnimationFrame(animate);
            };
            req = requestAnimationFrame(animate);
        }
        return () => cancelAnimationFrame(req);
    }, [state]);

    const startGame = () => {
        setState('playing');
        setScore(0);
        setTime(30);

        timerRef.current = setInterval(() => {
            setTime(prev => {
                if (prev <= 1) {
                    endGame();
                    return 0;
                }
                return prev - 1;
            });
        }, 1000);
    };

    const endGame = () => {
        clearInterval(timerRef.current);
        setState('result');
    };

    const handleAction = () => {
        if (state !== 'playing') return;
        // Hit box is between 45% and 55%
        if (pos >= 45 && pos <= 55) {
            setScore(s => s + 1);
        }
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Crosshair className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Click Timing Trainer</CardTitle>
                <p className="text-muted-foreground mt-2">Click when the target crosses your center mark.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-6">
                <div className="flex justify-between items-center bg-muted/30 p-4 rounded-2xl border border-primary/5 font-bold">
                    <span>Accurate Hits: {score}</span>
                    <span>Time: {time}s</span>
                </div>

                <div
                    ref={containerRef}
                    className="relative w-full h-[300px] bg-muted/10 rounded-[2.5rem] border-2 border-primary/10 overflow-hidden flex items-center justify-center cursor-pointer"
                    onClick={handleAction}
                >
                    {/* Hit Zone */}
                    <div className="absolute top-0 bottom-0 w-[10%] bg-primary/20 border-x-2 border-primary/30 z-0" />
                    <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-[2px] h-[80%] bg-primary shadow-[0_0_10px_rgba(var(--primary),0.5)] z-0" />

                    {state === 'idle' && (
                        <div className="absolute inset-0 flex flex-col items-center justify-center bg-background/40 backdrop-blur-sm z-20">
                            <Button size="lg" className="rounded-2xl" onClick={startGame}>
                                Start Trainer
                            </Button>
                        </div>
                    )}

                    {state === 'playing' && (
                        <div
                            className="absolute w-16 h-16 bg-primary rounded-2xl flex items-center justify-center shadow-lg transition-transform active:scale-90"
                            style={{ left: `${pos}%`, transform: 'translateX(-50%)' }}
                        >
                            <Target className="text-white h-8 w-8" />
                        </div>
                    )}

                    {state === 'result' && (
                        <div className="absolute inset-0 flex flex-col items-center justify-center bg-background/60 backdrop-blur-md z-20">
                            <h3 className="text-5xl font-black text-primary mb-2">{score}</h3>
                            <p className="text-xl font-bold mb-8">Precision Hits</p>
                            <Button size="lg" className="rounded-2xl" onClick={startGame}>
                                Try Again
                            </Button>
                        </div>
                    )}
                </div>

                <p className="text-center text-xs text-muted-foreground italic">
                    Great for practicing "Hold Angles" and "Reactive Clicking" with Snipers/Shotguns.
                </p>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Code, Play, CheckCircle2, Terminal } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

const CODE_EXAMPLES: Record<string, string> = {
    javascript: `function calculateSum(a, b) {\n  const result = a + b;\n  console.log("Result:", result);\n  return result;\n}`,
    python: `def greet_user(name):\n    message = f"Hello, {name}!"\n    print(message)\n    return True`,
    css: `.card-container {\n  display: flex;\n  justify-content: center;\n  border-radius: 12px;\n  padding: 2rem;\n}`,
};

export default function CodeTypingSpeed() {
    const [lang, setLang] = useState('javascript');
    const [state, setState] = useState<'idle' | 'playing' | 'result'>('idle');
    const [input, setInput] = useState("");
    const [startTime, setStartTime] = useState(0);
    const [wpm, setWpm] = useState(0);

    const target = CODE_EXAMPLES[lang];

    const startGame = () => {
        setInput("");
        setWpm(0);
        setState('playing');
        setStartTime(Date.now());
    };

    const handleInput = (val: string) => {
        setInput(val);
        if (val.trim() === target.trim()) {
            const duration = (Date.now() - startTime) / 60000;
            const finalWpm = Math.round((target.length / 5) / duration);
            setWpm(finalWpm);
            setState('result');
        }
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Code className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Code Snippet Speed Test</CardTitle>
                <p className="text-muted-foreground mt-2">Practice typing brackets, semicolons, and indentation for better dev speed.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-8">
                <div className="flex justify-between items-center bg-muted/30 p-4 rounded-2xl border border-primary/5">
                    <div className="flex items-center gap-2">
                        <Terminal className="h-4 w-4 text-primary" />
                        <span className="text-sm font-bold uppercase tracking-widest text-primary">Language Selection</span>
                    </div>
                    <Select value={lang} onValueChange={setLang} disabled={state === 'playing'}>
                        <SelectTrigger className="w-[180px] h-10 rounded-xl">
                            <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                            <SelectItem value="javascript">JavaScript</SelectItem>
                            <SelectItem value="python">Python</SelectItem>
                            <SelectItem value="css">CSS</SelectItem>
                        </SelectContent>
                    </Select>
                </div>

                <div className="relative p-8 bg-black/80 rounded-[2rem] border border-white/10 font-mono text-sm leading-relaxed overflow-hidden">
                    <pre className="text-white/50">{target}</pre>
                    {state === 'playing' && (
                        <textarea
                            autoFocus
                            value={input}
                            onChange={(e) => handleInput(e.target.value)}
                            className="absolute inset-0 p-8 bg-transparent text-primary outline-none resize-none caret-white"
                            spellCheck="false"
                        />
                    )}
                    {state === 'idle' && (
                        <div className="absolute inset-0 flex flex-col items-center justify-center bg-black/60 backdrop-blur-sm">
                            <Button size="lg" className="rounded-2xl" onClick={startGame}>Start Coding Test</Button>
                        </div>
                    )}
                    {state === 'result' && (
                        <div className="absolute inset-0 flex flex-col items-center justify-center bg-black/80 backdrop-blur-md">
                            <CheckCircle2 className="h-12 w-12 text-green-500 mb-4" />
                            <h3 className="text-5xl font-black text-white">{wpm} WPM</h3>
                            <p className="text-white/60 mb-8 uppercase tracking-widest text-xs font-bold">Code Speed Precision</p>
                            <Button size="lg" className="rounded-2xl" onClick={startGame}>Repeat Challenge</Button>
                        </div>
                    )}
                </div>
            </CardContent>
        </Card>
    );
}

