                    <CheckCircle className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Full Keyboard Health Check</CardTitle>
                <p className="text-muted-foreground mt-2">Interactive map to test every switch on your keyboard. Keys turn green when registered.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-12">
                <div className="flex flex-col gap-3">
                    {LAYOUT.map((row, i) => (
                        <div key={i} className="flex justify-center gap-1.5">
                            {row.map(code => (
                                <div
                                    key={code}
                                    className={`
                                        h-12 flex items-center justify-center rounded-lg text-[10px] font-bold px-2 border transition-all
                                        ${tested.has(code) ? 'bg-green-500 border-green-400 text-white animate-in zoom-in-75' : 'bg-background/40 border-primary/10 text-muted-foreground'}
                                        ${code === 'Space' ? 'w-64' : code === 'Enter' ? 'w-24' : code === 'ShiftLeft' ? 'w-28' : 'min-w-[48px]'}
                                    `}
                                >
                                    {code.replace('Key', '').replace('Digit', '').replace('Arrow', '')}
                                </div>
                            ))}
                        </div>
                    ))}
                </div>

                <div className="flex justify-between items-center bg-muted/20 p-6 rounded-3xl border border-primary/5">
                    <div className="flex gap-8">
                        <div>
                            <p className="text-[10px] font-black uppercase tracking-widest text-primary">Tested</p>
                            <p className="text-3xl font-black">{tested.size}</p>
                        </div>
                        <div>
                            <p className="text-[10px] font-black uppercase tracking-widest text-muted-foreground">Remaining</p>
                            <p className="text-3xl font-black">{80 - tested.size}</p>
                        </div>
                    </div>
                    <Button variant="outline" className="rounded-xl h-12 px-8 font-bold" onClick={reset}>
                        <RefreshCw className="mr-2 h-4 w-4" /> Reset Layout
                    </Button>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Maximize2, Ruler, User, Monitor } from 'lucide-react';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

export default function GamingDeskHeight() {
    const [userHeight, setUserHeight] = useState('175');
    const [unit, setUnit] = useState('cm');

    const calculateErgonomics = (height: number, u: string) => {
        const h = u === 'in' ? height * 2.54 : height;

        // General ergonomic formulas (approximations)
        return {
            chair: (h * 0.25).toFixed(1),
            desk: (h * 0.41).toFixed(1),
            monitor: (h * 0.70).toFixed(1),
        };
    };

    const val = parseFloat(userHeight) || 0;
    const results = calculateErgonomics(val, unit);

    const convert = (cm: string) => {
        return unit === 'in' ? (parseFloat(cm) / 2.54).toFixed(1) : cm;
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Maximize2 className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Gaming Desk Height</CardTitle>
                <p className="text-muted-foreground mt-2">Optimize your posture for better aim and long-term health.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-10">
                <div className="flex flex-col md:flex-row gap-6 items-end justify-center">
                    <div className="space-y-2 flex-1 max-w-[200px]">
                        <Label>Your Height</Label>
                        <Input value={userHeight} onChange={(e) => setUserHeight(e.target.value)} type="number" className="h-12 rounded-xl" />
                    </div>
                    <div className="space-y-2 w-[120px]">
                        <Label>Unit</Label>
                        <Select value={unit} onValueChange={setUnit}>
                            <SelectTrigger className="h-12 rounded-xl">
                                <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                                <SelectItem value="cm">cm</SelectItem>
                                <SelectItem value="in">inches</SelectItem>
                            </SelectContent>
                        </Select>
                    </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    <ErgoCard
                        icon={<User className="h-5 w-5" />}
                        label="Seat Height"
                        value={convert(results.chair)}
                        unit={unit}
                        desc="From floor to top of seat"
                    />
                    <ErgoCard
                        icon={<Ruler className="h-5 w-5" />}
                        label="Desk Height"
                        value={convert(results.desk)}
                        unit={unit}
                        desc="From floor to desk surface"
                        primary
                    />
                    <ErgoCard
                        icon={<Monitor className="h-5 w-5" />}
                        label="Monitor Top"
                        value={convert(results.monitor)}
                        unit={unit}
                        desc="Eye level should be here"
                    />
                </div>

                <div className="p-8 rounded-3xl bg-primary/5 border border-primary/10 space-y-4">
                    <h3 className="font-bold text-lg text-primary">Pro Posture Tips:</h3>
                    <ul className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-muted-foreground">
                        <li className="flex gap-2">├óÔé¼┬ó <span>Your elbows should be at a <b>90-100&deg;</b> angle when resting on the desk.</span></li>
                        <li className="flex gap-2">├óÔé¼┬ó <span>Keep your feet flat on the floor; use a footrest if necessary.</span></li>
                        <li className="flex gap-2">├óÔé¼┬ó <span>The monitor should be about an <b>arm&apos;s length</b> away.</span></li>
                        <li className="flex gap-2">├óÔé¼┬ó <span>Lowering your desk slightly can help with <b>arm aiming</b> and range of motion.</span></li>
                    </ul>
                </div>
            </CardContent>
        </Card>
    );
}

function ErgoCard({ icon, label, value, unit, desc, primary = false }: any) {
    return (
        <div className={`p-8 rounded-2xl border flex flex-col items-center text-center space-y-3 transition-all ${primary ? 'bg-primary border-primary text-primary-foreground shadow-xl shadow-primary/20 scale-105' : 'bg-muted/30 border-primary/5'
            }`}>
            <div className={`p-3 rounded-xl ${primary ? 'bg-white/20' : 'bg-primary/10 text-primary'}`}>
                {icon}
            </div>
            <div>
                <p className={`text-[10px] font-black uppercase tracking-widest ${primary ? 'text-white/70' : 'text-primary'}`}>{label}</p>
                <div className="flex items-baseline justify-center gap-1">
                    <span className="text-4xl font-black">{value}</span>
                    <span className="text-sm font-bold opacity-70">{unit}</span>
                </div>
            </div>
            <p className={`text-[10px] leading-tight ${primary ? 'text-white/60' : 'text-muted-foreground'}`}>{desc}</p>
        </div>
    );
}

'use client';

import React, { useState } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Gamepad2, Info, Wind, Target } from 'lucide-react';
import { motion } from 'framer-motion';

export default function GrenadeCalculator() {
    const [angle, setAngle] = useState(45);
    const [power, setPower] = useState(100);

    // Basic physics simulation for visualization
    const points = [];
    const g = 9.8;
    const rad = (angle * Math.PI) / 180;
    const v0 = power * 0.8;
    const vx = v0 * Math.cos(rad);
    const vy = v0 * Math.sin(rad);

    for (let t = 0; t <= 10; t += 0.2) {
        const x = vx * t;
        const y = vy * t - 0.5 * g * t * t;
        if (y < -10) break;
        points.push({ x: 50 + x, y: 350 - y * 3 });
    }

    return (
        <div className="space-y-8 max-w-5xl mx-auto">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
                <div className="md:col-span-2 h-[500px] bg-slate-950 rounded-[3rem] border-8 border-slate-900 shadow-2xl relative overflow-hidden p-12">
                    <div className="absolute inset-x-0 bottom-20 h-[2px] bg-white/5" />
                    <div className="absolute bottom-20 left-20 h-4 w-4 bg-primary rounded-full shadow-[0_0_20px_rgba(var(--primary),0.8)]" />

                    <svg className="w-full h-full overflow-visible relative z-10">
                        <motion.path
                            key={`${angle}-${power}`}
                            initial={{ pathLength: 0 }}
                            animate={{ pathLength: 1 }}
                            transition={{ duration: 1, ease: "easeOut" }}
                            d={`M 20 280 ${points.map(p => `L ${p.x + 20} ${p.y - 50}`).join(' ')}`}
                            fill="none"
                            stroke="hsl(var(--primary))"
                            strokeWidth="4"
                            strokeDasharray="8 8"
                        />
                        {points.length > 0 && (
                            <circle
                                cx={points[points.length - 1].x + 20}
                                cy={points[points.length - 1].y - 50}
                                r="8"
                                fill="white"
                                className="animate-ping opacity-20"
                            />
                        )}
                    </svg>

                    <div className="absolute top-10 right-10 text-right opacity-30">
                        <Wind className="h-10 w-10 text-white mb-2 ml-auto" />
                        <span className="text-[10px] font-black uppercase tracking-widest text-white">Wind Factor: 0.0</span>
                    </div>
                </div>

                <div className="space-y-6">
                    <div className="space-y-1">
                        <h2 className="text-3xl font-black italic">Tactic<span className="text-primary italic">CALC</span></h2>
                        <p className="text-sm text-muted-foreground">Physics-based trajectory analyzer for tactical games.</p>
                    </div>

                    <Card className="rounded-3xl border-none bg-muted/30">
                        <CardContent className="p-8 space-y-8">
                            <div className="space-y-4">
                                <div className="flex justify-between items-center text-xs font-black uppercase tracking-widest">
                                    <span>Launch Angle</span>
                                    <span className="text-primary">{angle}┬░</span>
                                </div>
                                <input type="range" min="0" max="90" value={angle} onChange={(e) => setAngle(parseInt(e.target.value))} className="w-full" />
                            </div>

                            <div className="space-y-4">
                                <div className="flex justify-between items-center text-xs font-black uppercase tracking-widest">
                                    <span>Throw Force</span>
                                    <span className="text-primary">{power}%</span>
                                </div>
                                <input type="range" min="10" max="200" value={power} onChange={(e) => setPower(parseInt(e.target.value))} className="w-full" />
                            </div>
                        </CardContent>
                    </Card>

                    <div className="bg-zinc-950 text-white p-6 rounded-3xl relative overflow-hidden group">
                        <Info className="absolute -right-2 -top-2 h-16 w-16 opacity-5 group-hover:opacity-10 transition-opacity" />
                        <h4 className="font-bold flex items-center gap-2 mb-2">
                            <Target className="h-4 w-4 text-primary" />
                            Tactical Use
                        </h4>
                        <p className="text-[10px] text-zinc-500 leading-relaxed uppercase font-bold tracking-tight">
                            Perfect for learning "nades" in CS2 or Smoke lineups in Valorant. Note: Each game uses custom gravity scales.
                        </p>
                    </div>
                </div>
            </div>
        </div>
    );
}

