'use client';

import { useState, useEffect, useRef, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { MousePointer2, Timer, Zap, Trophy, RotateCcw, LineChart } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

export default function JitterClickTest() {
    const [isActive, setIsActive] = useState(false);
    const [timeLeft, setTimeLeft] = useState(10);
    const [clicks, setClicks] = useState(0);
    const [bestCPS, setBestCPS] = useState(0);
    const [isFinished, setIsFinished] = useState(false);
    const [duration, setDuration] = useState(10);

    const timerRef = useRef<NodeJS.Timeout | null>(null);

    const startTest = useCallback(() => {
        setIsActive(true);
        setClicks(0);
        setTimeLeft(duration);
        setIsFinished(false);
    }, [duration]);

    const handleClick = () => {
        if (!isActive && !isFinished) {
            startTest();
        }
        if (isActive) {
            setClicks((prev) => prev + 1);
        }
    };

    useEffect(() => {
        if (isActive && timeLeft > 0) {
            timerRef.current = setInterval(() => {
                setTimeLeft((prev) => {
                    if (prev <= 0.1) {
                        clearInterval(timerRef.current!);
                        setIsActive(false);
                        setIsFinished(true);
                        return 0;
                    }
                    return parseFloat((prev - 0.1).toFixed(1));
                });
            }, 100);
        }
        return () => {
            if (timerRef.current) clearInterval(timerRef.current);
        };
    }, [isActive, timeLeft]);

    useEffect(() => {
        if (isFinished) {
            const cps = clicks / duration;
            if (cps > bestCPS) setBestCPS(cps);
        }
    }, [isFinished, clicks, duration, bestCPS]);

    const currentCPS = isActive ? (clicks / (duration - timeLeft) || 0) : (clicks / duration || 0);

    return (
        <div className="mx-auto max-w-4xl space-y-8">
            {/* HUD */}
            <div className="grid gap-4 md:grid-cols-3">
                <Card className="bg-primary/5 border-primary/20">
                    <CardContent className="pt-6 text-center">
                        <Timer className="h-5 w-5 text-primary mx-auto mb-1" />
                        <p className="text-[10px] font-bold uppercase text-muted-foreground">Time Left</p>
                        <p className="text-3xl font-black">{timeLeft}s</p>
                    </CardContent>
                </Card>
                <Card className="bg-primary/5 border-primary/20">
                    <CardContent className="pt-6 text-center">
                        <Zap className="h-5 w-5 text-primary mx-auto mb-1" />
                        <p className="text-[10px] font-bold uppercase text-muted-foreground">Current CPS</p>
                        <p className="text-3xl font-black">{currentCPS.toFixed(1)}</p>
                    </CardContent>
                </Card>
                <Card className="bg-primary/5 border-primary/20">
                    <CardContent className="pt-6 text-center">
                        <Trophy className="h-5 w-5 text-yellow-500 mx-auto mb-1" />
                        <p className="text-[10px] font-bold uppercase text-muted-foreground">Best CPS</p>
                        <p className="text-3xl font-black">{bestCPS.toFixed(1)}</p>
                    </CardContent>
                </Card>
            </div>

            {/* Click Area */}
            <Card className="overflow-hidden border-2 border-dashed border-primary/30 bg-muted/20 relative group">
                <div
                    className={cn(
                        "h-[400px] flex flex-col items-center justify-center cursor-pointer select-none transition-all duration-300",
                        isActive ? "bg-primary/5" : "hover:bg-primary/5"
                    )}
                    onClick={handleClick}
                >
                    <AnimatePresence mode="wait">
                        {!isActive && !isFinished && (
                            <motion.div
                                key="start"
                                initial={{ scale: 0.9, opacity: 0 }}
                                animate={{ scale: 1, opacity: 1 }}
                                className="text-center space-y-4"
                            >
                                <div className="h-20 w-20 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-2 group-hover:scale-110 transition-transform">
                                    <MousePointer2 className="h-10 w-10 text-primary" />
                                </div>
                                <div>
                                    <h3 className="text-2xl font-black tracking-tight">JITTER CLICK TEST</h3>
                                    <p className="text-muted-foreground text-sm uppercase font-bold tracking-widest">Click to start the challenge</p>
                                </div>
                            </motion.div>
                        )}

                        {isActive && (
                            <motion.div
                                key="active"
                                initial={{ scale: 0.9, opacity: 0 }}
                                animate={{ scale: 1, opacity: 1 }}
                                className="text-center"
                            >
                                <p className="text-8xl font-black text-primary drop-shadow-2xl">{clicks}</p>
                                <p className="text-sm font-bold uppercase tracking-widest text-muted-foreground">KEEP VIBRATING!</p>
                            </motion.div>
                        )}

                        {isFinished && (
                            <motion.div
                                key="finished"
                                initial={{ y: 20, opacity: 0 }}
                                animate={{ y: 0, opacity: 1 }}
                                className="text-center space-y-6"
                            >
                                <div className="space-y-1">
                                    <p className="text-sm font-bold uppercase text-primary">Test Results</p>
                                    <h3 className="text-6xl font-black tracking-tighter">{currentCPS.toFixed(2)} <span className="text-2xl">CPS</span></h3>
                                    <p className="text-muted-foreground">You clicked {clicks} times in {duration} seconds.</p>
                                </div>
                                <Button size="lg" onClick={() => setIsFinished(false)} className="rounded-xl h-12 px-8 gap-2">
                                    <RotateCcw className="h-4 w-4" /> Try Again
                                </Button>
                            </motion.div>
                        )}
                    </AnimatePresence>
                </div>

                {/* Visual Clicks (Ripple Effect placeholder logic) */}
                {isActive && (
                    <div className="absolute top-4 right-4 text-[10px] font-bold text-primary opacity-50 flex items-center gap-1">
                        <Activity className="h-3 w-3 animate-pulse" /> LIVE ACCURACY TRACKING
                    </div>
                )}
            </Card>

            <div className="grid gap-6 md:grid-cols-2">
                <div className="p-8 rounded-2xl border bg-muted/30 space-y-4">
                    <h3 className="text-xl font-bold flex items-center gap-2">
                        <Zap className="h-5 w-5 text-primary" />
                        What is Jitter Clicking?
                    </h3>
                    <p className="text-sm text-muted-foreground leading-relaxed">
                        Jitter clicking is a technique involving fast arm muscles vibrations to achieve high click rates (10-14 CPS).
                        It is popular among Minecraft PvPers for better knockback and hit registration.
                    </p>
                    <div className="text-[10px] text-destructive font-bold uppercase p-2 border border-destructive/20 rounded-lg bg-destructive/5 leading-relaxed">
                        ÔÜá´©Å Warning: Excessive jitter clicking can lead to strain. Take regular breaks and stop if you feel pain.
                    </div>
                </div>
                <div className="p-8 rounded-2xl border bg-primary/5 space-y-4">
                    <h3 className="text-xl font-bold flex items-center gap-2">
                        <LineChart className="h-5 w-5 text-primary" />
                        Pro Technique Tips
                    </h3>
                    <ul className="space-y-2">
                        {[
                            'Position your hand like a claw.',
                            'Vibrate your hand by tensing your forearm.',
                            'Keep your wrist locked and use your arm muscles.',
                            'Practice in short 5s bursts first.'
                        ].map(tip => (
                            <li key={tip} className="flex gap-2 text-xs text-muted-foreground">
                                <ChevronRight className="h-3 w-3 text-primary shrink-0 transition-transform" />
                                {tip}
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    );
}

function ChevronRight(props: any) {
    return (
        <svg
            {...props}
            xmlns="http://www.w3.org/2000/svg"
            width="24"
            height="24"
            viewBox="0 0 24 24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
        >
            <path d="m9 18 6-6-6-6" />
        </svg>
    );
}

function cn(...inputs: any[]) {
    return inputs.filter(Boolean).join(' ');
}

'use client';

import React, { useState, useEffect } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Keyboard, ShieldAlert, CheckCircle2 } from 'lucide-react';

export default function KeyGhostingTest() {
    const [pressedKeys, setPressedKeys] = useState<Set<string>>(new Set());

    useEffect(() => {
        const handleDown = (e: KeyboardEvent) => {
            setPressedKeys(prev => {
                const next = new Set(prev);
                next.add(e.code);
                return next;
            });
        };
        const handleUp = (e: KeyboardEvent) => {
            setPressedKeys(prev => {
                const next = new Set(prev);
                next.delete(e.code);
                return next;
            });
        };

        window.addEventListener('keydown', handleDown);
        window.addEventListener('keyup', handleUp);
        return () => {
            window.removeEventListener('keydown', handleDown);
            window.removeEventListener('keyup', handleUp);
        };
    }, []);

    return (
        <div className="space-y-8">
            <div className="text-center space-y-2">
                <h2 className="text-3xl font-black">Ghosting Detector</h2>
                <p className="text-muted-foreground">Press multiple keys simultaneously to see if they all register.</p>
            </div>

            <div className="grid grid-cols-2 sm:grid-cols-4 md:grid-cols-6 gap-3">
                {Array.from(pressedKeys).map(key => (
                    <div
                        key={key}
                        className="h-16 flex items-center justify-center bg-primary text-primary-foreground font-mono rounded-xl shadow-lg shadow-primary/20 animate-in zoom-in-90 duration-75"
                    >
                        {key.replace('Key', '')}
                    </div>
                ))}
            </div>

            {pressedKeys.size === 0 && (
                <div className="h-32 border-2 border-dashed rounded-2xl flex items-center justify-center text-muted-foreground italic bg-muted/20">
                    Waiting for key input...
                </div>
            )}

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <Card className="border-primary/20 bg-primary/5">
                    <CardContent className="py-6 flex gap-4 items-center">
                        <CheckCircle2 className="h-8 w-8 text-primary" />
                        <div>
                            <div className="font-bold">Active Keys</div>
                            <div className="text-2xl font-black">{pressedKeys.size}</div>
                        </div>
                    </CardContent>
                </Card>
                <Card className="border-muted bg-muted/30">
                    <CardContent className="py-6 flex gap-4 items-center">
                        <ShieldAlert className="h-8 w-8 text-muted-foreground" />
                        <div>
                            <div className="font-bold">Ghosting Info</div>
                            <p className="text-xs text-muted-foreground">Average membrane keyboards ghost after 3-6 keys.</p>
                        </div>
                    </CardContent>
                </Card>
            </div>

            <div className="bg-muted/30 p-6 rounded-3xl text-sm leading-relaxed border border-muted">
                <h3 className="font-bold mb-2">What is Keyboard Ghosting?</h3>
                <p className="text-muted-foreground">
                    Ghosting happens when some keys don't work when multiple keys are pressed simultaneously. This is due to the wiring design in some keyboards. Mechanical gaming keyboards often have "Anti-Ghosting" technology or "N-Key Rollover" (NKRO) to prevent this entirely.
                </p>
            </div>
        </div>
    );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Keyboard, Activity, RefreshCw, Zap } from 'lucide-react';
import { Button } from '@/components/ui/button';

export default function KeyRepeatRateTest() {
    const [rate, setRate] = useState(0);
    const [isTesting, setIsTesting] = useState(false);
    const [history, setHistory] = useState<number[]>([]);
    const lastTime = useRef(0);
    const count = useRef(0);

    const handleKeyDown = (e: KeyboardEvent) => {
        if (!isTesting || e.repeat === false) return;

        const now = performance.now();
        if (lastTime.current > 0) {
            const dt = now - lastTime.current;
            const currentHz = 1000 / dt;
            setRate(Math.round(currentHz));
        }
        lastTime.current = now;
        count.current++;
    };

    useEffect(() => {
        window.addEventListener('keydown', handleKeyDown);
        return () => window.removeEventListener('keydown', handleKeyDown);
    }, [isTesting]);

    const toggleTest = () => {
        if (!isTesting) {
            setRate(0);
            lastTime.current = 0;
            count.current = 0;
        } else {
            setHistory(prev => [rate, ...prev].slice(0, 5));
        }
        setIsTesting(!isTesting);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Keyboard className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Key Repeat Rate Test</CardTitle>
                <p className="text-muted-foreground mt-2">Measure how many characters per second your OS registers when holding a key.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-10">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="p-8 rounded-[2rem] bg-primary/10 border border-primary/10 text-center">
                        <p className="text-xs font-black uppercase tracking-widest text-primary mb-1">Characters Per Second (CPS)</p>
                        <p className="text-7xl font-black">{rate}</p>
                        <p className="text-[10px] text-muted-foreground mt-2 font-bold">{rate > 30 ? 'EXTREME' : rate > 15 ? 'FAST' : 'STANDARD'}</p>
                    </div>
                    <div className="flex flex-col justify-center gap-4">
                        <div className="flex items-center gap-3 p-4 rounded-2xl bg-muted/30 border border-primary/5">
                            <Activity className="h-5 w-5 text-primary" />
                            <div className="text-xs">
                                <p className="font-bold">Software Influenced</p>
                                <p className="text-muted-foreground">Adjustable in Windows Control Panel.</p>
                            </div>
                        </div>
                        <div className="flex items-center gap-3 p-4 rounded-2xl bg-muted/30 border border-primary/5">
                            <Zap className="h-5 w-5 text-primary" />
                            <div className="text-xs">
                                <p className="font-bold">Input Frequency</p>
                                <p className="text-muted-foreground">High rates are preferred by programmers.</p>
                            </div>
                        </div>
                    </div>
                </div>

                <div
                    className={`p-16 rounded-[3rem] border-4 transition-all duration-300 flex flex-col items-center justify-center text-center ${isTesting ? 'bg-primary/5 border-primary/40' : 'bg-muted/10 border-primary/5'
                        }`}
                >
                    {!isTesting ? (
                        <Button size="lg" className="rounded-2xl h-16 px-12 text-xl font-black" onClick={toggleTest}>
                            Start Testing
                        </Button>
                    ) : (
                        <div className="space-y-6">
                            <h3 className="text-3xl font-black animate-pulse uppercase">Hold ANY key down...</h3>
                            <p className="text-muted-foreground">Keep it held to measure the sustained rate.</p>
                            <Button variant="destructive" className="rounded-xl" onClick={toggleTest}>Stop Test</Button>
                        </div>
                    )}
                </div>

                {history.length > 0 && (
                    <div className="space-y-4">
                        <p className="text-xs font-black uppercase tracking-widest text-muted-foreground">Previous High Scores</p>
                        <div className="flex flex-wrap gap-2">
                            {history.map((h, i) => (
                                <div key={i} className="px-4 py-2 rounded-xl bg-background border border-primary/10 font-bold">
                                    {h} CPS
                                </div>
                            ))}
                        </div>
                    </div>
                )}
            </CardContent>
        </Card>
    );
}

'use client';

import { useState, useRef, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Volume2, Play, Activity, Timer } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { toast } from 'sonner';

export default function KeyboardAudioLatency() {
    const [latencies, setLatencies] = useState<number[]>([]);
    const [state, setState] = useState<'idle' | 'ready'>('idle');
    const startTimeRef = useRef(0);
    const audioCtxRef = useRef<AudioContext | null>(null);

    const initAudio = () => {
        if (!audioCtxRef.current) {
            audioCtxRef.current = new (window.AudioContext || (window as any).webkitAudioContext)();
        }
    };

    const playBeep = () => {
        if (!audioCtxRef.current) return;
        const osc = audioCtxRef.current.createOscillator();
        const gain = audioCtxRef.current.createGain();
        osc.connect(gain);
        gain.connect(audioCtxRef.current.destination);
        osc.start();
        osc.stop(audioCtxRef.current.currentTime + 0.1);
    };

    const handleKeyDown = (e: KeyboardEvent) => {
        if (state !== 'ready') return;
        const now = performance.now();
        const diff = Math.round(now - startTimeRef.current);

        setLatencies(prev => [diff, ...prev].slice(0, 5));
        playBeep();
        setState('idle');
        toast.info(`Audio Latency: ${diff}ms`);
    };

    useEffect(() => {
        window.addEventListener('keydown', handleKeyDown);
        return () => window.removeEventListener('keydown', handleKeyDown);
    }, [state]);

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Volume2 className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Keyboard Audio Latency Test</CardTitle>
                <p className="text-muted-foreground mt-2">Measure the delay between a keypress and hearing a sound cue.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-10">
                <div className="flex gap-6 justify-center">
                    <div className="p-8 rounded-[2rem] bg-primary/10 border border-primary/10 text-center flex-1">
                        <p className="text-xs font-black uppercase tracking-widest text-primary mb-1">Average Sync Delay</p>
                        <p className="text-7xl font-black">
                            {latencies.length > 0 ? Math.round(latencies.reduce((a, b) => a + b, 0) / latencies.length) : 0}
                            <span className="text-xl">ms</span>
                        </p>
                    </div>
                </div>

                <div
                    className={`h-64 rounded-[3rem] border-4 flex flex-col items-center justify-center cursor-pointer transition-all ${state === 'ready' ? 'bg-green-500/10 border-green-500/30 ring-4 ring-green-500/10' : 'bg-muted/10 border-primary/10'
                        }`}
                    onClick={() => {
                        initAudio();
                        setState('ready');
                        startTimeRef.current = performance.now();
                    }}
                >
                    {state === 'idle' ? (
                        <div className="text-center space-y-4">
                            <Play className="h-12 w-12 text-primary mx-auto fill-primary" />
                            <h3 className="text-3xl font-black">CLICK TO ARM</h3>
                        </div>
                    ) : (
                        <div className="text-center space-y-4">
                            <Timer className="h-12 w-12 text-green-500 animate-spin mx-auto" />
                            <h3 className="text-3xl font-black uppercase">PRESS ANY KEY NOW</h3>
                        </div>
                    )}
                </div>

                <p className="text-center text-xs text-muted-foreground italic px-12">
                    Critical for rhythm game calibration (osu!, StepMania, Guitar Hero). This measures the system's total path from input scan to audio driver output.
                </p>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Zap, AlertTriangle, CheckCircle2, History } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { toast } from 'sonner';

export default function KeyboardDebounceTester() {
    const [events, setEvents] = useState<any[]>([]);
    const [badKeys, setBadKeys] = useState<Record<string, number>>({});
    const lastPress = useRef<Record<string, number>>({});

    const handleKeyDown = (e: KeyboardEvent) => {
        const now = performance.now();
        const prev = lastPress.current[e.key] || 0;
        const diff = now - prev;

        if (diff > 0 && diff < 30) {
            setBadKeys(prevBad => ({
                ...prevBad,
                [e.key]: (prevBad[e.key] || 0) + 1
            }));
            setEvents(prevEv => [{ key: e.key, diff: Math.round(diff), time: new Date().toLocaleTimeString() }, ...prevEv].slice(0, 10));
            toast.error(`Chatter detected on key: ${e.key} (${Math.round(diff)}ms)`);
        }

        lastPress.current[e.key] = now;
    };

    useEffect(() => {
        window.addEventListener('keydown', handleKeyDown);
        return () => window.removeEventListener('keydown', handleKeyDown);
    }, []);

    const clear = () => {
        setEvents([]);
        setBadKeys({});
        lastPress.current = {};
    };

    return (
