}

'use client';

import React, { useState, useEffect } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { MousePointer2, Thermometer, RotateCcw } from 'lucide-react';

export default function KeyboardHeatmap() {
    const [presses, setPresses] = useState<Record<string, number>>({});
    const [totalPresses, setTotalPresses] = useState(0);

    useEffect(() => {
        const handleDown = (e: KeyboardEvent) => {
            setPresses(prev => {
                const key = e.code.replace('Key', '');
                return { ...prev, [key]: (prev[key] || 0) + 1 };
            });
            setTotalPresses(p => p + 1);
        };
        window.addEventListener('keydown', handleDown);
        return () => window.removeEventListener('keydown', handleDown);
    }, []);

    const getKeyColor = (count: number) => {
        if (!count) return 'bg-muted/40';
        const intensity = Math.min(count / 10, 1);
        if (intensity < 0.3) return 'bg-blue-500/30 text-blue-700 dark:text-blue-300';
        if (intensity < 0.6) return 'bg-yellow-500/50 text-yellow-800 dark:text-yellow-200';
        return 'bg-red-500 text-white shadow-[0_0_15px_rgba(239,68,68,0.4)]';
    };

    const row1 = ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'];
    const row2 = ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'];
    const row3 = ['Z', 'X', 'C', 'V', 'B', 'N', 'M'];

    return (
        <div className="space-y-8">
            <div className="flex justify-between items-end bg-gradient-to-r from-muted/50 to-transparent p-6 rounded-3xl border border-muted">
                <div className="space-y-1">
                    <h2 className="text-2xl font-black flex items-center gap-2">
                        <Thermometer className="h-6 w-6 text-red-500" />
                        Usage Heatmap
                    </h2>
                    <p className="text-sm text-muted-foreground">Real-time visualization of key frequency.</p>
                </div>
                <div className="text-right">
                    <div className="text-[10px] uppercase font-bold text-muted-foreground">Total Events</div>
                    <div className="text-4xl font-black text-primary">{totalPresses}</div>
                </div>
            </div>

            <div className="flex flex-col gap-2 p-8 bg-zinc-950 rounded-[2.5rem] border-[12px] border-zinc-900 shadow-2xl overflow-x-auto min-w-[600px]">
                <div className="flex justify-center gap-2">
                    {row1.map(k => (
                        <div key={k} className={`h-14 w-14 rounded-xl flex flex-col items-center justify-center transition-all duration-300 ${getKeyColor(presses[k])}`}>
                            <span className="text-xs font-black">{k}</span>
                            <span className="text-[10px] opacity-60 font-mono">{presses[k] || 0}</span>
                        </div>
                    ))}
                </div>
                <div className="flex justify-center gap-2 pl-6">
                    {row2.map(k => (
                        <div key={k} className={`h-14 w-14 rounded-xl flex flex-col items-center justify-center transition-all duration-300 ${getKeyColor(presses[k])}`}>
                            <span className="text-xs font-black">{k}</span>
                            <span className="text-[10px] opacity-60 font-mono">{presses[k] || 0}</span>
                        </div>
                    ))}
                </div>
                <div className="flex justify-center gap-2 pl-12">
                    {row3.map(k => (
                        <div key={k} className={`h-14 w-14 rounded-xl flex flex-col items-center justify-center transition-all duration-300 ${getKeyColor(presses[k])}`}>
                            <span className="text-xs font-black">{k}</span>
                            <span className="text-[10px] opacity-60 font-mono">{presses[k] || 0}</span>
                        </div>
                    ))}
                </div>
                <div className="flex justify-center mt-2">
                    <div className={`h-14 w-[300px] rounded-xl flex flex-col items-center justify-center transition-all duration-300 ${getKeyColor(presses['Space'])}`}>
                        <span className="text-[10px] font-black uppercase">Space</span>
                        <span className="text-[10px] opacity-60 font-mono">{presses['Space'] || 0}</span>
                    </div>
                </div>
            </div>

            <div className="flex justify-center gap-4">
                <Button variant="outline" className="rounded-full px-8" onClick={() => { setPresses({}); setTotalPresses(0); }}>
                    <RotateCcw className="mr-2 h-4 w-4" /> Reset Heatmap
                </Button>
            </div>
        </div>
    );
}

'use client';

import { useState, useEffect, useCallback, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Keyboard, RotateCcw, Zap, BarChart3 } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

export default function KeyboardLatency() {
    const [latencies, setLatencies] = useState<number[]>([]);
    const [lastLatency, setLastLatency] = useState<number | null>(null);
    const [isReady, setIsReady] = useState(false);

    const lastPressRef = useRef<number>(0);

    const handleKeyPress = useCallback((e: KeyboardEvent) => {
        if (e.repeat) return;

        const now = performance.now();
        // This is a naive client-side measurement, actually measuring the overhead of the browser event loop
        // But usable for comparing consistent feel.
        if (lastPressRef.current !== 0) {
            const diff = now - lastPressRef.current;
            // In a real browser environment, it's hard to get true hardware latency, 
            // but we can measure responsiveness of the current environment.
            setLastLatency(diff);
            setLatencies(prev => [diff, ...prev.slice(0, 19)]);
        }
        lastPressRef.current = now;
    }, []);

    useEffect(() => {
        window.addEventListener('keydown', handleKeyPress);
        return () => window.removeEventListener('keydown', handleKeyPress);
    }, [handleKeyPress]);

    const averageLatency = latencies.length > 0
        ? latencies.reduce((a, b) => a + b, 0) / latencies.length
        : 0;

    const reset = () => {
        setLatencies([]);
        setLastLatency(null);
        lastPressRef.current = 0;
    };

    return (
        <div className="mx-auto max-w-4xl space-y-8">
            <div className="grid gap-6 md:grid-cols-2">
                <Card className="border-primary/10 overflow-hidden">
                    <CardHeader className="bg-primary/5 pb-6">
                        <div className="mx-auto flex h-12 w-12 items-center justify-center rounded-2xl bg-primary/10 mb-4">
                            <Keyboard className="h-6 w-6 text-primary" />
                        </div>
                        <CardTitle className="text-center">Detection Area</CardTitle>
                        <CardDescription className="text-center">Press any key repeatedly to measure responsiveness.</CardDescription>
                    </CardHeader>
                    <CardContent className="h-64 flex flex-col items-center justify-center p-6 text-center space-y-4">
                        <div className="relative">
                            <AnimatePresence mode='wait'>
                                <motion.div
                                    key={lastLatency}
                                    initial={{ scale: 1.2, opacity: 0 }}
                                    animate={{ scale: 1, opacity: 1 }}
                                    className="text-7xl font-black text-primary tracking-tighter tabular-nums"
                                >
                                    {lastLatency ? `${lastLatency.toFixed(1)}` : '0.0'}
                                    <span className="text-xl font-bold ml-1">ms</span>
                                </motion.div>
                            </AnimatePresence>
                            <div className="absolute -top-4 -right-8 h-8 w-8 bg-yellow-400/20 rounded-full flex items-center justify-center animate-bounce">
                                <Zap className="h-4 w-4 text-yellow-500 fill-yellow-500" />
                            </div>
                        </div>
                        <p className="text-sm font-medium text-muted-foreground uppercase tracking-widest">Latest Response Delay</p>
                    </CardContent>
                </Card>

                <div className="space-y-6">
                    <Card className="border-primary/10">
                        <CardContent className="pt-6">
                            <div className="flex items-center justify-between mb-4">
                                <div className="flex items-center gap-2">
                                    <BarChart3 className="h-4 w-4 text-primary" />
                                    <span className="font-bold">Average Latency</span>
                                </div>
                                <span className="text-2xl font-black text-primary">{averageLatency.toFixed(2)} ms</span>
                            </div>
                            <div className="space-y-2">
                                <div className="h-2 w-full bg-muted rounded-full overflow-hidden">
                                    <motion.div
                                        className="h-full bg-primary"
                                        initial={{ width: 0 }}
                                        animate={{ width: `${Math.min(100, (averageLatency / 100) * 100)}%` }}
                                    />
                                </div>
                                <div className="flex justify-between text-[10px] text-muted-foreground font-bold font-mono">
                                    <span>0ms</span>
                                    <span>50ms (Normal)</span>
                                    <span>100ms+</span>
                                </div>
                            </div>
                        </CardContent>
                    </Card>

                    <Card className="border-primary/10 h-[164px] overflow-hidden">
                        <CardHeader className="py-3 bg-muted/30">
                            <CardTitle className="text-sm font-bold">Recent History</CardTitle>
                        </CardHeader>
                        <CardContent className="p-0 h-full">
                            <div className="h-full overflow-y-auto px-4 py-2 space-y-1 font-mono text-xs">
                                {latencies.map((l, i) => (
                                    <div key={i} className="flex justify-between border-b border-muted py-1">
                                        <span className="text-muted-foreground">#{latencies.length - i}</span>
                                        <span className="font-bold">{l.toFixed(2)} ms</span>
                                    </div>
                                ))}
                                {latencies.length === 0 && (
                                    <div className="h-full flex items-center justify-center text-muted-foreground italic">
                                        Start typing to see results...
                                    </div>
                                )}
                            </div>
                        </CardContent>
                    </Card>

                    <Button variant="outline" className="w-full rounded-xl" onClick={reset}>
                        <RotateCcw className="mr-2 h-4 w-4" />
                        Clear Results
                    </Button>
                </div>
            </div>

            <div className="rounded-2xl border bg-muted/30 p-8 space-y-6">
                <h3 className="text-xl font-bold">How measurement works</h3>
                <p className="text-muted-foreground leading-relaxed">
                    This tool measures the time between browser JavaScript receiving one `keydown` event and the next.
                    While it doesn't represent true hardware "input lag" (which requires high-speed cameras),
                    it effectively measures **System & Browser Response Consistency**.
                </p>
                <div className="grid gap-4 sm:grid-cols-2">
                    <div className="bg-background p-4 rounded-xl border border-primary/5">
                        <h4 className="font-bold text-sm mb-2">Lower is Better</h4>
                        <p className="text-xs text-muted-foreground">Typical browser overhead is 8ms to 32ms. Pro setups with high polling rates may see even lower values.</p>
                    </div>
                    <div className="bg-background p-4 rounded-xl border border-primary/5">
                        <h4 className="font-bold text-sm mb-2">Consistency Matters</h4>
                        <p className="text-xs text-muted-foreground">Gamers prefer stable 1ms-5ms variations over erratic jumps, as it ensures predictable reaction timing.</p>
                    </div>
                </div>
            </div>
        </div>
    );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Activity, Zap, RefreshCw, Info } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { ResponsiveContainer, AreaChart, Area, XAxis, YAxis } from 'recharts';

export default function KeyboardPollingRateCheck() {
    const [rate, setRate] = useState(0);
    const [isTesting, setIsTesting] = useState(false);
    const [data, setData] = useState<any[]>([]);
    const lastTime = useRef(0);

    const handleKeyDown = (e: KeyboardEvent) => {
        if (!isTesting) return;
        const now = performance.now();
        if (lastTime.current > 0) {
            const dt = now - lastTime.current;
            const hz = Math.round(1000 / dt);
            setRate(hz);
            setData(prev => [...prev, { val: hz }].slice(-30));
        }
        lastTime.current = now;
    };

    useEffect(() => {
        window.addEventListener('keydown', handleKeyDown);
        return () => window.removeEventListener('keydown', handleKeyDown);
    }, [isTesting]);

    const toggleTest = () => {
        if (!isTesting) {
            setRate(0);
            setData([]);
            lastTime.current = 0;
        }
        setIsTesting(!isTesting);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Activity className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Keyboard Polling Rate Checker</CardTitle>
                <p className="text-muted-foreground mt-2">Measure the reporting frequency of your keyboard to ensure low latency.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-8">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="p-10 rounded-[2.5rem] bg-primary/10 border border-primary/10 text-center">
                        <p className="text-xs font-black uppercase tracking-widest text-primary mb-1">Measured Frequency</p>
                        <p className="text-8xl font-black">{rate}<span className="text-xl">Hz</span></p>
                    </div>
                    <div className="flex flex-col justify-center gap-4">
                        <PollingBox label="Standard Office" value="125Hz" />
                        <PollingBox label="Gaming Grade" value="1000Hz" primary />
                        <PollingBox label="Ultra-Responsive" value="8000Hz" />
                    </div>
                </div>

                <div className="h-48 w-full bg-muted/10 rounded-3xl p-6 border border-primary/5">
                    <ResponsiveContainer width="100%" height="100%">
                        <AreaChart data={data}>
                            <Area type="monotone" dataKey="val" stroke="hsl(var(--primary))" fill="hsl(var(--primary)/0.1)" />
                        </AreaChart>
                    </ResponsiveContainer>
                </div>

                <div className={`p-16 rounded-[3rem] border-4 transition-all duration-300 flex flex-col items-center justify-center text-center ${isTesting ? 'bg-primary/5 border-primary/40' : 'bg-muted/10 border-primary/5'
                    }`}>
                    {!isTesting ? (
                        <Button size="lg" className="rounded-2xl h-16 px-12 text-xl font-bold" onClick={toggleTest}>Start Polling Test</Button>
                    ) : (
                        <div className="animate-in zoom-in-50">
                            <h3 className="text-3xl font-black mb-4">MASH MULTIPLE KEYS!</h3>
                            <p className="text-muted-foreground">The more keys you press, the more data we collect to calculate accurate Hz.</p>
                            <Button variant="ghost" className="mt-6" onClick={toggleTest}>Stop Test</Button>
                        </div>
                    )}
                </div>

                <div className="p-6 rounded-2xl bg-muted/20 flex gap-4">
                    <Info className="h-6 w-6 text-primary shrink-0" />
                    <p className="text-xs text-muted-foreground italic leading-relaxed">
                        <b>Note:</b> We measure the interval between raw `keydown` events. For precise 1000Hz+ readings, browsers often require high-performance system settings.
                    </p>
                </div>
            </CardContent>
        </Card>
    );
}

function PollingBox({ label, value, primary }: any) {
    return (
        <div className={`flex justify-between items-center p-4 rounded-xl border ${primary ? 'bg-primary/20 border-primary/30' : 'bg-background/40 border-primary/5'}`}>
            <span className="text-xs font-bold text-muted-foreground">{label}</span>
            <span className="font-black text-primary">{value}</span>
        </div>
    );
}

