    );
}

'use client';

import { useState, useEffect, useRef, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { TrendingUp, RotateCcw, Info, Activity, AlertTriangle, CheckCircle2 } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

export default function MouseAcceleration() {
    const [isActive, setIsActive] = useState(false);
    const [data, setData] = useState<{ speed: number; distance: number }[]>([]);
    const [accelerationScore, setAccelerationScore] = useState<number>(0);

    const lastPosRef = useRef<{ x: number; y: number } | null>(null);
    const lastTimeRef = useRef<number>(0);
    const pathRef = useRef<{ speed: number; distance: number }[]>([]);

    const handleMouseMove = useCallback((e: MouseEvent) => {
        if (!isActive) return;

        const now = performance.now();
        if (lastPosRef.current && lastTimeRef.current !== 0) {
            const dx = e.clientX - lastPosRef.current.x;
            const dy = e.clientY - lastPosRef.current.y;
            const dist = Math.sqrt(dx * dx + dy * dy);
            const dt = now - lastTimeRef.current;

            if (dist > 0 && dt > 0) {
                const speed = dist / dt;
                pathRef.current.push({ speed, distance: dist });

                if (pathRef.current.length > 100) {
                    setData([...pathRef.current]);

                    // Heuristic to calculate acceleration:
                    // Does the distance per pixel change significantly at different speeds?
                    // This is hard in browser due to OS abstraction, but we can measure consistency.
                    const speeds = pathRef.current.map(p => p.speed);
                    const distances = pathRef.current.map(p => p.distance);

                    // Simple correlation check (rough estimation)
                    const n = speeds.length;
                    const sumX = speeds.reduce((a, b) => a + b, 0);
                    const sumY = distances.reduce((a, b) => a + b, 0);
                    const avgX = sumX / n;
                    const avgY = sumY / n;

                    // Variance
                    const variance = speeds.reduce((a, b) => a + Math.pow(b - avgX, 2), 0) / n;
                    setAccelerationScore(Math.min(100, (variance * 100)));

                    pathRef.current = pathRef.current.slice(50);
                }
            }
        }

        lastPosRef.current = { x: e.clientX, y: e.clientY };
        lastTimeRef.current = now;
    }, [isActive]);

    useEffect(() => {
        window.addEventListener('mousemove', handleMouseMove);
        return () => window.removeEventListener('mousemove', handleMouseMove);
    }, [handleMouseMove]);

    const toggle = () => {
        setIsActive(!isActive);
        if (!isActive) {
            lastPosRef.current = null;
            lastTimeRef.current = 0;
            pathRef.current = [];
            setData([]);
        }
    };

    const reset = () => {
        setData([]);
        setAccelerationScore(0);
        pathRef.current = [];
    };

    return (
        <div className="mx-auto max-w-4xl space-y-8">
            <div className="grid gap-6 md:grid-cols-3">
                <Card className="md:col-span-2 overflow-hidden border-primary/10">
                    <CardHeader className="bg-primary/5">
                        <div className="flex items-center justify-between">
                            <div className="flex items-center gap-3">
                                <div className="flex h-10 w-10 items-center justify-center rounded-xl bg-primary/10">
                                    <TrendingUp className="h-6 w-6 text-primary" />
                                </div>
                                <div>
                                    <CardTitle>Consistency Tracker</CardTitle>
                                    <CardDescription>Move your mouse at varying speeds</CardDescription>
                                </div>
                            </div>
                            <Button variant={isActive ? "destructive" : "default"} onClick={toggle} className="rounded-full">
                                {isActive ? "Stop Analysis" : "Start Analysis"}
                            </Button>
                        </div>
                    </CardHeader>
                    <CardContent className="h-80 flex flex-col items-center justify-center p-6 text-center">
                        {isActive ? (
                            <div className="w-full h-full flex flex-col items-center justify-center space-y-8">
                                <div className="relative">
                                    <motion.div
                                        className="text-8xl font-black text-primary tracking-tighter"
                                    >
                                        {accelerationScore.toFixed(0)}
                                        <span className="text-xl font-bold ml-1">%</span>
                                    </motion.div>
                                    <p className="text-xs font-bold uppercase tracking-widest text-muted-foreground mt-2">Variance Score</p>
                                </div>

                                <div className="w-full flex items-end justify-center gap-1 h-32 px-4 opacity-50">
                                    {data.slice(-50).map((d, i) => (
                                        <div
                                            key={i}
                                            style={{ height: `${Math.min(100, d.speed * 20)}%` }}
                                            className="flex-1 bg-primary/40 rounded-t-sm"
                                        />
                                    ))}
                                </div>
                            </div>
                        ) : (
                            <div className="text-muted-foreground space-y-4">
                                <div className="h-20 w-20 bg-muted rounded-full flex items-center justify-center mx-auto opacity-50">
                                    <TrendingUp className="h-10 w-10" />
                                </div>
                                <p className="max-w-xs mx-auto">Click "Start Analysis" and move your mouse in circles/lines at different speeds.</p>
                            </div>
                        )}
                    </CardContent>
                </Card>

                <div className="space-y-6">
                    <Card className={`border-primary/10 ${accelerationScore < 15 ? 'bg-green-500/5' : accelerationScore < 40 ? 'bg-yellow-500/5' : 'bg-destructive/5'}`}>
                        <CardContent className="pt-6 text-center space-y-4">
                            <p className="text-[10px] font-bold uppercase tracking-widest text-muted-foreground">Detection Status</p>
                            {accelerationScore < 15 ? (
                                <div className="space-y-2">
                                    <CheckCircle2 className="h-10 w-10 text-green-500 mx-auto" />
                                    <p className="text-sm font-bold text-green-600">Pure Consistency</p>
                                    <p className="text-[10px] text-green-600/70">Linear movement detected.</p>
                                </div>
                            ) : accelerationScore < 40 ? (
                                <div className="space-y-2">
                                    <Info className="h-10 w-10 text-yellow-500 mx-auto" />
                                    <p className="text-sm font-bold text-yellow-600">Minor Variance</p>
                                    <p className="text-[10px] text-yellow-600/70">Potential slight acceleration.</p>
                                </div>
                            ) : (
                                <div className="space-y-2">
                                    <AlertTriangle className="h-10 w-10 text-destructive mx-auto" />
                                    <p className="text-sm font-bold text-destructive">High Acceleration</p>
                                    <p className="text-[10px] text-destructive-foreground/70">Non-linear speed detected.</p>
                                </div>
                            )}
                        </CardContent>
                    </Card>

                    <div className="p-6 rounded-2xl border bg-muted/30 space-y-3">
                        <h4 className="font-bold text-sm">PRO TIP</h4>
                        <p className="text-xs text-muted-foreground leading-relaxed">
                            Always disable "Enhance Pointer Precision" in Windows Mouse Settings for linear, 1:1 movement.
                        </p>
                    </div>

                    <Button variant="outline" className="w-full rounded-xl" onClick={reset}>
                        <RotateCcw className="mr-2 h-4 w-4" />
                        Clear Analysis
                    </Button>
                </div>
            </div>

            <div className="rounded-2xl border bg-muted/30 p-8 space-y-6">
                <h3 className="text-xl font-bold">What is Mouse Acceleration?</h3>
                <p className="text-muted-foreground leading-relaxed">
                    Mouse Acceleration means the distance your cursor moves depends on **how fast** you move
                    your mouse, not just how far. In competitive FPS gaming, this is generally considered bad
                    because it makes building muscle memory nearly impossible.
                </p>
                <div className="grid gap-4 sm:grid-cols-2">
                    <div className="bg-background p-5 rounded-2xl border border-primary/5">
                        <h4 className="font-bold text-sm mb-2 text-primary">Acceleration OFF (Raw Input)</h4>
                        <p className="text-xs text-muted-foreground">1cm of mousepad movement = 100 pixels on screen. Faster or slower movement doesn't change this.</p>
                    </div>
                    <div className="bg-background p-5 rounded-2xl border border-primary/5">
                        <h4 className="font-bold text-sm mb-2 text-destructive">Acceleration ON</h4>
                        <p className="text-xs text-muted-foreground">Slow 1cm move = 50 pixels. Fast 1cm move = 200 pixels. Predictability is lost.</p>
                    </div>
                </div>
            </div>
        </div>
    );
}

'use client';

import { useState, useRef, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Zap, Activity, MousePointer2, AlertCircle } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { ResponsiveContainer, LineChart, Line, XAxis, YAxis, Tooltip } from 'recharts';

export default function MouseGlideTest() {
    const [isTesting, setIsTesting] = useState(false);
    const [data, setData] = useState<any[]>([]);
    const [stats, setStats] = useState({ consistency: 0, maxSpeed: 0 });
    const lastPos = useRef({ x: 0, y: 0 });
    const lastTime = useRef(0);

    const handleMouseMove = (e: React.MouseEvent) => {
        if (!isTesting) return;

        const now = performance.now();
        const dt = now - lastTime.current;
        if (dt < 10) return; // Sample at ~100Hz

        const dx = e.clientX - lastPos.current.x;
        const dy = e.clientY - lastPos.current.y;
        const dist = Math.sqrt(dx * dx + dy * dy);
        const speed = (dist / dt) * 1000; // pixels per second

        setData(prev => {
            const next = [...prev, { time: Math.round(now), speed }].slice(-100);
            calculateStats(next);
            return next;
        });

        lastPos.current = { x: e.clientX, y: e.clientY };
        lastTime.current = now;
    };

    const calculateStats = (points: any[]) => {
        if (points.length < 10) return;
        const speeds = points.map(p => p.speed);
        const max = Math.max(...speeds);

        // Calculate coefficient of variation as consistency metric
        const avg = speeds.reduce((a, b) => a + b, 0) / speeds.length;
        const variance = speeds.reduce((a, b) => a + Math.pow(b - avg, 2), 0) / speeds.length;
        const stdDev = Math.sqrt(variance);
        const consistency = Math.max(0, 100 - (stdDev / (avg || 1) * 100));

        setStats({ consistency: Math.round(consistency), maxSpeed: Math.round(max) });
    };

    const toggleTest = () => {
        if (!isTesting) {
            setData([]);
            setStats({ consistency: 0, maxSpeed: 0 });
        }
        setIsTesting(!isTesting);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Zap className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Mouse Glide Test</CardTitle>
                <p className="text-muted-foreground mt-2">Measure the smoothness and consistency of your mouse surface movement.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-8">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="p-6 rounded-2xl bg-muted/30 border border-primary/5 text-center">
                        <p className="text-xs font-black uppercase tracking-widest text-primary mb-1">Consistency Score</p>
                        <p className="text-5xl font-black">{stats.consistency}%</p>
                        <p className="text-[10px] text-muted-foreground mt-2">Target: 90%+ for smooth aim</p>
                    </div>
                    <div className="p-6 rounded-2xl bg-muted/30 border border-primary/5 text-center">
                        <p className="text-xs font-black uppercase tracking-widest text-primary mb-1">Peak Glide Speed</p>
                        <p className="text-5xl font-black">{stats.maxSpeed}</p>
                        <p className="text-[10px] text-muted-foreground mt-2">Pixels per second</p>
                    </div>
                </div>

                <div
                    className={`relative h-64 rounded-3xl border-2 border-dashed transition-all duration-300 flex items-center justify-center ${isTesting ? 'bg-primary/5 border-primary/40' : 'bg-muted/10 border-primary/10'
                        }`}
                    onMouseMove={handleMouseMove}
                >
                    {!isTesting ? (
                        <div className="text-center">
                            <Button size="lg" className="rounded-2xl h-14 px-10 font-bold" onClick={toggleTest}>
                                Start Glide Test
                            </Button>
                            <p className="text-xs text-muted-foreground mt-4 italic">You will need to move your mouse in smooth, rapid circles.</p>
                        </div>
                    ) : (
                        <div className="absolute inset-0 pointer-events-none">
                            <ResponsiveContainer width="100%" height="100%">
                                <LineChart data={data}>
                                    <YAxis hide domain={[0, 'auto']} />
                                    <Line
                                        type="monotone"
                                        dataKey="speed"
                                        stroke="hsl(var(--primary))"
                                        strokeWidth={3}
                                        dot={false}
                                        animationDuration={0}
                                    />
                                </LineChart>
                            </ResponsiveContainer>
                            <div className="absolute top-4 right-4 animate-pulse flex items-center gap-2">
                                <span className="w-2 h-2 bg-red-500 rounded-full" />
                                <span className="text-[10px] font-bold text-red-500 uppercase">Recording...</span>
                            </div>
                            <Button
                                variant="outline"
                                className="absolute bottom-4 left-1/2 -translate-x-1/2 pointer-events-auto rounded-xl"
                                onClick={toggleTest}
                            >
                                Stop and Analyze
                            </Button>
                        </div>
                    )}
                </div>

                <div className="p-6 rounded-2xl bg-primary/5 border border-primary/10 flex gap-4 items-start">
                    <AlertCircle className="h-5 w-5 text-primary shrink-0 mt-0.5" />
                    <div className="text-sm">
                        <p className="font-bold text-primary mb-1">How to test:</p>
                        <p className="text-muted-foreground italic">Click start and move your mouse in continuous, fluid circles or "Figure 8" motions. A jagged line or low consistency score indicates friction issues, worn-out mouse feet (PTFE), or a dirty mousepad.</p>
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Timer, Zap, History, MousePointer2 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { toast } from 'sonner';

export default function MouseLatencyTester() {
    const [state, setState] = useState<'idle' | 'waiting' | 'ready' | 'result'>('idle');
    const [latencies, setLatencies] = useState<number[]>([]);
    const startTimeRef = useRef(0);
    const timeoutRef = useRef<any>(null);

    const startTest = () => {
        setState('waiting');
        const delay = Math.random() * 2000 + 1000;
        timeoutRef.current = setTimeout(() => {
            setState('ready');
            startTimeRef.current = performance.now();
        }, delay);
    };

    const handleClick = () => {
        if (state === 'ready') {
            const end = performance.now();
            const lat = Math.round(end - startTimeRef.current);
            setLatencies(prev => [lat, ...prev].slice(0, 5));
            setState('result');
            toast.success(`Click Registered: ${lat}ms`);
        } else if (state === 'waiting') {
            clearTimeout(timeoutRef.current);
            setState('idle');
            toast.error('Too early! Wait for the visual cue.');
        } else {
            startTest();
        }
    };

    const avg = latencies.length
        ? Math.round(latencies.reduce((a, b) => a + b, 0) / latencies.length)
        : 0;

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Timer className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Mouse Input Latency Test</CardTitle>
                <p className="text-muted-foreground mt-2">Measure the time between your click and browser event registration.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-10">
                <div
                    className={`w-full h-80 rounded-[3rem] border-4 transition-all duration-300 flex flex-col items-center justify-center cursor-pointer select-none ${state === 'waiting' ? 'bg-amber-500/10 border-amber-500/20' :
                            state === 'ready' ? 'bg-green-500/30 border-green-500/50 scale-[1.02]' :
                                'bg-muted/10 border-primary/10 hover:border-primary/20'
                        }`}
                    onClick={handleClick}
                >
                    <div className="text-center space-y-4">
                        {state === 'idle' || state === 'result' ? (
                            <>
                                <div className="p-6 rounded-3xl bg-primary/20 inline-block mb-4">
                                    <MousePointer2 className="h-12 w-12 text-primary" />
                                </div>
                                <h3 className="text-4xl font-black">{state === 'result' ? 'CLICK TO TEST AGAIN' : 'CLICK TO BEGIN'}</h3>
                                <p className="text-muted-foreground">Wait for the color change...</p>
                            </>
                        ) : state === 'waiting' ? (
                            <h3 className="text-4xl font-black opacity-30">PEEKING...</h3>
                        ) : (
                            <h3 className="text-6xl font-black text-green-500 uppercase animate-in zoom-in-75">CLICK NOW!</h3>
                        )}
                    </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                    <div className="p-8 rounded-3xl bg-primary/10 border border-primary/10 text-center">
                        <p className="text-xs font-black uppercase tracking-widest text-primary mb-1">Average Latency</p>
                        <p className="text-6xl font-black">{avg}ms</p>
                        <p className="text-xs text-muted-foreground mt-2 font-bold uppercase">Technical Response Time</p>
                    </div>
                    <div className="space-y-4">
                        <h4 className="font-bold flex items-center gap-2">
                            <History className="h-4 w-4" /> Recent Samples
                        </h4>
                        <div className="flex flex-wrap gap-3">
                            {latencies.map((l, i) => (
                                <div key={i} className="px-5 py-3 rounded-2xl bg-muted/40 border border-primary/10 font-black text-xl">
                                    {l}ms
                                </div>
                            ))}
                            {latencies.length === 0 && <p className="text-muted-foreground italic text-sm">Waiting for first result...</p>}
                        </div>
                    </div>
                </div>

                <div className="p-6 rounded-2xl bg-amber-500/5 border border-amber-500/10 flex gap-4 items-center">
                    <Zap className="h-6 w-6 text-amber-500 shrink-0" />
                    <p className="text-xs text-muted-foreground italic leading-relaxed">
                        <b>Note:</b> This test measures the sum of mouse click latency, USB polling, and browser event loop delay. Sub-10ms results are excellent for professional gaming peripherals.
                    </p>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { LayoutGrid, AlertCircle, Info, CheckCircle2, Zap } from 'lucide-react';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';

export default function MousePixelSkippingCheck() {
    const [dpi, setDpi] = useState('800');
    const [sens, setSens] = useState('1.0');
    const [res, setRes] = useState('1920');

    const calculateSkipping = () => {
        const d = parseFloat(dpi) || 800;
        const s = parseFloat(sens) || 1;
        const r = parseFloat(res) || 1920;

        // A very simplified heuristic for pixel skipping:
        // If (sens * multiplier) > 1, pixel skipping is mathematically possible
        // especially if Windows pointer speed != 6/11.
        const isSkipping = s > 1.5;

        return {
            isSkipping,
            ratio: (s).toFixed(2),
            advice: s > 1.5
                ? 'High probability of pixel skipping. Consider increasing DPI and lowering in-game sensitivity.'
                : 'Your sensitivity settings are safe. Motion will be smooth and pixel-perfect.'
        };
    };

    const results = calculateSkipping();

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <LayoutGrid className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Pixel Skipping Checker</CardTitle>
                <p className="text-muted-foreground mt-2">Check if your sensitivity settings cause cursor rounding errors.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-10">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    <div className="space-y-2">
                        <Label>Mouse DPI</Label>
                        <Input value={dpi} onChange={(e) => setDpi(e.target.value)} type="number" className="rounded-xl h-12" />
                    </div>
                    <div className="space-y-2">
                        <Label>In-Game Sens</Label>
                        <Input value={sens} onChange={(e) => setSens(e.target.value)} type="number" step="0.1" className="rounded-xl h-12 border-primary/30" />
                    </div>
                    <div className="space-y-2">
                        <Label>Monitor Width (Horizontal Res)</Label>
                        <Input value={res} onChange={(e) => setRes(e.target.value)} type="number" className="rounded-xl h-12" />
                    </div>
                </div>

                <div className={`p-8 rounded-[2.5rem] border-2 transition-all flex flex-col md:flex-row items-center gap-8 ${results.isSkipping ? 'bg-destructive/10 border-destructive/20' : 'bg-green-500/10 border-green-500/20'
                    }`}>
                    <div className="text-center md:text-left flex-1 space-y-2">
                        <div className="flex items-center gap-2 justify-center md:justify-start">
                            {results.isSkipping ? <AlertCircle className="text-destructive h-6 w-6" /> : <CheckCircle2 className="text-green-500 h-6 w-6" />}
                            <h3 className={`text-2xl font-black ${results.isSkipping ? 'text-destructive' : 'text-green-500'}`}>
                                {results.isSkipping ? 'Skipping Detected' : 'Perfect Scaling'}
                            </h3>
                        </div>
                        <p className="text-muted-foreground text-sm leading-relaxed">{results.advice}</p>
                    </div>
                    <div className="text-center bg-background/50 p-6 rounded-3xl border border-white/10">
                        <p className="text-[10px] font-black uppercase tracking-widest text-primary mb-1">Sens Multiplier</p>
                        <p className="text-4xl font-black">{results.ratio}x</p>
                    </div>
                </div>

                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                    <div className="p-6 rounded-2xl bg-muted/30 border border-primary/5 space-y-2">
                        <h4 className="font-bold text-sm flex items-center gap-2">
                            <Info className="h-4 w-4 text-primary" /> What is Pixel Skipping?
                        </h4>
                        <p className="text-xs text-muted-foreground leading-relaxed">
                            When your in-game sensitivity is so high that one "tick" of your mouse movement causes the cursor to jump multiple pixels, you lose the ability to aim at fine targets.
                        </p>
                    </div>
                    <div className="p-6 rounded-2xl bg-muted/30 border border-primary/5 space-y-2">
                        <h4 className="font-bold text-sm flex items-center gap-2">
                            <Zap className="h-4 w-4 text-primary" /> The Solution
                        </h4>
                        <p className="text-xs text-muted-foreground leading-relaxed">
                            Always aim for a sensitivity multiplier close to 1.0 or lower. If you need more speed, increase your physical mouse DPI instead of in-game multipliers.
                        </p>
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState, useRef, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Activity, AlertTriangle, CheckCircle2, Zap } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { ResponsiveContainer, ScatterChart, Scatter, XAxis, YAxis, ZAxis, Tooltip } from 'recharts';

export default function MouseSensorJitterTest() {
    const [isTesting, setIsTesting] = useState(false);
    const [points, setPoints] = useState<any[]>([]);
    const [jitterScore, setJitterScore] = useState(0);
    const lastPos = useRef({ x: 0, y: 0 });
    const samples = useRef<any[]>([]);

    const handleMouseMove = (e: React.MouseEvent) => {
        if (!isTesting) return;

        const dx = e.movementX;
        const dy = e.movementY;

        // Jitter is often characterized by small, random micro-movements
        // even when moving in a straight-ish line.
        samples.current.push({ x: dx, y: dy });

        if (samples.current.length > 50) {
            analyzeJitter();
            setPoints([...samples.current]);
            samples.current = [];
        }
    };

    const analyzeJitter = () => {
        const s = samples.current;
        if (s.length < 10) return;

        // Calculate variance in micro-movements
        let totalJitter = 0;
        for (let i = 1; i < s.length; i++) {
            const angle1 = Math.atan2(s[i - 1].y, s[i - 1].x);
            const angle2 = Math.atan2(s[i].y, s[i].x);
            totalJitter += Math.abs(angle1 - angle2);
        }

        const avgJitter = (totalJitter / s.length) * 100;
        setJitterScore(Math.min(100, Math.round(avgJitter)));
    };

    const toggleTest = () => {
        if (!isTesting) {
            setPoints([]);
            setJitterScore(0);
            samples.current = [];
        }
        setIsTesting(!isTesting);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Activity className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Sensor Jitter & Skip Test</CardTitle>
                <p className="text-muted-foreground mt-2">Detect micro-stuttering or sensor malfunctions at high DPI levels.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-8">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className={`p-8 rounded-[2rem] border-2 transition-all text-center ${jitterScore > 20 ? 'bg-destructive/10 border-destructive/20 text-destructive' : 'bg-green-500/10 border-green-500/20 text-green-500'
                        }`}>
                        <p className="text-xs font-black uppercase tracking-widest opacity-70">Jitter Index</p>
                        <p className="text-6xl font-black">{jitterScore}</p>
                        <p className="text-[10px] mt-2 font-bold">{jitterScore > 20 ? 'DETECTED' : 'CLEAN SENSOR'}</p>
                    </div>
                    <div className="p-8 rounded-[2rem] bg-muted/30 border border-primary/5 flex flex-col items-center justify-center text-center">
                        <div className="flex items-center gap-2 mb-2">
                            {jitterScore > 20 ? <AlertTriangle className="h-5 w-5 text-destructive" /> : <CheckCircle2 className="h-5 w-5 text-green-500" />}
                            <span className="font-bold uppercase tracking-tighter">Sensor Health</span>
                        </div>
                        <p className="text-xs text-muted-foreground leading-relaxed">
                            {jitterScore > 20
                                ? 'Sensor shows signs of jitter. Clean your mousepad or lower DPi settings.'
                                : 'Motion is fluid and consistent. Your sensor is performing at spec.'}
                        </p>
                    </div>
                </div>

                <div
                    className={`relative h-[400px] rounded-[3rem] border-4 flex items-center justify-center overflow-hidden transition-all duration-300 ${isTesting ? 'bg-background/80 border-primary/40' : 'bg-muted/10 border-primary/10'
                        }`}
                    onMouseMove={handleMouseMove}
                >
                    {!isTesting ? (
                        <div className="text-center">
                            <Button size="lg" className="rounded-2xl h-16 px-12 text-xl font-bold" onClick={toggleTest}>
                                Start Analysis
                            </Button>
                            <p className="text-xs text-muted-foreground mt-4 italic">Move your mouse in straight lines across the pad.</p>
                        </div>
                    ) : (
                        <div className="absolute inset-0 pointer-events-none">
                            <ResponsiveContainer width="100%" height="100%">
                                <ScatterChart>
                                    <XAxis type="number" dataKey="x" hide domain={[-50, 50]} />
                                    <YAxis type="number" dataKey="y" hide domain={[-50, 50]} />
                                    <ZAxis type="number" range={[20, 20]} />
                                    <Scatter data={points} fill="hsl(var(--primary))" />
                                </ScatterChart>
                            </ResponsiveContainer>
                            <Button
                                variant="destructive"
                                className="absolute bottom-6 left-1/2 -translate-x-1/2 pointer-events-auto rounded-xl"
                                onClick={toggleTest}
                            >
                                Stop Test
                            </Button>
                        </div>
                    )}
                </div>
            </CardContent>
        </Card>
    );
}

