'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Shapes, Play, Zap, Target } from 'lucide-react';
import { Button } from '@/components/ui/button';

const COLORS = ['#ef4444', '#22c55e', '#3b82f6', '#eab308'];
const LABELS = ['RED', 'GREEN', 'BLUE', 'YELLOW'];

export default function ReflexPatternMatch() {
    const [state, setState] = useState<'idle' | 'playing' | 'result'>('idle');
    const [score, setScore] = useState(0);
    const [time, setTime] = useState(30);
    const [targetColor, setTargetColor] = useState(0);
    const [options, setOptions] = useState<number[]>([]);
    const timerRef = useRef<any>(null);

    const startGame = () => {
        setState('playing');
        setScore(0);
        setTime(30);
        nextRound();

        timerRef.current = setInterval(() => {
            setTime(prev => {
                if (prev <= 1) {
                    endGame();
                    return 0;
                }
                return prev - 1;
            });
        }, 1000);
    };

    const endGame = () => {
        clearInterval(timerRef.current);
        setState('result');
    };

    const nextRound = () => {
        const target = Math.floor(Math.random() * 4);
        setTargetColor(target);
        setOptions([0, 1, 2, 3].sort(() => Math.random() - 0.5));
    };

    const handleMatch = (index: number) => {
        if (state !== 'playing') return;
        if (index === targetColor) {
            setScore(s => s + 1);
            nextRound();
        } else {
            // Penalty for wrong match
            setScore(s => Math.max(0, s - 1));
        }
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Shapes className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Reflex Pattern Match</CardTitle>
                <p className="text-muted-foreground mt-2">Identify and match the target color quickly. Improvements target focus.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-8">
                <div className="flex justify-between items-center bg-muted/30 p-4 rounded-2xl border border-primary/5 font-bold">
                    <span>Correct Matches: {score}</span>
                    <span>Time: {time}s</span>
                </div>

                <div className="flex flex-col items-center gap-10 min-h-[400px] justify-center">
                    {state === 'idle' && (
                        <div className="text-center space-y-6">
                            <p className="text-muted-foreground max-w-sm">Match the large colored box with one of the options below as fast as possible.</p>
                            <Button size="lg" className="rounded-2xl h-14" onClick={startGame}>
                                Start Pattern Test
                            </Button>
                        </div>
                    )}

                    {state === 'playing' && (
                        <>
                            <div
                                className="w-32 h-32 rounded-[2rem] shadow-2xl transition-all border-8 border-white/20 animate-in zoom-in-50 duration-300"
                                style={{ backgroundColor: COLORS[targetColor] }}
                            />

                            <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 w-full">
                                {options.map(idx => (
                                    <button
                                        key={idx}
                                        className="h-24 rounded-2xl border-2 border-primary/5 hover:border-primary/50 transition-all active:scale-95 shadow-lg group"
                                        style={{ backgroundColor: COLORS[idx] }}
                                        onClick={() => handleMatch(idx)}
                                    >
                                        <span className="text-white font-black drop-shadow-md opacity-0 group-hover:opacity-100 transition-opacity">
                                            {LABELS[idx]}
                                        </span>
                                    </button>
                                ))}
                            </div>
                        </>
                    )}

                    {state === 'result' && (
                        <div className="text-center">
                            <h3 className="text-6xl font-black text-primary mb-2">{score}</h3>
                            <p className="text-xl font-bold mb-8">Reflex Score</p>
                            <Button size="lg" className="rounded-2xl" onClick={startGame}>
                                Try Again
                            </Button>
                        </div>
                    )}
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Monitor, RefreshCw, Layers, Layout } from 'lucide-react';
import { toast } from 'sonner';
import { Label } from '@/components/ui/label';
import { Slider } from '@/components/ui/slider';

export default function ResolutionScaler() {
    const [width, setWidth] = useState('1920');
    const [height, setHeight] = useState('1080');
    const [scale, setScale] = useState([100]);

    const scaledWidth = Math.round((parseInt(width) || 0) * (scale[0] / 100));
    const scaledHeight = Math.round((parseInt(height) || 0) * (scale[0] / 100));

    const totalPixels = scaledWidth * scaledHeight;
    const nativePixels = (parseInt(width) || 0) * (parseInt(height) || 0);
    const reduction = nativePixels ? (100 - (totalPixels / nativePixels) * 100).toFixed(1) : 0;

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Monitor className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Resolution Scaler</CardTitle>
                <p className="text-muted-foreground mt-2">Calculate render scale impact on performance and clarity.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-8">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                    <div className="space-y-6">
                        <div className="grid grid-cols-2 gap-4">
                            <div className="space-y-2">
                                <Label>Native Width</Label>
                                <Input value={width} onChange={(e) => setWidth(e.target.value)} type="number" className="rounded-xl" />
                            </div>
                            <div className="space-y-2">
                                <Label>Native Height</Label>
                                <Input value={height} onChange={(e) => setHeight(e.target.value)} type="number" className="rounded-xl" />
                            </div>
                        </div>

                        <div className="space-y-4">
                            <div className="flex justify-between">
                                <Label className="font-bold">Render Scale: {scale[0]}%</Label>
                                <span className="text-primary font-bold">{scaledWidth} x {scaledHeight}</span>
                            </div>
                            <Slider value={scale} onValueChange={setScale} min={25} max={200} step={1} className="py-2" />
                        </div>
                    </div>

                    <div className="grid grid-cols-1 gap-4">
                        <div className="p-6 rounded-2xl bg-primary/10 border border-primary/10 flex flex-col items-center justify-center text-center">
                            <Layers className="h-6 w-6 text-primary mb-2" />
                            <p className="text-xs font-bold uppercase tracking-tighter text-primary">Pixel Load Reduction</p>
                            <p className="text-4xl font-black">{reduction}%</p>
                            <p className="text-xs text-muted-foreground mt-1">Less GPU power needed</p>
                        </div>
                        <div className="p-6 rounded-2xl bg-muted/30 border border-primary/5 flex flex-col items-center justify-center text-center">
                            <Layout className="h-6 w-6 text-muted-foreground mb-2" />
                            <p className="text-xs font-bold uppercase tracking-tighter">Total Scaled Pixels</p>
                            <p className="text-2xl font-black">{(totalPixels / 1000000).toFixed(2)}M</p>
                            <p className="text-xs text-muted-foreground mt-1">vs {(nativePixels / 1000000).toFixed(2)}M native</p>
                        </div>
                    </div>
                </div>

                <div className="pt-6 border-t border-primary/5 text-sm text-muted-foreground italic">
                    <p>Tip: Setting render scale to 75% at 4K provides almost identical load as 1440p native, but often with better UI clarity.</p>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState, useRef, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { MousePointer2, Zap, History, Timer } from 'lucide-react';
import { Button } from '@/components/ui/button';

export default function RightClickSpeedTest() {
    const [clicks, setClicks] = useState(0);
    const [isRunning, setIsRunning] = useState(false);
    const [timeLeft, setTimeLeft] = useState(5);
    const [results, setResults] = useState<number[]>([]);
    const timerRef = useRef<any>(null);

    const startTest = (e: React.MouseEvent) => {
        if (e.button !== 2) return; // Only right click

        setIsRunning(true);
        setClicks(1);
        setTimeLeft(5);

        timerRef.current = setInterval(() => {
            setTimeLeft(prev => {
                if (prev <= 0.1) {
                    endTest();
                    return 0;
                }
                return parseFloat((prev - 0.1).toFixed(1));
            });
        }, 100);
    };

    const endTest = () => {
        clearInterval(timerRef.current);
        setIsRunning(false);
        setResults(prev => [clicks / 5, ...prev].slice(0, 5));
    };

    const handleClick = (e: React.MouseEvent) => {
        e.preventDefault();
        if (e.button !== 2) return;

        if (!isRunning && timeLeft === 5) {
            startTest(e);
        } else if (isRunning) {
            setClicks(prev => prev + 1);
        }
    };

    const cps = isRunning ? (clicks / (5 - timeLeft || 0.1)).toFixed(1) : (clicks / 5).toFixed(1);

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <MousePointer2 className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Right Click Speed Test</CardTitle>
                <p className="text-muted-foreground mt-2">Measure your Clicks Per Second (CPS) using only the right mouse button.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-10">
                <div className="grid grid-cols-2 gap-6">
                    <div className="p-8 rounded-[2rem] bg-muted/30 border border-primary/5 text-center space-y-2">
                        <p className="text-xs font-black uppercase tracking-widest text-primary">Time Left</p>
                        <p className="text-6xl font-black">{timeLeft}s</p>
                    </div>
                    <div className="p-8 rounded-[2rem] bg-primary/10 border border-primary/10 text-center space-y-2">
                        <p className="text-xs font-black uppercase tracking-widest text-primary">Live CPS</p>
                        <p className="text-6xl font-black">{cps}</p>
                    </div>
                </div>

                <div
                    className={`relative w-full h-[400px] rounded-[3rem] border-4 transition-all duration-300 flex flex-col items-center justify-center cursor-pointer select-none ${isRunning ? 'bg-primary/5 border-primary/40 scale-[1.01]' : 'bg-muted/10 border-primary/5 hover:border-primary/20'
                        }`}
                    onMouseDown={handleClick}
                    onContextMenu={(e) => e.preventDefault()}
                >
                    {!isRunning ? (
                        <div className="text-center space-y-4">
                            <div className="p-6 rounded-3xl bg-primary/20 inline-block mb-4">
                                <Zap className="h-12 w-12 text-primary" />
                            </div>
                            <h3 className="text-4xl font-black">RIGHT CLICK TO START</h3>
                            <p className="text-muted-foreground">Test duration: 5 seconds</p>
                        </div>
                    ) : (
                        <div className="text-center">
                            <p className="text-8xl font-black opacity-20 pointer-events-none">{clicks}</p>
                            <p className="text-xl font-bold text-primary mt-4 uppercase animate-pulse">Keep Clicking!</p>
                        </div>
                    )}
                </div>

                <div className="space-y-4">
                    <h4 className="font-bold flex items-center gap-2">
                        <History className="h-4 w-4" /> Recent Top CPS
                    </h4>
                    <div className="flex flex-wrap gap-3">
                        {results.map((r, i) => (
                            <div key={i} className="px-6 py-3 rounded-2xl bg-background border border-primary/10 font-black text-lg">
                                {r.toFixed(1)} <span className="text-xs font-bold text-muted-foreground">CPS</span>
                            </div>
                        ))}
                        {results.length === 0 && <p className="text-muted-foreground italic text-sm">No tests completed yet...</p>}
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Mouse, Activity, ArrowDown, History, BarChart3 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { ResponsiveContainer, BarChart, Bar, XAxis, YAxis, Cell } from 'recharts';

export default function ScrollSpeedTester() {
    const [isTesting, setIsTesting] = useState(false);
    const [scrolls, setScrolls] = useState<any[]>([]);
    const [stats, setStats] = useState({ peak: 0, current: 0 });
    const timerRef = useRef<any>(null);
    const scrollAcc = useRef(0);

    const handleScroll = (e: React.WheelEvent) => {
        if (!isTesting) return;
        scrollAcc.current += Math.abs(e.deltaY);
    };

    useEffect(() => {
        if (isTesting) {
            timerRef.current = setInterval(() => {
                const currentSpeed = Math.round(scrollAcc.current * 10); // pixels/sec
                setScrolls(prev => [...prev, { val: currentSpeed }].slice(-20));
                setStats(prev => ({
                    peak: Math.max(prev.peak, currentSpeed),
                    current: currentSpeed
                }));
                scrollAcc.current = 0;
            }, 100);
        } else {
            clearInterval(timerRef.current);
        }
        return () => clearInterval(timerRef.current);
    }, [isTesting]);

    const toggleTest = () => {
        if (!isTesting) {
            setScrolls([]);
            setStats({ peak: 0, current: 0 });
            scrollAcc.current = 0;
        }
        setIsTesting(!isTesting);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Mouse className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Scroll Speed Tester</CardTitle>
                <p className="text-muted-foreground mt-2">Measure your scroll wheel velocity in pixels per second.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-10">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="p-8 rounded-[2rem] bg-primary/10 border border-primary/10 text-center">
                        <p className="text-xs font-black uppercase tracking-widest text-primary mb-1">Peak Scroll Velocity</p>
                        <p className="text-7xl font-black">{stats.peak}</p>
                        <p className="text-[10px] text-muted-foreground mt-2">Pixels per second</p>
                    </div>
                    <div className="p-8 rounded-[2rem] bg-muted/30 border border-primary/5 text-center">
                        <p className="text-xs font-black uppercase tracking-widest text-primary mb-1">Current Speed</p>
                        <p className="text-7xl font-black">{stats.current}</p>
                        <p className="text-[10px] text-muted-foreground mt-2">Real-time sampling</p>
                    </div>
                </div>

                <div
                    className={`relative w-full h-[400px] rounded-[3rem] border-4 transition-all duration-300 flex flex-col items-center justify-center cursor-ns-resize overflow-hidden ${isTesting ? 'bg-primary/5 border-primary/40' : 'bg-muted/10 border-primary/5'
                        }`}
                    onWheel={handleScroll}
                >
                    {!isTesting ? (
                        <div className="text-center z-10">
                            <Button size="lg" className="rounded-2xl h-16 px-12 text-xl font-black shadow-xl shadow-primary/20" onClick={toggleTest}>
                                Start Scroll Test
                            </Button>
                            <p className="text-muted-foreground mt-6 italic">Once started, scroll as fast as you can inside this box.</p>
                        </div>
                    ) : (
                        <div className="absolute inset-0 p-8 flex flex-col">
                            <div className="flex-1">
                                <ResponsiveContainer width="100%" height="100%">
                                    <BarChart data={scrolls}>
                                        <Bar dataKey="val">
                                            {scrolls.map((entry, index) => (
                                                <Cell key={`cell-${index}`} fill={`rgba(var(--primary-rgb), ${0.3 + (index / 20) * 0.7})`} />
                                            ))}
                                        </Bar>
                                    </BarChart>
                                </ResponsiveContainer>
                            </div>
                            <div className="mt-8 flex justify-center">
                                <Button variant="destructive" className="rounded-xl h-12 px-8 font-bold" onClick={toggleTest}>
                                    Finish & Save
                                </Button>
                            </div>
                            <div className="absolute top-8 left-1/2 -translate-x-1/2 opacity-20 transition-all">
                                <ArrowDown className="h-48 w-48 text-primary animate-bounce" />
                            </div>
                        </div>
                    )}
                </div>

                <div className="bg-muted/20 p-6 rounded-2xl border border-primary/5 space-y-3">
                    <h4 className="font-bold flex items-center gap-2 text-primary">
                        <BarChart3 className="h-4 w-4" /> Why measure scroll speed?
                    </h4>
                    <p className="text-sm text-muted-foreground leading-relaxed">
                        Competitive gamers use scroll speed for bunny-hopping (Minecraft/CS) and rapid weapon switching.
                        Digital creators and developers benefit from high scroll precision for navigating long documents and timelines.
                        A high speed with consistent reporting indicates a quality encoder in your mouse.
                    </p>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState, useEffect, useCallback, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Mouse, RotateCcw, ArrowUp, ArrowDown, Activity, Info } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

export default function ScrollTest() {
    const [scrollCount, setScrollCount] = useState<number>(0);
    const [direction, setDirection] = useState<'up' | 'down' | null>(null);
    const [history, setHistory] = useState<{ id: number; dir: 'up' | 'down'; delta: number }[]>([]);

    const timeoutRef = useRef<NodeJS.Timeout | null>(null);

    const handleScroll = useCallback((e: WheelEvent) => {
        // Prevent page scrolling during test
        e.preventDefault();

        const dir = e.deltaY < 0 ? 'up' : 'down';
        setDirection(dir);
        setScrollCount(prev => prev + 1);

        setHistory(prev => [
            { id: Date.now(), dir, delta: Math.abs(e.deltaY) },
            ...prev.slice(0, 19)
        ]);

        if (timeoutRef.current) clearTimeout(timeoutRef.current);
        timeoutRef.current = setTimeout(() => {
            setDirection(null);
        }, 150);
    }, []);

    useEffect(() => {
        const area = document.getElementById('scroll-area');
        if (area) {
            area.addEventListener('wheel', handleScroll, { passive: false });
        }
        return () => {
            if (area) area.removeEventListener('wheel', handleScroll);
        };
    }, [handleScroll]);

    const reset = () => {
        setScrollCount(0);
        setDirection(null);
        setHistory([]);
    };

    return (
        <div className="mx-auto max-w-4xl space-y-8">
            <div className="grid gap-6 md:grid-cols-3">
                <Card className="md:col-span-2 overflow-hidden border-primary/10">
                    <CardHeader className="bg-primary/5">
                        <div className="flex items-center gap-3">
                            <div className="flex h-10 w-10 items-center justify-center rounded-xl bg-primary/10">
                                <Mouse className="h-6 w-6 text-primary" />
                            </div>
                            <div>
                                <CardTitle>Scroll Area</CardTitle>
                                <CardDescription>Scroll up and down inside this box</CardDescription>
                            </div>
                        </div>
                    </CardHeader>
                    <CardContent
                        id="scroll-area"
                        className="h-80 flex flex-col items-center justify-center p-0 cursor-ns-resize overflow-hidden hover:bg-muted/50 transition-colors"
                    >
                        <div className="text-center space-y-6">
                            <AnimatePresence mode="wait">
                                {direction === 'up' ? (
                                    <motion.div
                                        key="up"
                                        initial={{ y: 20, opacity: 0 }}
                                        animate={{ y: 0, opacity: 1 }}
                                        exit={{ y: -20, opacity: 0 }}
                                        className="text-primary"
                                    >
                                        <ArrowUp className="h-20 w-20 mx-auto" strokeWidth={3} />
                                        <p className="font-black text-2xl uppercase tracking-widest mt-2">Scrolling Up</p>
                                    </motion.div>
                                ) : direction === 'down' ? (
                                    <motion.div
                                        key="down"
                                        initial={{ y: -20, opacity: 0 }}
                                        animate={{ y: 0, opacity: 1 }}
                                        exit={{ y: 20, opacity: 0 }}
                                        className="text-primary"
                                    >
                                        <ArrowDown className="h-20 w-20 mx-auto" strokeWidth={3} />
                                        <p className="font-black text-2xl uppercase tracking-widest mt-2">Scrolling Down</p>
                                    </motion.div>
                                ) : (
                                    <motion.div
                                        key="idle"
                                        initial={{ opacity: 0 }}
                                        animate={{ opacity: 0.2 }}
                                        className="text-muted-foreground"
                                    >
                                        <Mouse className="h-24 w-24 mx-auto" strokeWidth={1} />
                                        <p className="font-bold text-sm tracking-widest mt-4">READY TO SCROLL</p>
                                    </motion.div>
                                )}
                            </AnimatePresence>
                        </div>
                    </CardContent>
                </Card>

                <div className="space-y-6">
                    <Card className="border-primary/10 bg-primary/5">
                        <CardContent className="pt-6 text-center space-y-1">
                            <Activity className="h-8 w-8 text-primary mx-auto mb-2" />
                            <p className="text-xs font-bold uppercase tracking-widest text-muted-foreground">Total Ticks Detected</p>
                            <p className="text-6xl font-black text-primary tabular-nums">{scrollCount}</p>
                        </CardContent>
                    </Card>

                    <Card className="border-primary/10 h-48 overflow-hidden">
                        <CardHeader className="py-2 bg-muted/30">
                            <CardTitle className="text-[10px] font-bold uppercase tracking-widest">Scroll Log</CardTitle>
                        </CardHeader>
                        <CardContent className="p-0 h-full overflow-y-auto font-mono text-[10px]">
                            {history.map((h) => (
                                <div key={h.id} className="flex justify-between px-4 py-2 border-b">
                                    <span className={h.dir === 'up' ? 'text-blue-500 font-bold' : 'text-orange-500 font-bold'}>
                                        {h.dir.toUpperCase()}
                                    </span>
                                    <span className="text-muted-foreground">Intensity: {h.delta.toFixed(0)}</span>
                                </div>
                            ))}
                            {history.length === 0 && (
                                <div className="h-full flex items-center justify-center italic text-muted-foreground">No input detected...</div>
                            )}
                        </CardContent>
                    </Card>

                    <Button variant="outline" className="w-full rounded-xl" onClick={reset}>
                        <RotateCcw className="mr-2 h-4 w-4" />
                        Reset Count
                    </Button>
                </div>
            </div>

            <div className="rounded-2xl border bg-muted/30 p-8 space-y-4">
                <h3 className="text-xl font-bold flex items-center gap-2">
                    <Info className="h-5 w-5 text-primary" />
                    Mouse Wheel Accuracy
                </h3>
                <p className="text-sm text-muted-foreground leading-relaxed">
                    Modern gaming mice use either optical or mechanical encoders for the scroll wheel.
                    Over time, these can become dusty or worn, leading to "ghost scrolls" or missed
                    inputs. This test helps you verify if every physical click of your wheel is
                    registering a tick in the correct direction.
                </p>
                <p className="text-[10px] text-muted-foreground uppercase font-bold bg-primary/5 p-2 rounded border border-primary/10 inline-block">
                    Pro Tip: Keep the scroll wheel clean with compressed air to maintain accuracy.
                </p>
            </div>
        </div>
    );
}

