'use client';

import { useState, useEffect, useRef, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Keyboard, Timer, Trophy, RotateCcw, BarChart2 } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

export default function SpacebarCounter() {
    const [isActive, setIsActive] = useState(false);
    const [timeLeft, setTimeLeft] = useState(5);
    const [hits, setHits] = useState(0);
    const [isFinished, setIsFinished] = useState(false);
    const [bestScore, setBestScore] = useState(0);

    const timerRef = useRef<NodeJS.Timeout | null>(null);

    const startTest = useCallback(() => {
        setIsActive(true);
        setHits(0);
        setTimeLeft(5);
        setIsFinished(false);
    }, []);

    const handleKeyDown = useCallback((e: KeyboardEvent) => {
        if (e.code === 'Space') {
            e.preventDefault();
            if (!isActive && !isFinished) {
                startTest();
            }
            if (isActive) {
                setHits((prev) => prev + 1);
            }
        }
    }, [isActive, isFinished, startTest]);

    useEffect(() => {
        window.addEventListener('keydown', handleKeyDown);
        return () => window.removeEventListener('keydown', handleKeyDown);
    }, [handleKeyDown]);

    useEffect(() => {
        if (isActive && timeLeft > 0) {
            timerRef.current = setInterval(() => {
                setTimeLeft((prev) => {
                    if (prev <= 0.1) {
                        clearInterval(timerRef.current!);
                        setIsActive(false);
                        setIsFinished(true);
                        return 0;
                    }
                    return parseFloat((prev - 0.1).toFixed(1));
                });
            }, 100);
        }
        return () => {
            if (timerRef.current) clearInterval(timerRef.current);
        };
    }, [isActive, timeLeft]);

    useEffect(() => {
        if (isFinished) {
            if (hits > bestScore) setBestScore(hits);
        }
    }, [isFinished, hits, bestScore]);

    return (
        <div className="mx-auto max-w-4xl space-y-8" onKeyDown={(e) => e.code === 'Space' && e.preventDefault()}>
            <div className="grid gap-4 md:grid-cols-3">
                <Card className="bg-primary/5 border-primary/20">
                    <CardContent className="pt-6 text-center">
                        <Timer className="h-5 w-5 text-primary mx-auto mb-1" />
                        <p className="text-[10px] font-bold uppercase text-muted-foreground">Time Left</p>
                        <p className="text-3xl font-black">{timeLeft}s</p>
                    </CardContent>
                </Card>
                <Card className="bg-primary/5 border-primary/20">
                    <CardContent className="pt-6 text-center">
                        <BarChart2 className="h-5 w-5 text-primary mx-auto mb-1" />
                        <p className="text-[10px] font-bold uppercase text-muted-foreground">Hits</p>
                        <p className="text-3xl font-black">{hits}</p>
                    </CardContent>
                </Card>
                <Card className="bg-primary/5 border-primary/20">
                    <CardContent className="pt-6 text-center">
                        <Trophy className="h-5 w-5 text-yellow-500 mx-auto mb-1" />
                        <p className="text-[10px] font-bold uppercase text-muted-foreground">Record</p>
                        <p className="text-3xl font-black">{bestScore}</p>
                    </CardContent>
                </Card>
            </div>

            <Card className="border-2 border-primary/20 bg-muted/20 overflow-hidden relative">
                <div className="h-[400px] flex flex-col items-center justify-center text-center p-8">
                    <AnimatePresence mode="wait">
                        {!isActive && !isFinished && (
                            <motion.div
                                key="start"
                                initial={{ scale: 0.9, opacity: 0 }}
                                animate={{ scale: 1, opacity: 1 }}
                                className="space-y-6"
                            >
                                <div className="h-24 w-48 bg-background border-4 border-muted rounded-2xl flex items-center justify-center mx-auto shadow-inner">
                                    <span className="text-xs font-bold text-muted-foreground uppercase tracking-widest animate-pulse">Press Spacebar</span>
                                </div>
                                <div>
                                    <h3 className="text-2xl font-black tracking-tight uppercase">Spacebar Speed Test</h3>
                                    <p className="text-muted-foreground text-xs font-medium max-w-xs mx-auto">Click into the window and start hammering your spacebar to begin.</p>
                                </div>
                            </motion.div>
                        )}

                        {isActive && (
                            <motion.div
                                key="active"
                                initial={{ scale: 0.9, opacity: 0 }}
                                animate={{ scale: 1, opacity: 1 }}
                                className="space-y-4"
                            >
                                <p className="text-9xl font-black text-primary drop-shadow-2xl">{hits}</p>
                                <div className="flex gap-1 justify-center">
                                    {[...Array(5)].map((_, i) => (
                                        <div key={i} className="h-1 w-8 rounded-full bg-primary/20 overflow-hidden">
                                            <div className="h-full bg-primary animate-progress origin-left" style={{ animationDelay: `${i * 0.1}s` }} />
                                        </div>
                                    ))}
                                </div>
                            </motion.div>
                        )}

                        {isFinished && (
                            <motion.div
                                key="finished"
                                initial={{ scale: 1.1, opacity: 0 }}
                                animate={{ scale: 1, opacity: 1 }}
                                className="space-y-6"
                            >
                                <Trophy className="h-16 w-16 text-yellow-500 mx-auto" />
                                <div className="space-y-1">
                                    <h3 className="text-5xl font-black tracking-tighter">{hits} Hits</h3>
                                    <p className="text-muted-foreground text-sm">Average: {(hits / 5).toFixed(1)} Hits/sec</p>
                                </div>
                                <Button size="lg" onClick={() => setIsFinished(false)} className="rounded-full h-12 px-8 gap-2">
                                    <RotateCcw className="h-4 w-4" /> Try Again
                                </Button>
                            </motion.div>
                        )}
                    </AnimatePresence>
                </div>
            </Card>

            <div className="rounded-2xl border bg-muted/30 p-8">
                <div className="flex items-center gap-3 mb-4">
                    <div className="p-2 rounded-lg bg-primary/10">
                        <Keyboard className="h-5 w-5 text-primary" />
                    </div>
                    <h3 className="text-lg font-bold">How fast can you tap?</h3>
                </div>
                <p className="text-sm text-muted-foreground leading-relaxed">
                    The spacebar test measures how many times you can press the spacebar key on your
                    keyboard in a fixed time period. It's used to test keyboard switch responsiveness
                    and your own finger speed. Pro players often hit 10+ HPS (Hits Per Second).
                </p>
            </div>
        </div>
    );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Brain, Play, CheckCircle2, XCircle } from 'lucide-react';
import { Button } from '@/components/ui/button';

export default function SpatialMemoryBench() {
    const [state, setState] = useState<'idle' | 'showing' | 'playing' | 'result' | 'fail'>('idle');
    const [level, setLevel] = useState(3);
    const [targets, setTargets] = useState<{ x: number, y: number, id: number }[]>([]);
    const [sequence, setSequence] = useState<number[]>([]);
    const containerRef = useRef<HTMLDivElement>(null);

    const startGame = () => {
        setState('showing');
        generateTargets();
    };

    const generateTargets = () => {
        const newTargets = [];
        for (let i = 0; i < level; i++) {
            newTargets.push({
                x: Math.random() * 80 + 10,
                y: Math.random() * 80 + 10,
                id: i
            });
        }
        setTargets(newTargets);
        setSequence([]);

        setTimeout(() => {
            setState('playing');
        }, 2000); // Show for 2 seconds
    };

    const handleTargetClick = (id: number) => {
        if (state !== 'playing' || sequence.includes(id)) return;

        const nextId = sequence.length;
        if (id === nextId) {
            const nextSeq = [...sequence, id];
            setSequence(nextSeq);
            if (nextSeq.length === level) {
                setLevel(l => l + 1);
                setState('result');
            }
        } else {
            setState('fail');
        }
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Brain className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Spatial Memory Bench</CardTitle>
                <p className="text-muted-foreground mt-2">Test your ability to remember and recall target positions.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-6">
                <div className="flex justify-between items-center bg-muted/30 p-4 rounded-2xl border border-primary/5 font-bold">
                    <span>Current Level: {level - 2}</span>
                    <span>Targets to Recall: {level}</span>
                </div>

                <div
                    ref={containerRef}
                    className="relative w-full h-[500px] bg-muted/10 rounded-[2.5rem] border-2 border-primary/10 overflow-hidden"
                >
                    {state === 'idle' && (
                        <div className="absolute inset-0 flex flex-col items-center justify-center bg-background/40 backdrop-blur-sm z-10">
                            <Button size="lg" className="rounded-2xl h-14 px-10 text-lg font-bold" onClick={startGame}>
                                Start Memory Test
                            </Button>
                        </div>
                    )}

                    {(state === 'showing' || state === 'playing') && targets.map(t => (
                        <div
                            key={t.id}
                            className={`absolute w-12 h-12 rounded-2xl flex items-center justify-center cursor-pointer transition-all duration-300 font-black text-xl shadow-lg ${state === 'showing' ? 'bg-primary text-white' :
                                    sequence.includes(t.id) ? 'bg-green-500 text-white' : 'bg-muted border border-primary/10 hover:border-primary'
                                }`}
                            style={{ left: `${t.x}%`, top: `${t.y}%`, transform: 'translate(-50%, -50%)' }}
                            onClick={() => handleTargetClick(t.id)}
                        >
                            {(state === 'showing' || sequence.includes(t.id)) ? t.id + 1 : ''}
                        </div>
                    ))}

                    {state === 'result' && (
                        <div className="absolute inset-0 flex flex-col items-center justify-center bg-background/60 backdrop-blur-md z-10">
                            <CheckCircle2 className="h-16 w-16 text-green-500 mb-4" />
                            <h3 className="text-3xl font-black mb-8">Level Complete!</h3>
                            <Button size="lg" className="rounded-2xl h-14 px-10 text-lg font-bold" onClick={generateTargets}>
                                Next Level
                            </Button>
                        </div>
                    )}

                    {state === 'fail' && (
                        <div className="absolute inset-0 flex flex-col items-center justify-center bg-background/60 backdrop-blur-md z-10">
                            <XCircle className="h-16 w-16 text-red-500 mb-4" />
                            <h3 className="text-3xl font-black mb-2">Game Over</h3>
                            <p className="mb-8">Reached Level {level - 2}</p>
                            <Button size="lg" className="rounded-2xl h-14 px-10 text-lg font-bold" onClick={() => { setLevel(3); startGame(); }}>
                                Restart
                            </Button>
                        </div>
                    )}
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Move, Play, Activity, Target } from 'lucide-react';
import { Button } from '@/components/ui/button';

export default function StrafeAimingSim() {
    const [state, setState] = useState<'idle' | 'playing' | 'result'>('idle');
    const [score, setScore] = useState(0);
    const [time, setTime] = useState(30);
    const [targetPos, setTargetPos] = useState(50);
    const [playerPos, setPlayerPos] = useState(50);
    const timerRef = useRef<any>(null);
    const lastTimeRef = useRef(0);
    const targetVel = useRef(0.2);
    const playerVel = useRef(0.3);

    const startGame = () => {
        setState('playing');
        setScore(0);
        setTime(30);
        requestAnimationFrame(animate);

        timerRef.current = setInterval(() => {
            setTime(prev => {
                if (prev <= 1) {
                    endGame();
                    return 0;
                }
                return prev - 1;
            });
        }, 1000);
    };

    const endGame = () => {
        clearInterval(timerRef.current);
        setState('result');
    };

    const animate = (t: number) => {
        if (state !== 'playing') return;
        if (!lastTimeRef.current) lastTimeRef.current = t;
        const dt = t - lastTimeRef.current;
        lastTimeRef.current = t;

        setTargetPos(prev => {
            let next = prev + targetVel.current * dt * 0.1;
            if (next < 10 || next > 90) {
                targetVel.current *= -1;
                return prev;
            }
            if (Math.random() < 0.02) targetVel.current *= -1;
            return next;
        });

        setPlayerPos(prev => {
            let next = prev + playerVel.current * dt * 0.1;
            if (next < 20 || next > 80) {
                playerVel.current *= -1;
                return prev;
            }
            return next;
        });

        requestAnimationFrame(animate);
    };

    const handleShot = () => {
        if (state !== 'playing') return;
        // In this sim, "hitting" means your crosshair (relative to world) is on target
        // Since we are moving, our crosshair is effective world pos
        // For simplicity, we just check if current target and a "neutral" mouse pos match
        // But better: let user use mouse. For this MVP, we use mouse click on target.
    };

    const handleMouseClick = (e: React.MouseEvent) => {
        if (state !== 'playing') return;
        setScore(s => s + 1);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Move className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Strafe Aiming Sim</CardTitle>
                <p className="text-muted-foreground mt-2">Practice aiming while your "character" is automatically strafing.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-6">
                <div className="flex justify-between items-center bg-muted/30 p-4 rounded-2xl border border-primary/5 font-bold">
                    <span>Accuracy: {score} hits</span>
                    <span>Time: {time}s</span>
                </div>

                <div
                    className="relative w-full h-[400px] bg-muted/10 rounded-[2.5rem] border-2 border-primary/10 overflow-hidden cursor-crosshair pb-20"
                >
                    {/* Perspective lines */}
                    <div className="absolute inset-0 z-0 opacity-10 pointer-events-none">
                        {[...Array(10)].map((_, i) => (
                            <div key={i} className="absolute inset-0 border-t border-primary" style={{ top: `${i * 10}%` }} />
                        ))}
                    </div>

                    {state === 'idle' && (
                        <div className="absolute inset-0 flex flex-col items-center justify-center bg-background/40 backdrop-blur-sm z-10">
                            <Button size="lg" className="rounded-2xl" onClick={startGame}>
                                Start Strafing
                            </Button>
                        </div>
                    )}

                    {state === 'playing' && (
                        <>
                            {/* Moving Target */}
                            <div
                                className="absolute top-1/2 w-16 h-16 bg-primary rounded-full flex items-center justify-center shadow-lg transition-transform hover:scale-110"
                                style={{ left: `${targetPos}%`, transform: 'translate(-50%, -50%)' }}
                                onClick={handleMouseClick}
                            >
                                <Target className="text-white h-8 w-8" />
                            </div>

                            {/* Player Indicator (moving bottom bar) */}
                            <div
                                className="absolute bottom-10 h-4 bg-primary/20 rounded-full border border-primary/30 flex items-center justify-center"
                                style={{ left: `${playerPos}%`, width: '100px', transform: 'translateX(-50%)' }}
                            >
                                <span className="text-[10px] font-black uppercase tracking-tighter text-primary">YOUR VIEW</span>
                            </div>
                        </>
                    )}

                    {state === 'result' && (
                        <div className="absolute inset-0 flex flex-col items-center justify-center bg-background/60 backdrop-blur-md z-10">
                            <h3 className="text-6xl font-black text-primary mb-2">{score}</h3>
                            <p className="text-xl font-bold mb-8">Strafing Accuracy</p>
                            <Button size="lg" className="rounded-2xl" onClick={startGame}>
                                Try Again
                            </Button>
                        </div>
                    )}
                </div>
            </CardContent>
        </Card>
    );
}

