                )}
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { RefreshCw, Upload, Download, Image as ImageIcon } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

export default function WebPConverter() {
    const [image, setImage] = useState<string | null>(null);
    const [format, setFormat] = useState('png');
    const [isConverting, setIsConverting] = useState(false);

    const handleUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (!file) return;
        const reader = new FileReader();
        reader.onload = (event) => setImage(event.target?.result as string);
        reader.readAsDataURL(file);
    };

    const convertAction = () => {
        setIsConverting(true);
        const img = new Image();
        img.onload = () => {
            const canvas = document.createElement('canvas');
            canvas.width = img.width;
            canvas.height = img.height;
            const ctx = canvas.getContext('2d');
            ctx?.drawImage(img, 0, 0);

            const link = document.createElement('a');
            link.download = `converted-from-webp.${format}`;
            link.href = canvas.toDataURL(`image/${format === 'jpg' ? 'jpeg' : format}`);
            link.click();
            setIsConverting(false);
        };
        img.src = image!;
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm overflow-hidden text-center">
            <CardHeader className="border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <RefreshCw className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">WebP to JPG/PNG</CardTitle>
                <p className="text-muted-foreground mt-2">Make modern WebP images compatible with legacy software and older platforms.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                {!image ? (
                    <div
                        className="h-64 border-4 border-dashed border-primary/10 rounded-[3rem] flex flex-col items-center justify-center gap-4 cursor-pointer hover:bg-primary/5 transition-all"
                        onClick={() => document.getElementById('webp-upload')?.click()}
                    >
                        <Upload className="h-12 w-12 text-primary opacity-20" />
                        <p className="font-bold text-muted-foreground">Select WebP File</p>
                        <input id="webp-upload" type="file" accept="image/webp" className="hidden" onChange={handleUpload} />
                    </div>
                ) : (
                    <div className="space-y-12">
                        <div className="flex flex-col md:flex-row gap-8 items-center justify-center">
                            <img src={image} className="w-64 h-auto rounded-3xl border-4 border-primary/5 shadow-2xl" alt="Preview" />

                            <div className="space-y-6 text-left">
                                <div className="space-y-2">
                                    <label className="text-[10px] font-black uppercase text-primary tracking-widest pl-2">Output Format</label>
                                    <Select value={format} onValueChange={setFormat}>
                                        <SelectTrigger className="h-14 w-48 rounded-2xl bg-muted/20 border-primary/10 text-lg font-bold">
                                            <SelectValue />
                                        </SelectTrigger>
                                        <SelectContent>
                                            <SelectItem value="png">PNG (Lossless)</SelectItem>
                                            <SelectItem value="jpg">JPG (Compressed)</SelectItem>
                                        </SelectContent>
                                    </Select>
                                </div>
                                <Button size="lg" onClick={convertAction} disabled={isConverting} className="h-20 px-12 rounded-[2rem] font-black text-xl w-full shadow-2xl shadow-primary/20">
                                    {isConverting ? <RefreshCw className="animate-spin" /> : <Download className="mr-2" />}
                                    CONVERT
                                </Button>
                            </div>
                        </div>

                        <Button variant="ghost" onClick={() => setImage(null)} className="opacity-30">Change Image</Button>
                    </div>
                )}
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Calendar, Cake } from 'lucide-react';

export default function AgeCalculator() {
    const [birthDate, setBirthDate] = useState<string>('2000-01-01');

    const calculateAge = () => {
        const today = new Date();
        const birth = new Date(birthDate);

        let years = today.getFullYear() - birth.getFullYear();
        let months = today.getMonth() - birth.getMonth();
        let days = today.getDate() - birth.getDate();

        if (months < 0 || (months === 0 && today.getDate() < birth.getDate())) {
            years--;
            months += 12;
        }

        if (days < 0) {
            months--;
            const lastMonth = new Date(today.getFullYear(), today.getMonth(), 0);
            days += lastMonth.getDate();
        }

        return { years, months, days };
    };

    const result = calculateAge();

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Cake className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Age Calculator</CardTitle>
                <p className="text-muted-foreground mt-2">Find out exactly how old you are in years, months, and days.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-10">
                <div className="max-w-xs mx-auto space-y-4">
                    <label className="text-sm font-bold block text-center">Select Date of Birth</label>
                    <Input
                        type="date"
                        className="h-14 rounded-2xl text-lg text-center font-bold"
                        value={birthDate}
                        onChange={(e) => setBirthDate(e.target.value)}
                    />
                </div>

                <div className="grid grid-cols-3 gap-6">
                    <div className="p-8 rounded-3xl bg-muted/50 border border-primary/10 text-center space-y-2 transition-transform hover:scale-105">
                        <p className="text-5xl font-black text-primary">{result.years}</p>
                        <p className="text-sm font-bold text-muted-foreground uppercase tracking-wider">Years</p>
                    </div>
                    <div className="p-8 rounded-3xl bg-muted/50 border border-primary/10 text-center space-y-2 transition-transform hover:scale-105">
                        <p className="text-5xl font-black text-primary">{result.months}</p>
                        <p className="text-sm font-bold text-muted-foreground uppercase tracking-wider">Months</p>
                    </div>
                    <div className="p-8 rounded-3xl bg-muted/50 border border-primary/10 text-center space-y-2 transition-transform hover:scale-105">
                        <p className="text-5xl font-black text-primary">{result.days}</p>
                        <p className="text-sm font-bold text-muted-foreground uppercase tracking-wider">Days</p>
                    </div>
                </div>

                <div className="p-6 rounded-2xl bg-primary/5 border border-dashed border-primary/20 text-center">
                    <p className="text-sm text-muted-foreground">
                        Next Birthday: <span className="font-bold text-primary">Soon!</span> Stay tuned for advanced birthday countdowns.
                    </p>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Braces, Copy, RefreshCw, Trash2 } from 'lucide-react';
import { toast } from 'sonner';

export default function Base64Tool() {
    const [input, setInput] = useState('');
    const [output, setOutput] = useState('');

    const handleCopy = () => {
        navigator.clipboard.writeText(output);
        toast.success('Copied to clipboard!');
    };

    const encode = () => {
        try {
            setOutput(btoa(input));
            toast.success('Text encoded successfully!');
        } catch (e) {
            toast.error('Could not encode. Ensure text is valid UTF-8.');
        }
    };

    const decode = () => {
        try {
            setOutput(atob(input));
            toast.success('Text decoded successfully!');
        } catch (e) {
            toast.error('Invalid Base64 string.');
        }
    };

    const swap = () => {
        setInput(output);
        setOutput('');
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Braces className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Base64 Encoder / Decoder</CardTitle>
                <p className="text-muted-foreground mt-2">Encode your text to Base64 or decode it back to plain text instantly.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-8">
                <div className="space-y-4">
                    <div className="flex justify-between items-center px-1">
                        <label className="text-sm font-bold">Input Text</label>
                        <Button variant="ghost" size="sm" className="h-8 text-xs text-destructive" onClick={() => setInput('')}>
                            <Trash2 className="h-3 w-3 mr-1" /> Clear
                        </Button>
                    </div>
                    <Textarea
                        placeholder="Enter text to encode or Base64 to decode..."
                        className="min-h-[150px] text-lg p-6 rounded-2xl border-primary/20 focus:border-primary transition-all resize-none bg-background/50"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                    />
                </div>

                <div className="flex flex-col md:flex-row gap-4 justify-center">
                    <Button className="flex-1 rounded-xl h-12 font-bold shadow-lg shadow-primary/10" onClick={encode}>
                        Encode to Base64
                    </Button>
                    <Button variant="outline" className="flex-1 rounded-xl h-12 font-bold" onClick={decode}>
                        Decode to Text
                    </Button>
                    <Button variant="ghost" size="icon" className="rounded-xl h-12 w-12" onClick={swap} title="Swap Input/Output">
                        <RefreshCw className="h-5 w-5" />
                    </Button>
                </div>

                <div className="space-y-4 pt-4 border-t border-primary/5">
                    <div className="flex justify-between items-center px-1">
                        <label className="text-sm font-bold">Result</label>
                        <Button variant="ghost" size="sm" className="h-8 text-xs" onClick={handleCopy}>
                            <Copy className="h-3 w-3 mr-1" /> Copy
                        </Button>
                    </div>
                    <Textarea
                        readOnly
                        placeholder="Result will appear here..."
                        className="min-h-[150px] text-lg p-6 rounded-2xl border-primary/10 bg-muted/30 transition-all resize-none cursor-default"
                        value={output}
                    />
                </div>
            </CardContent>
        </Card>
    );
}

