                            onChange={(e) => setParagraphs(parseInt(e.target.value) || 1)}
                        />
                    </div>
                    <Button className="h-12 px-8 rounded-xl font-bold shadow-lg shadow-primary/10 mt-6" onClick={generate}>
                        <RefreshCw className="mr-2 h-5 w-5" />
