'use client';

import { useState, useEffect, useCallback } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Clock, Play, Pause, RefreshCw, Coffee, Focus } from 'lucide-react';
import { toast } from 'sonner';

export default function PomodoroTimer() {
    const [timeLeft, setTimeLeft] = useState(25 * 60);
    const [isRunning, setIsRunning] = useState(false);
    const [mode, setMode] = useState<'work' | 'break'>('work');

    const switchMode = useCallback((newMode: 'work' | 'break') => {
        setMode(newMode);
        setTimeLeft(newMode === 'work' ? 25 * 60 : 5 * 60);
        setIsRunning(false);
    }, []);

    useEffect(() => {
        let timer: NodeJS.Timeout;
        if (isRunning && timeLeft > 0) {
            timer = setInterval(() => {
                setTimeLeft((prev) => prev - 1);
            }, 1000);
        } else if (timeLeft === 0) {
            const nextMode = mode === 'work' ? 'break' : 'work';
            toast.success(mode === 'work' ? 'Time for a break!' : 'Back to work!');
            switchMode(nextMode);
        }
        return () => clearInterval(timer);
    }, [isRunning, timeLeft, mode, switchMode]);

    const formatTime = (seconds: number) => {
        const m = Math.floor(seconds / 60);
        const s = seconds % 60;
        return `${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}`;
    };

    const progress = (timeLeft / (mode === 'work' ? 25 * 60 : 5 * 60)) * 100;

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-2xl bg-card/50 backdrop-blur-sm overflow-hidden">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Clock className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Pomodoro Focus Timer</CardTitle>
                <p className="text-muted-foreground mt-2">Boost your productivity with timed focus and rest intervals.</p>
            </CardHeader>
            <CardContent className="p-12 space-y-12">
                <div className="flex justify-center gap-4">
                    <Button
                        variant={mode === 'work' ? 'default' : 'outline'}
                        className="rounded-full px-8 h-12 font-bold"
                        onClick={() => switchMode('work')}
                    >
                        <Focus className="mr-2 h-5 w-5" />
                        Focus Mode
                    </Button>
                    <Button
                        variant={mode === 'break' ? 'default' : 'outline'}
                        className="rounded-full px-8 h-12 font-bold"
                        onClick={() => switchMode('break')}
                    >
                        <Coffee className="mr-2 h-5 w-5" />
                        Break Time
                    </Button>
                </div>

                <div className="relative flex items-center justify-center">
                    <div className="w-80 h-80 rounded-full border-8 border-primary/10 flex flex-col items-center justify-center relative overflow-hidden">
                        <div
                            className="absolute bottom-0 w-full bg-primary transition-all duration-1000 ease-linear opacity-20"
                            style={{ height: `${100 - progress}%` }}
                        />
                        <h2 className="text-7xl font-black tabular-nums tracking-tighter relative z-10">
                            {formatTime(timeLeft)}
                        </h2>
                        <p className="text-sm font-bold uppercase tracking-widest text-muted-foreground mt-2 relative z-10">
                            {mode === 'work' ? 'Working' : 'Resting'}
                        </p>
                    </div>
                </div>

                <div className="flex justify-center gap-4">
                    <Button
                        size="lg"
                        className="rounded-2xl h-16 w-16 shadow-xl shadow-primary/20"
                        onClick={() => setIsRunning(!isRunning)}
                    >
                        {isRunning ? <Pause className="h-8 w-8" /> : <Play className="h-8 w-8 ml-1" />}
                    </Button>
                    <Button
                        size="lg"
                        variant="outline"
                        className="rounded-2xl h-16 w-16"
                        onClick={() => switchMode(mode)}
                    >
                        <RefreshCw className="h-6 w-6" />
                    </Button>
                </div>

                <div className="text-center">
                    <p className="text-xs text-muted-foreground font-medium uppercase tracking-[0.3em]">
                        Standard: 25m Focus / 5m Break
                    </p>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Copy, Trash2, Sparkles, RefreshCw, Hash, Type } from 'lucide-react';
import { toast } from 'sonner';
import { Slider } from '@/components/ui/slider';
import { Label } from '@/components/ui/label';
import { Checkbox } from '@/components/ui/checkbox';

export default function RandomStringGen() {
    const [result, setResult] = useState('');
    const [length, setLength] = useState([16]);
    const [options, setOptions] = useState({
        uppercase: true,
        lowercase: true,
        numbers: true,
        symbols: false
    });

    const handleCopy = () => {
        if (!result) return;
        navigator.clipboard.writeText(result);
        toast.success('Copied to clipboard!');
    };

    const generateString = () => {
        let charset = "";
        if (options.uppercase) charset += "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
        if (options.lowercase) charset += "abcdefghijklmnopqrstuvwxyz";
        if (options.numbers) charset += "0123456789";
        if (options.symbols) charset += "!@#$%^&*()_+~`|}{[]:;?><,./-=";

        if (!charset) {
            toast.error('Please select at least one character set');
            return;
        }

        let generated = "";
        for (let i = 0; i < length[0]; i++) {
            generated += charset.charAt(Math.floor(Math.random() * charset.length));
        }
        setResult(generated);
        toast.success('New string generated');
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Hash className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Random String Generator</CardTitle>
                <p className="text-muted-foreground mt-2">Create secure, random character sequences instantly.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-8">
                <div className="space-y-6 max-w-lg mx-auto">
                    <div className="space-y-4">
                        <div className="flex justify-between items-center">
                            <Label className="text-lg font-bold">String Length: {length[0]}</Label>
                        </div>
                        <Slider
                            value={length}
                            onValueChange={setLength}
                            min={4}
                            max={128}
                            step={1}
                            className="py-4"
                        />
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                        <div className="flex items-center space-x-2">
                            <Checkbox
                                id="upper"
                                checked={options.uppercase}
                                onCheckedChange={(v: boolean) => setOptions(prev => ({ ...prev, uppercase: v }))}
                            />
                            <Label htmlFor="upper" className="font-medium cursor-pointer">Uppercase (A-Z)</Label>
                        </div>
                        <div className="flex items-center space-x-2">
                            <Checkbox
                                id="lower"
                                checked={options.lowercase}
                                onCheckedChange={(v: boolean) => setOptions(prev => ({ ...prev, lowercase: v }))}
                            />
                            <Label htmlFor="lower" className="font-medium cursor-pointer">Lowercase (a-z)</Label>
                        </div>
                        <div className="flex items-center space-x-2">
                            <Checkbox
                                id="numbers"
                                checked={options.numbers}
                                onCheckedChange={(v: boolean) => setOptions(prev => ({ ...prev, numbers: v }))}
                            />
                            <Label htmlFor="numbers" className="font-medium cursor-pointer">Numbers (0-9)</Label>
                        </div>
                        <div className="flex items-center space-x-2">
                            <Checkbox
                                id="symbols"
                                checked={options.symbols}
                                onCheckedChange={(v: boolean) => setOptions(prev => ({ ...prev, symbols: v }))}
                            />
                            <Label htmlFor="symbols" className="font-medium cursor-pointer">Symbols (!@#$)</Label>
                        </div>
                    </div>

                    <Button onClick={generateString} size="lg" className="w-full rounded-2xl h-14 text-lg font-bold shadow-xl shadow-primary/20">
                        <RefreshCw className="mr-2 h-5 w-5" /> Generate Random String
                    </Button>
                </div>

                <div className="relative">
                    <Textarea
                        readOnly
                        placeholder="Generated string will appear here..."
                        className="min-h-[150px] text-2xl p-8 rounded-2xl border-primary/20 bg-muted/30 resize-none font-mono text-center tracking-wider break-all"
                        value={result}
                    />
                    {result && (
                        <div className="absolute bottom-4 right-4 flex gap-2">
                            <Button variant="outline" className="rounded-xl shadow-sm bg-background/80 backdrop-blur-sm" onClick={handleCopy}>
                                <Copy className="mr-2 h-4 w-4" /> Copy String
                            </Button>
                        </div>
                    )}
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Copy, Trash2, Activity, AlignLeft, FileText } from 'lucide-react';
import { toast } from 'sonner';

export default function SentenceCounterPro() {
    const [text, setText] = useState('');

    const stats = {
        characters: text.length,
        words: text.trim() ? text.trim().split(/\s+/).length : 0,
        sentences: text.split(/[.!?]+/).filter(Boolean).length,
        paragraphs: text.split(/\n\s*\n/).filter(Boolean).length,
        averageWordLength: text.trim() ? (text.replace(/\s/g, '').length / (text.trim().split(/\s+/).length)).toFixed(1) : 0,
        readTime: Math.ceil(text.trim().split(/\s+/).length / 200) || 0 // Average 200 words per minute
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Activity className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Sentence Counter Pro</CardTitle>
                <p className="text-muted-foreground mt-2">Deep analysis of your text structure and readability.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-8">
                <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                    <StatCard label="Sentences" value={stats.sentences} icon={<AlignLeft className="h-4 w-4" />} />
                    <StatCard label="Words" value={stats.words} icon={<FileText className="h-4 w-4" />} />
                    <StatCard label="Paragraphs" value={stats.paragraphs} icon={<Activity className="h-4 w-4" />} />
                    <StatCard label="Characters" value={stats.characters} icon={<Type className="h-4 w-4" />} />
                    <StatCard label="Avg Word Length" value={stats.averageWordLength} icon={<Activity className="h-4 w-4" />} />
                    <StatCard label="Read Time" value={`${stats.readTime} min`} icon={<Clock className="h-4 w-4" />} />
                </div>

                <Textarea
                    placeholder="Paste or type your content for analysis..."
                    className="min-h-[300px] text-lg p-6 rounded-2xl border-primary/20 focus:border-primary transition-all resize-none bg-background/50"
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                />
            </CardContent>
        </Card>
    );
}

import { Type, Clock } from 'lucide-react';

function StatCard({ label, value, icon }: { label: string, value: any, icon: any }) {
    return (
        <div className="p-6 rounded-2xl bg-muted/30 border border-primary/5 flex flex-col items-center justify-center text-center space-y-1">
            <div className="flex items-center gap-2 text-primary font-bold text-xs uppercase tracking-widest mb-1">
                {icon} {label}
            </div>
            <div className="text-2xl font-black text-foreground">{value}</div>
        </div>
    );
}

