'use client';

import { useState, useMemo } from 'react';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Copy, Trash2, Zap, Ghost, Skull } from 'lucide-react';
import { toast } from 'sonner';
import { Slider } from '@/components/ui/slider';
import { Label } from '@/components/ui/label';

const ZALGO_UP = [
    '\u030d', '\u030e', '\u0304', '\u0305', '\u033f', '\u0311', '\u0306', '\u0310',
    '\u0352', '\u033e', '\u035b', '\u0346', '\u0341', '\u034a', '\u034b', '\u034c',
    '\u034c', '\u0344', '\u030a', '\u030b', '\u030c', '\u0300', '\u0301', '\u0302',
    '\u0303', '\u0314', '\u0315', '\u0316', '\u0317'
];

const ZALGO_DOWN = [
    '\u0316', '\u0317', '\u0318', '\u0319', '\u031c', '\u031d', '\u031e', '\u031f',
    '\u0320', '\u0324', '\u0325', '\u0326', '\u0329', '\u032a', '\u032b', '\u032c',
    '\u032d', '\u032e', '\u032f', '\u0330', '\u0331', '\u0332', '\u0333', '\u0339',
    '\u033a', '\u033b', '\u0347', '\u0348', '\u0349', '\u034d', '\u034e', '\u0353',
    '\u0354', '\u0355', '\u0356', '\u0359', '\u035a', '\u0323'
];

const ZALGO_MID = [
    '\u0315', '\u031b', '\u0340', '\u0341', '\u0358', '\u0321', '\u0322', '\u0327',
    '\u0328', '\u0334', '\u0335', '\u0336', '\u0337', '\u0338', '\u035c', '\u035d',
    '\u035e', '\u035f', '\u0360', '\u0362', '\u0338', '\u0337', '\u0334', '\u0321', '\u0328', '\u034b'
];

export default function ZalgoTextGenerator() {
    const [text, setText] = useState('');
    const [intensity, setIntensity] = useState([5]);

    const zalgoText = useMemo(() => {
        if (!text) return '';
        let result = '';
        for (let i = 0; i < text.length; i++) {
            result += text[i];
            const numZalgos = Math.floor(Math.random() * intensity[0]);
            for (let j = 0; j < numZalgos; j++) {
                const choice = Math.floor(Math.random() * 3);
                if (choice === 0) result += ZALGO_UP[Math.floor(Math.random() * ZALGO_UP.length)];
                else if (choice === 1) result += ZALGO_MID[Math.floor(Math.random() * ZALGO_MID.length)];
                else result += ZALGO_DOWN[Math.floor(Math.random() * ZALGO_DOWN.length)];
            }
        }
        return result;
    }, [text, intensity]);

    const handleCopy = () => {
        navigator.clipboard.writeText(zalgoText);
        toast.success('Glitch text copied!');
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Zap className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Zalgo Text Generator</CardTitle>
                <p className="text-muted-foreground mt-2">I┬┤VE COME TO DESTROY YOUR TEXT FLAVOR.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-6">
                <div className="space-y-4">
                    <Label className="flex items-center gap-2 font-bold italic">
                        Glitch Intensity: <span className="text-primary">{intensity[0]}</span>
                    </Label>
                    <Slider
                        value={intensity}
                        onValueChange={setIntensity}
                        min={1}
                        max={15}
                        step={1}
                        className="py-4"
                    />
                </div>

                <Textarea
                    placeholder="Type your pure text here..."
                    className="min-h-[150px] text-lg p-6 rounded-2xl border-primary/20 focus:border-primary transition-all resize-none bg-background/50"
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                />

                <div className="relative min-h-[150px] p-8 rounded-2xl bg-muted/30 border border-primary/5 flex items-center justify-center text-center">
                    <div className="text-2xl font-bold overflow-hidden break-all max-w-full">
                        {text ? zalgoText : 'y├î┬Âo├î┬Âu├î┬Âr├î┬Â ├î┬Âg├î┬Âl├î┬Âi├î┬Ât├î┬Âc├î┬Âh├î┬Â ├î┬Âw├î┬Âi├î┬Âl├î┬Âl├î┬Â ├î┬Âa├î┬Âp├î┬Âp├î┬Âe├î┬Âa├î┬Âr├î┬Â ├î┬Âh├î┬Âe├î┬Âr├î┬Âe├î┬Â'}
                    </div>
                </div>

                <div className="flex gap-4">
                    <Button onClick={() => setText(text)} className="flex-1 h-12 rounded-xl font-bold">Refresh Glitch</Button>
                    <Button onClick={handleCopy} variant="outline" className="flex-1 h-12 rounded-xl font-bold">
                        <Copy className="mr-2 h-4 w-4" /> Copy Glitch
                    </Button>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Eye, AlertCircle, CheckCircle2, Info, Image as ImageIcon } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';

export default function ALTTextAccessibility() {
    const [html, setHtml] = useState('');
    const [results, setResults] = useState<any[]>([]);

    const scan = () => {
        const imgTags = html.match(/<img.*?>/gi) || [];
        const processed = imgTags.map(tag => {
            const altMatch = tag.match(/alt=["'](.*?)["']/i);
            const srcMatch = tag.match(/src=["'](.*?)["']/i);
            const alt = altMatch ? altMatch[1] : null;
            const src = srcMatch ? srcMatch[1] : 'unknown';
            return { tag, alt, src };
        });
        setResults(processed);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Eye className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">ALT Text Accessibility Scan</CardTitle>
                <p className="text-muted-foreground mt-2">Identify missing image descriptions to improve accessibility and SEO rankings.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-8">
                <div className="space-y-4">
                    <label className="text-[10px] font-black uppercase tracking-widest text-primary">Paste HTML Content</label>
                    <Textarea
                        placeholder="<img src='logo.png'> <img src='hero.jpg' alt='premium tools'>..."
                        className="h-48 rounded-3xl border-primary/10 bg-muted/20 font-mono text-xs"
                        value={html}
                        onChange={(e) => setHtml(e.target.value)}
                    />
                    <Button size="lg" className="w-full rounded-2xl h-14 font-black" onClick={scan}>Scan for Accessibility Gaps</Button>
                </div>

                {results.length > 0 && (
                    <div className="space-y-4 animate-in fade-in slide-in-from-bottom-4">
                        <div className="flex justify-between items-center px-4">
                            <h4 className="font-bold text-sm uppercase tracking-widest text-primary">Image Audit</h4>
                            <span className="text-[10px] font-black bg-destructive/10 text-destructive px-3 py-1 rounded-full uppercase tracking-tighter">
                                {results.filter(r => !r.alt).length} ISSUES FOUND
                            </span>
                        </div>

                        <div className="grid grid-cols-1 gap-3">
                            {results.map((r, i) => (
                                <div key={i} className={`p-4 rounded-2xl border flex items-center gap-4 ${r.alt ? 'bg-green-500/5 border-green-500/10' : 'bg-destructive/5 border-destructive/10'
                                    }`}>
                                    <div className="w-12 h-12 rounded-lg bg-muted flex items-center justify-center overflow-hidden shrink-0 border border-primary/5">
                                        <ImageIcon className="h-5 w-5 text-muted-foreground" />
                                    </div>
                                    <div className="flex-1 min-w-0">
                                        <p className="text-[10px] font-bold text-muted-foreground truncate">{r.src}</p>
                                        <p className={`text-sm font-bold truncate ${!r.alt ? 'text-destructive italic' : 'text-primary'}`}>
                                            {r.alt || 'MISSING ALT TEXT'}
                                        </p>
                                    </div>
                                    {r.alt ? <CheckCircle2 className="h-5 w-5 text-green-500 shrink-0" /> : <AlertCircle className="h-5 w-5 text-destructive shrink-0" />}
                                </div>
                            ))}
                        </div>
                    </div>
                )}
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Link, MousePointer2, Shuffle, AlertCircle, Copy } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { toast } from 'sonner';

export default function BacklinkAnchorGen() {
    const [brand, setBrand] = useState('Antigravity');
    const [keyword, setKeyword] = useState('Web Tools');
    const [url, setUrl] = useState('https://tools.example.com');
    const [results, setResults] = useState<any[]>([]);

    const generate = () => {
        const anchors = [
            { type: 'Branded', text: brand },
            { type: 'Keyword', text: keyword },
            { type: 'B+K', text: `${brand} ${keyword}` },
            { type: 'Generic', text: 'click here' },
            { type: 'Generic', text: 'visit website' },
            { type: 'Naked', text: url.replace('https://', '') },
        ];
        setResults(anchors);
        toast.info('Anchor Profile Generated');
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Link className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Backlink Anchor Generator</CardTitle>
                <p className="text-muted-foreground mt-2">Diversify your link building strategy with smart anchor text variations.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-10">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    <div className="space-y-2">
                        <label className="text-[10px] font-black uppercase tracking-widest text-primary font-bold">Brand Name</label>
                        <Input value={brand} onChange={(e) => setBrand(e.target.value)} className="rounded-xl border-primary/10" />
                    </div>
                    <div className="space-y-2">
                        <label className="text-[10px] font-black uppercase tracking-widest text-primary font-bold">Target Keyword</label>
                        <Input value={keyword} onChange={(e) => setKeyword(e.target.value)} className="rounded-xl border-primary/10" />
                    </div>
                    <div className="space-y-2">
                        <label className="text-[10px] font-black uppercase tracking-widest text-primary font-bold">Landing URL</label>
                        <Input value={url} onChange={(e) => setUrl(e.target.value)} className="rounded-xl border-primary/10" />
                    </div>
                </div>

                <Button size="lg" className="w-full rounded-2xl h-16 font-black gap-2 shadow-lg shadow-primary/20" onClick={generate}>
                    <Shuffle className="h-6 w-6" /> Create Diversified Profile
                </Button>

                {results.length > 0 && (
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 animate-in fade-in duration-500">
                        {results.map((r, i) => (
                            <div key={i} className="flex justify-between items-center p-6 rounded-3xl bg-muted/20 border border-primary/5 hover:border-primary/20 transition-all group">
                                <div className="space-y-1">
                                    <span className="text-[10px] font-black text-primary uppercase bg-primary/10 px-2 py-0.5 rounded leading-none">{r.type}</span>
                                    <p className="text-sm font-bold truncate max-w-[150px]">{r.text}</p>
                                </div>
                                <Button variant="ghost" className="rounded-xl h-10 w-10 p-0 hover:bg-primary/20" onClick={() => {
                                    navigator.clipboard.writeText(`<a href="${url}">${r.text}</a>`);
                                    toast.success('HTML Code Copied');
                                }}>
                                    <Copy className="h-4 w-4 text-primary" />
                                </Button>
                            </div>
                        ))}
                    </div>
                )}
            </CardContent>
        </Card>
    );
}

