'use client';

import { useState, useEffect, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Zap, Activity, Timer, ArrowRight, Gauge } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { ResponsiveContainer, AreaChart, Area, XAxis, YAxis } from 'recharts';

export default function PageLoadSimulator() {
    const [state, setState] = useState<'idle' | 'testing' | 'result'>('idle');
    const [progress, setProgress] = useState(0);
    const [metrics, setMetrics] = useState({ dns: 0, tcp: 0, dom: 0, full: 0 });

    const startTest = () => {
        setState('testing');
        setProgress(0);

        let p = 0;
        const interval = setInterval(() => {
            p += Math.random() * 5;
            if (p >= 100) {
                p = 100;
                clearInterval(interval);
                completeTest();
            }
            setProgress(Math.round(p));
        }, 100);
    };

    const completeTest = () => {
        setMetrics({
            dns: Math.round(Math.random() * 100 + 20),
            tcp: Math.round(Math.random() * 200 + 50),
            dom: Math.round(Math.random() * 800 + 400),
            full: Math.round(Math.random() * 1000 + 1500)
        });
        setState('result');
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Zap className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Page Load Time Estimator</CardTitle>
                <p className="text-muted-foreground mt-2">Estimate visual performance and bottleneck analysis for Core Web Vitals.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-10">
                {state === 'idle' && (
                    <div className="text-center py-12 space-y-6">
                        <div className="p-10 rounded-[3rem] bg-muted/10 border-4 border-dashed border-primary/10 flex flex-col items-center">
                            <Gauge className="h-16 w-16 text-primary/30 mb-4" />
                            <h3 className="text-3xl font-black mb-2 uppercase">Ready for Analysis?</h3>
                            <p className="text-muted-foreground max-w-sm mx-auto">Click start to run a simulated lighthouse scan of the current network profile.</p>
                        </div>
                        <Button size="lg" className="rounded-2xl h-16 px-12 text-xl font-bold shadow-xl shadow-primary/20" onClick={startTest}>
                            Initialize Speed Test
                        </Button>
                    </div>
                )}

                {state === 'testing' && (
                    <div className="text-center py-20 space-y-8 animate-in zoom-in-50">
                        <div className="relative w-48 h-48 mx-auto">
                            <svg className="w-full h-full transform -rotate-90">
                                <circle cx="96" cy="96" r="88" className="stroke-primary/10 fill-none" strokeWidth="12" />
                                <circle cx="96" cy="96" r="88" className="stroke-primary fill-none transition-all duration-300" strokeWidth="12" strokeDasharray={552.92} strokeDashoffset={552.92 - (552.92 * progress) / 100} />
                            </svg>
                            <div className="absolute inset-0 flex items-center justify-center">
                                <p className="text-4xl font-black text-primary">{progress}%</p>
                            </div>
                        </div>
                        <div>
                            <h3 className="text-2xl font-black uppercase tracking-tighter">Measuring Network Throughput...</h3>
                            <p className="text-muted-foreground italic">Analyzing TTFB, FCP, and DOM Interactive phases.</p>
                        </div>
                    </div>
                )}

                {state === 'result' && (
                    <div className="space-y-8 animate-in slide-in-from-bottom-6">
                        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                            <MetricBox label="DNS Lookup" value={metrics.dns} unit="ms" sub="Excellent" />
                            <MetricBox label="TCP Handshake" value={metrics.tcp} unit="ms" sub="Standard" />
                            <MetricBox label="DOM Load" value={metrics.dom} unit="ms" sub="Attention" warning={metrics.dom > 1000} />
                            <MetricBox label="Fully Loaded" value={metrics.full} unit="ms" primary />
                        </div>

                        <div className="p-8 rounded-[3rem] bg-primary/10 border border-primary/20 text-center">
                            <div className="flex items-center justify-center gap-2 mb-2">
                                <Zap className="h-5 w-5 text-primary" />
                                <span className="text-xs font-black uppercase tracking-widest text-primary">Performance Rating</span>
                            </div>
                            <h3 className="text-5xl font-black">GOOD</h3>
                            <p className="text-xs text-muted-foreground mt-2 italic px-12">Your page would likely pass Google's Core Web Vitals on standard 4G connections.</p>
                        </div>

                        <div className="flex justify-center">
                            <Button size="lg" className="rounded-2xl h-14" onClick={() => setState('idle')}>Run New Simulation</Button>
                        </div>
                    </div>
                )}
            </CardContent>
        </Card>
    );
}

function MetricBox({ label, value, unit, sub, primary, warning }: any) {
    return (
        <div className={`p-6 rounded-3xl border text-center transition-all ${primary ? 'bg-primary border-primary shadow-xl shadow-primary/30 text-white' :
                warning ? 'bg-destructive/10 border-destructive/20 text-destructive' :
                    'bg-muted/30 border-primary/5'
            }`}>
            <p className={`text-[10px] font-black uppercase tracking-widest ${primary ? 'text-white/70' : 'text-primary/60'}`}>{label}</p>
            <p className="text-2xl font-black">{value}{unit}</p>
            {!primary && <p className="text-[10px] font-bold mt-1 uppercase tracking-tighter opacity-50">{sub}</p>}
        </div>
    );
}

'use client';

import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Copy, Check, Bot, Plus, Trash2, Settings2, Globe } from 'lucide-react';
import { Badge } from '@/components/ui/badge';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Label } from '@/components/ui/label';

export default function RobotsGenerator() {
    const [sitemap, setSitemap] = useState('');
    const [userAgent, setUserAgent] = useState('*');
    const [crawlDelay, setCrawlDelay] = useState('none');
    const [disallowPaths, setDisallowPaths] = useState<string[]>(['/admin', '/private']);
    const [newPath, setNewPath] = useState('');
    const [copied, setCopied] = useState(false);

    const addPath = () => {
        if (newPath && !disallowPaths.includes(newPath)) {
            setDisallowPaths([...disallowPaths, newPath.startsWith('/') ? newPath : `/${newPath}`]);
            setNewPath('');
        }
    };

    const removePath = (index: number) => {
        setDisallowPaths(disallowPaths.filter((_, i) => i !== index));
    };

    const generateCode = () => {
        let code = `# Robots.txt generated by Assets Tools Hub\n`;
        code += `User-agent: ${userAgent}\n`;

        if (disallowPaths.length > 0) {
            disallowPaths.forEach(path => {
                code += `Disallow: ${path}\n`;
            });
        } else {
            code += `Allow: /\n`;
        }

        if (crawlDelay !== 'none') {
            code += `Crawl-delay: ${crawlDelay}\n`;
        }

        if (sitemap) {
            code += `\nSitemap: ${sitemap}`;
        }

        return code;
    };

    const copyToClipboard = () => {
        navigator.clipboard.writeText(generateCode());
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
    };

    return (
        <div className="max-w-4xl mx-auto space-y-8">
            <div className="grid gap-8 lg:grid-cols-5">
                {/* Configuration Sidebar */}
                <Card className="lg:col-span-2 border-primary/10 shadow-xl">
                    <CardHeader className="pb-4">
                        <CardTitle className="flex items-center gap-2">
                            <Settings2 className="h-5 w-5 text-primary" />
                            Configuration
                        </CardTitle>
                        <CardDescription>Customize your bot rules</CardDescription>
                    </CardHeader>
                    <CardContent className="space-y-6">
                        <div className="space-y-3">
                            <Label className="text-xs font-black uppercase tracking-widest opacity-70">User Agent</Label>
                            <Select onValueChange={setUserAgent} defaultValue="*">
                                <SelectTrigger className="h-11 rounded-xl">
                                    <SelectValue placeholder="All Bots (*)" />
                                </SelectTrigger>
                                <SelectContent>
                                    <SelectItem value="*">All Bots (*)</SelectItem>
                                    <SelectItem value="Googlebot">Googlebot</SelectItem>
                                    <SelectItem value="Bingbot">Bingbot</SelectItem>
                                    <SelectItem value="Baiduspider">Baiduspider</SelectItem>
                                    <SelectItem value="Slurp">Yahoo! Slurp</SelectItem>
                                    <SelectItem value="DuckDuckBot">DuckDuckBot</SelectItem>
                                </SelectContent>
                            </Select>
                        </div>

                        <div className="space-y-3">
                            <Label className="text-xs font-black uppercase tracking-widest opacity-70">Crawl Delay</Label>
                            <Select onValueChange={setCrawlDelay} defaultValue="none">
                                <SelectTrigger className="h-11 rounded-xl">
                                    <SelectValue placeholder="No Delay" />
                                </SelectTrigger>
                                <SelectContent>
                                    <SelectItem value="none">No Delay</SelectItem>
                                    <SelectItem value="1">1 Second</SelectItem>
                                    <SelectItem value="2">2 Seconds</SelectItem>
                                    <SelectItem value="5">5 Seconds</SelectItem>
                                    <SelectItem value="10">10 Seconds</SelectItem>
                                </SelectContent>
                            </Select>
                        </div>

                        <div className="space-y-3">
                            <Label className="text-xs font-black uppercase tracking-widest opacity-70">Sitemap URL</Label>
                            <div className="relative">
                                <Globe className="absolute left-3 top-3 h-5 w-5 text-muted-foreground" />
                                <Input
                                    placeholder="https://site.com/sitemap.xml"
                                    value={sitemap}
                                    onChange={(e) => setSitemap(e.target.value)}
                                    className="h-11 pl-10 rounded-xl"
                                />
                            </div>
                        </div>
                    </CardContent>
                </Card>

                {/* Main Content Areas */}
                <div className="lg:col-span-3 space-y-6">
                    <Card className="border-primary/10 shadow-lg">
                        <CardContent className="pt-6 space-y-4">
                            <Label className="text-xs font-black uppercase tracking-widest opacity-70">Disallowed Paths</Label>
                            <div className="flex gap-2">
                                <Input
                                    placeholder="e.g. /wp-admin"
                                    value={newPath}
                                    onChange={(e) => setNewPath(e.target.value)}
                                    onKeyDown={(e) => e.key === 'Enter' && addPath()}
                                    className="h-12 rounded-xl"
                                />
                                <Button onClick={addPath} size="icon" className="h-12 w-12 rounded-xl">
                                    <Plus className="h-5 w-5" />
                                </Button>
                            </div>

                            <div className="flex flex-wrap gap-2 pt-2">
                                {disallowPaths.map((path, i) => (
                                    <Badge key={i} variant="secondary" className="pl-3 pr-1 py-1.5 rounded-lg flex items-center gap-2 group hover:bg-destructive/10 hover:text-destructive transition-colors">
                                        <span className="font-mono">{path}</span>
                                        <button onClick={() => removePath(i)} className="p-0.5 rounded-md hover:bg-destructive/20">
                                            <Trash2 className="h-3 w-3" />
                                        </button>
                                    </Badge>
                                ))}
                                {disallowPaths.length === 0 && (
                                    <p className="text-sm text-muted-foreground italic py-2">No paths restricted. Bots will crawl everything.</p>
                                )}
                            </div>
                        </CardContent>
                    </Card>

                    <div className="relative group">
                        <div className="absolute inset-0 bg-primary/20 blur-[40px] opacity-0 group-hover:opacity-100 transition-opacity rounded-full -z-10" />
                        <Button
                            className="absolute top-4 right-4 z-20 h-10 px-4 rounded-xl shadow-lg border border-primary/20"
                            onClick={copyToClipboard}
                        >
                            {copied ? <Check className="h-4 w-4 mr-2" /> : <Copy className="h-4 w-4 mr-2" />}
                            {copied ? 'Copied!' : 'Copy Rules'}
                        </Button>
                        <div className="p-8 bg-zinc-950 text-green-400 rounded-3xl font-mono text-sm min-h-[250px] shadow-2xl relative overflow-hidden border border-white/5">
                            <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-primary to-purple-500" />
                            <pre className="whitespace-pre-wrap leading-relaxed">
                                {generateCode()}
                            </pre>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { FileText, AlertCircle, CheckCircle2, History, ShieldAlert } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';

export default function RobotsTxtValidator() {
    const [input, setInput] = useState('User-agent: *\nDisallow: /admin/\nAllow: /\n\nSitemap: https://example.com/sitemap.xml');
    const [issues, setIssues] = useState<any[]>([]);

    const validate = () => {
        const found = [];
        const lines = input.split('\n');

        let hasUserAgent = false;
        lines.forEach((line, i) => {
            if (line.toLowerCase().startsWith('user-agent:')) hasUserAgent = true;

            if (line.trim() && !line.includes(':') && !line.startsWith('#')) {
                found.push({ type: 'error', line: i + 1, msg: `Missing colon in rule: "${line}"` });
            }
        });

        if (!hasUserAgent && input.trim()) {
            found.push({ type: 'error', line: 1, msg: 'Missing User-agent directive. Every group must start with User-agent.' });
        }

        if (!input.toLowerCase().includes('sitemap:')) {
            found.push({ type: 'warn', line: lines.length, msg: 'No Sitemap reference found. This helps crawlers find your content faster.' });
        }

        setIssues(found);
    };

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <FileText className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Robots.txt Validator</CardTitle>
                <p className="text-muted-foreground mt-2">Check your crawler access rules for syntax errors and indexing conflicts.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-10">
                <div className="space-y-4">
                    <label className="text-[10px] font-black uppercase tracking-widest text-primary">Robots.txt Content</label>
                    <Textarea
                        className="h-64 rounded-[2.5rem] bg-muted/20 border-2 border-primary/5 font-mono text-xs p-8"
                        value={input}
                        onChange={(e) => { setInput(e.target.value); validate(); }}
                    />
                    <Button size="lg" className="w-full rounded-2xl h-14 font-black" onClick={validate}>Validate Directives</Button>
                </div>

                <div className="space-y-4">
                    <h4 className="font-black text-xs uppercase tracking-widest text-muted-foreground flex items-center gap-2 px-4">
                        <History className="h-4 w-4" /> Compliance Log
                    </h4>
                    <div className="space-y-2">
                        {issues.map((issue, i) => (
                            <div key={i} className={`p-4 rounded-2xl border flex items-center gap-4 animate-in slide-in-from-left-2 ${issue.type === 'error' ? 'bg-destructive/10 border-destructive/20 text-destructive' : 'bg-amber-500/10 border-amber-500/20 text-amber-500'
                                }`}>
                                <AlertCircle className="h-5 w-5 shrink-0" />
                                <div className="space-y-0.5">
                                    <p className="text-[10px] font-black uppercase opacity-70">Line {issue.line}</p>
                                    <p className="text-xs font-bold leading-tight">{issue.msg}</p>
                                </div>
                            </div>
                        ))}
                        {issues.length === 0 && input.length > 0 && (
                            <div className="p-12 rounded-[2.5rem] bg-green-500/5 border-2 border-green-500/10 text-center animate-in zoom-in-95">
                                <CheckCircle2 className="h-12 w-12 text-green-500 mx-auto mb-4" />
                                <h3 className="text-2xl font-black text-green-500 uppercase tracking-tighter">Perfectly Optimized</h3>
                                <p className="text-xs text-muted-foreground italic mt-2">Crawlers will be able to parse your rules without ambiguity.</p>
                            </div>
                        )}
                    </div>
                </div>

                <div className="p-6 rounded-2xl bg-destructive/5 border border-destructive/10 flex gap-4">
                    <ShieldAlert className="h-6 w-6 text-destructive shrink-0" />
                    <p className="text-[11px] text-muted-foreground italic leading-relaxed">
                        <b>Critical Warning:</b> Be extremely careful with `Disallow: /`. This single line will hide your ENTIRE website from search engines, causing total loss of traffic.
                    </p>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useState } from 'react';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Button } from '@/components/ui/button';
import { Copy, Code, Check, Sparkles } from 'lucide-react';

export default function SchemaMarkupMaker() {
    const [data, setData] = useState({
        name: '',
        description: '',
        url: '',
        image: '',
        author: ''
    });
    const [copied, setCopied] = useState(false);

    const generateSchema = () => {
        const schema = {
            "@context": "https://schema.org",
            "@type": "Article",
            "headline": data.name,
            "description": data.description,
            "image": data.image,
            "author": {
                "@type": "Person",
                "name": data.author
            },
            "url": data.url
        };
        return JSON.stringify(schema, null, 2);
    };

    const copyToClipboard = () => {
        navigator.clipboard.writeText(`<script type="application/ld+json">\n${generateSchema()}\n</script>`);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
    };

    return (
        <div className="grid gap-8 lg:grid-cols-2">
            <div className="space-y-6">
                <div className="p-6 rounded-2xl bg-primary/5 border border-primary/10 flex items-center gap-3">
                    <Sparkles className="h-5 w-5 text-primary" />
                    <p className="text-sm font-medium text-primary">Easily generate "Article" JSON-LD schema for SEO.</p>
                </div>

                <div className="grid gap-4">
                    <div className="space-y-2">
                        <Label>Article Headline</Label>
                        <Input
                            className="rounded-xl h-11"
                            placeholder="e.g. 10 Best Gaming Mice 2024"
                            value={data.name}
                            onChange={(e) => setData({ ...data, name: e.target.value })}
                        />
                    </div>
                    <div className="space-y-2">
                        <Label>Short Description</Label>
                        <Textarea
                            className="rounded-xl min-h-[100px]"
                            placeholder="Briefly describe the content..."
                            value={data.description}
                            onChange={(e) => setData({ ...data, description: e.target.value })}
                        />
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                        <div className="space-y-2">
                            <Label>Author Name</Label>
                            <Input
                                className="rounded-xl h-11"
                                placeholder="Writer's Name"
                                value={data.author}
                                onChange={(e) => setData({ ...data, author: e.target.value })}
                            />
                        </div>
                        <div className="space-y-2">
                            <Label>Page URL</Label>
                            <Input
                                className="rounded-xl h-11"
                                placeholder="https://site.com/page"
                                value={data.url}
                                onChange={(e) => setData({ ...data, url: e.target.value })}
                            />
                        </div>
                    </div>
                    <div className="space-y-2">
                        <Label>Feature Image URL</Label>
                        <Input
                            className="rounded-xl h-11"
                            placeholder="https://site.com/image.jpg"
                            value={data.image}
                            onChange={(e) => setData({ ...data, image: e.target.value })}
                        />
                    </div>
                </div>
            </div>

            <div className="space-y-4">
                <div className="flex items-center justify-between">
                    <Label className="text-lg">JSON-LD Script</Label>
                    <Button
                        variant="ghost"
                        size="sm"
                        className="rounded-full"
                        onClick={copyToClipboard}
                    >
                        {copied ? <Check className="h-4 w-4 text-green-500" /> : <Copy className="h-4 w-4" />}
                    </Button>
                </div>
                <div className="min-h-[450px] w-full rounded-2xl border bg-zinc-950 text-blue-400 p-6 font-mono text-xs overflow-auto">
                    <pre className="whitespace-pre-wrap">
                        {`<script type="application/ld+json">\n${generateSchema()}\n</script>`}
                    </pre>
                </div>
            </div>
        </div>
    );
}

'use client';

import { useState } from 'react';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Copy, Plus, Trash2, FileCode, Check, Globe, Settings2, Download, AlertCircle } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';

interface SitemapURL {
    url: string;
    priority: string;
    freq: string;
}

export default function SitemapGenerator() {
    const [urls, setUrls] = useState<SitemapURL[]>([
        { url: 'https://example.com/', priority: '1.0', freq: 'daily' }
    ]);
    const [batchText, setBatchText] = useState('');
    const [xmlOutput, setXmlOutput] = useState('');
    const [copied, setCopied] = useState(false);

    const addBatchUrls = () => {
        const newUrls = batchText
            .split('\n')
            .map(u => u.trim())
            .filter(u => u && u.startsWith('http'))
            .map(u => ({ url: u, priority: '0.8', freq: 'weekly' }));

        setUrls([...urls, ...newUrls]);
        setBatchText('');
    };

    const removeUrl = (index: number) => {
        setUrls(urls.filter((_, i) => i !== index));
    };

    const updateUrl = (index: number, field: keyof SitemapURL, value: string) => {
        const next = [...urls];
        next[index] = { ...next[index], [field]: value };
        setUrls(next);
    };

    const generateSitemap = () => {
        const date = new Date().toISOString().split('T')[0];
        let xml = `<?xml version="1.0" encoding="UTF-8"?>\n`;
        xml += `<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">\n`;

        urls.forEach(u => {
            xml += `  <url>\n`;
            xml += `    <loc>${u.url}</loc>\n`;
            xml += `    <lastmod>${date}</lastmod>\n`;
            xml += `    <changefreq>${u.freq}</changefreq>\n`;
            xml += `    <priority>${u.priority}</priority>\n`;
            xml += `  </url>\n`;
        });

        xml += `</urlset>`;
        setXmlOutput(xml);
    };

    const downloadSitemap = () => {
        const blob = new Blob([xmlOutput], { type: 'text/xml' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'sitemap.xml';
        a.click();
    };

    return (
        <div className="max-w-6xl mx-auto space-y-8">
            <div className="grid gap-8 lg:grid-cols-12">
                <div className="lg:col-span-12">
                    <div className="flex items-center gap-4 p-4 rounded-3xl bg-primary/5 border border-primary/10 mb-8">
                        <div className="h-12 w-12 rounded-2xl bg-primary/10 flex items-center justify-center text-primary">
                            <AlertCircle className="h-6 w-6" />
                        </div>
                        <div>
                            <h4 className="font-bold text-primary">SEO Pro Tip</h4>
                            <p className="text-sm text-muted-foreground">Internal sitemaps help Google discover your architecture faster. Keep your URL count under 50,000 per file.</p>
                        </div>
                    </div>
                </div>

                {/* Configuration Sidebar */}
                <div className="lg:col-span-5 space-y-6">
                    <Card className="border-primary/10 shadow-xl rounded-3xl overflow-hidden">
                        <CardHeader className="bg-muted/30">
                            <CardTitle className="flex items-center gap-2">
                                <Plus className="h-5 w-5 text-primary" />
                                Batch Import
                            </CardTitle>
                        </CardHeader>
                        <CardContent className="pt-6 space-y-4">
                            <Textarea
                                placeholder="Paste one URL per line..."
                                value={batchText}
                                onChange={(e) => setBatchText(e.target.value)}
                                className="min-h-[150px] rounded-2xl font-mono text-xs"
                            />
                            <Button onClick={addBatchUrls} className="w-full h-11 rounded-xl">
                                Add to List
                            </Button>
                        </CardContent>
                    </Card>

                    <div className="relative group">
                        <Button
                            className="w-full h-16 rounded-[2rem] text-lg font-black shadow-2xl shadow-primary/20 hover:scale-[1.02] transition-transform"
                            onClick={generateSitemap}
                        >
                            <FileCode className="mr-3 h-6 w-6" />
                            Build XML Sitemap
                        </Button>
                    </div>
                </div>

                {/* URL List and Output */}
                <div className="lg:col-span-7 space-y-6">
                    <div className="bg-muted/20 rounded-3xl border border-primary/5 p-6 max-h-[500px] overflow-y-auto custom-scrollbar">
                        <div className="flex items-center justify-between mb-4 border-b pb-2">
                            <Label className="text-sm font-black uppercase tracking-widest opacity-70">URL Configuration ({urls.length})</Label>
                            <button onClick={() => setUrls([])} className="text-xs text-destructive font-bold hover:underline">Clear All</button>
                        </div>
                        <div className="space-y-4">
                            <AnimatePresence>
                                {urls.map((u, i) => (
                                    <motion.div
                                        key={i}
                                        initial={{ opacity: 0, scale: 0.95 }}
                                        animate={{ opacity: 1, scale: 1 }}
                                        className="p-4 rounded-2xl bg-background border shadow-sm space-y-3"
                                    >
                                        <div className="flex items-center gap-2">
                                            <Globe className="h-4 w-4 text-primary shrink-0" />
                                            <Input
                                                value={u.url}
                                                onChange={(e) => updateUrl(i, 'url', e.target.value)}
                                                className="border-none shadow-none focus-visible:ring-0 font-medium px-0 truncate"
                                            />
                                            <Button variant="ghost" size="icon" onClick={() => removeUrl(i)} className="shrink-0 text-destructive hover:bg-destructive/10">
                                                <Trash2 className="h-4 w-4" />
                                            </Button>
                                        </div>
                                        <div className="grid grid-cols-2 gap-3 pt-2 border-t mt-2">
                                            <div className="space-y-1">
                                                <label className="text-[10px] font-black uppercase opacity-50">Priority</label>
                                                <Select value={u.priority} onValueChange={(v) => updateUrl(i, 'priority', v)}>
                                                    <SelectTrigger className="h-8 text-xs rounded-lg">
                                                        <SelectValue />
                                                    </SelectTrigger>
                                                    <SelectContent>
                                                        <SelectItem value="1.0">1.0 (Critical)</SelectItem>
                                                        <SelectItem value="0.8">0.8 (Important)</SelectItem>
                                                        <SelectItem value="0.5">0.5 (Normal)</SelectItem>
                                                        <SelectItem value="0.1">0.1 (Low)</SelectItem>
                                                    </SelectContent>
                                                </Select>
                                            </div>
                                            <div className="space-y-1">
                                                <label className="text-[10px] font-black uppercase opacity-50">Frequency</label>
                                                <Select value={u.freq} onValueChange={(v) => updateUrl(i, 'freq', v)}>
                                                    <SelectTrigger className="h-8 text-xs rounded-lg">
                                                        <SelectValue />
                                                    </SelectTrigger>
                                                    <SelectContent>
                                                        <SelectItem value="always">Always</SelectItem>
                                                        <SelectItem value="hourly">Hourly</SelectItem>
                                                        <SelectItem value="daily">Daily</SelectItem>
                                                        <SelectItem value="weekly">Weekly</SelectItem>
                                                        <SelectItem value="monthly">Monthly</SelectItem>
                                                    </SelectContent>
                                                </Select>
                                            </div>
                                        </div>
                                    </motion.div>
                                ))}
                            </AnimatePresence>
                        </div>
                    </div>

                    {xmlOutput && (
                        <motion.div initial={{ opacity: 0, y: 10 }} animate={{ opacity: 1, y: 0 }} className="space-y-4">
                            <div className="flex items-center justify-between">
                                <Label className="text-sm font-bold opacity-70 flex items-center gap-2">
                                    <FileCode className="h-4 w-4" />
                                    XML Structure
                                </Label>
                                <div className="flex gap-2">
                                    <Button variant="outline" size="sm" onClick={downloadSitemap} className="rounded-xl h-9">
                                        <Download className="h-3.5 w-3.5 mr-2" /> Download
                                    </Button>
                                    <Button size="sm" onClick={() => {
                                        navigator.clipboard.writeText(xmlOutput);
                                        setCopied(true);
                                        setTimeout(() => setCopied(false), 2000);
                                    }} className="rounded-xl h-9">
                                        {copied ? <Check className="h-3.5 w-3.5 mr-2" /> : <Copy className="h-3.5 w-3.5 mr-2" />}
                                        {copied ? 'Copied' : 'Copy'}
                                    </Button>
                                </div>
                            </div>
                            <pre className="p-6 bg-zinc-950 text-zinc-400 rounded-3xl font-mono text-xs overflow-auto max-h-[400px] border border-white/5 shadow-2xl">
                                {xmlOutput}
                            </pre>
                        </motion.div>
                    )}
                </div>
            </div>
        </div>
    );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Layout, Search, Star, MessageSquare, List, MoreVertical } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

export default function StructuredDataBench() {
    const [type, setType] = useState('review');

    return (
        <Card className="max-w-4xl mx-auto border-primary/10 shadow-xl bg-card/50 backdrop-blur-sm">
            <CardHeader className="text-center border-b border-primary/5 pb-8">
                <div className="mx-auto w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-4">
                    <Layout className="h-8 w-8 text-primary" />
                </div>
                <CardTitle className="text-3xl font-bold">Structured Data Benchmark</CardTitle>
                <p className="text-muted-foreground mt-2">Preview and optimize Rich Snippets for better search visibility.</p>
            </CardHeader>
            <CardContent className="p-8 space-y-12">
                <div className="flex justify-between items-center bg-muted/30 p-4 rounded-2xl border border-primary/5">
                    <div className="flex items-center gap-2">
                        <span className="text-[10px] font-black uppercase tracking-[0.2em] text-primary">Schema Pattern</span>
                    </div>
                    <Select value={type} onValueChange={setType}>
                        <SelectTrigger className="w-[180px] h-10 rounded-xl">
                            <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                            <SelectItem value="review">Review/Rating</SelectItem>
                            <SelectItem value="faq">FAQ Snippet</SelectItem>
                            <SelectItem value="recipe">Recipe Card</SelectItem>
                        </SelectContent>
                    </Select>
                </div>

                <div className="space-y-4">
                    <h4 className="text-[10px] font-black uppercase tracking-widest text-muted-foreground text-center">Simulated Google Result Expansion</h4>
                    <div className="p-10 rounded-[3rem] bg-white border border-slate-200 shadow-xl space-y-4 max-w-2xl mx-auto font-arial">
                        <div className="space-y-1">
                            <p className="text-[#4d5156] text-xs flex items-center gap-1">https://example.com/item <MoreVertical className="h-3 w-3" /></p>
                            <h3 className="text-[#1a0dab] text-xl hover:underline cursor-pointer">The Best Quality Product Review 2024</h3>
                            <p className="text-[#4d5156] text-sm leading-relaxed">Check out our deep dive into the latest features and pros/cons...</p>
                        </div>

                        <div className="py-2 border-t border-slate-100 flex flex-col gap-3">
                            {type === 'review' && (
                                <div className="flex items-center gap-2 text-[#4d5156] text-sm group animate-in slide-in-from-top-2">
                                    <span className="font-bold">Rating:</span>
                                    <div className="flex gap-0.5">
                                        {[...Array(5)].map((_, i) => <Star key={i} className="h-4 w-4 fill-amber-400 text-amber-400" />)}
                                    </div>
                                    <span className="text-slate-500">4.9 ┬À 1,220 reviews ┬À $45.00 ┬À In stock</span>
                                </div>
                            )}

                            {type === 'faq' && (
                                <div className="space-y-2 animate-in slide-in-from-top-2">
                                    {[1, 2].map(i => (
                                        <div key={i} className="flex justify-between items-center p-2 rounded-lg bg-slate-50 text-xs font-bold text-[#1a0dab] hover:bg-slate-100 cursor-pointer">
                                            <span>Frequently Asked Question #{i}?</span>
                                            <ChevronDown className="h-3 w-3 text-slate-400" />
                                        </div>
                                    ))}
                                </div>
                            )}

                            {type === 'recipe' && (
                                <div className="flex gap-4 items-center animate-in slide-in-from-top-2">
                                    <div className="w-16 h-16 bg-slate-200 rounded-lg overflow-hidden shrink-0">
                                        <img src="https://images.unsplash.com/photo-1546069901-ba9599a7e63c" className="w-full h-full object-cover" alt="Recipe" />
                                    </div>
                                    <div className="text-xs text-slate-500">
                                        <p className="font-bold flex items-center gap-1"><Timer className="h-3 w-3" /> 25 mins ┬À 440 cal ┬À 5 servings</p>
                                        <p>Best for quick dinners and healthy diets...</p>
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                </div>

                <div className="p-8 rounded-[2rem] bg-black/95 text-primary border-4 border-primary/10 flex flex-col items-center gap-6">
                    <p className="text-[10px] font-black uppercase tracking-[0.5em]">JSON-LD Preview</p>
                    <pre className="text-[10px] font-mono leading-relaxed opacity-70 w-full text-left overflow-x-auto whitespace-pre-wrap">
                        {`{
  "@context": "https://schema.org/",
  "@type": "Product",
  "name": "Quality Product",
  "aggregateRating": {
    "@type": "AggregateRating",
    "ratingValue": "4.9",
    "reviewCount": "1220"
  }
}`}
                    </pre>
                    <Button size="lg" className="rounded-xl w-full h-12 font-bold shadow-lg shadow-primary/20">Inject to Buffer</Button>
